const baytList: Bayt[] = [{ "id": 1, "number": 1, "text": "بَدَأْتُ بِبِسْمِ اْللهُ فيِ النَّظْمِ أوَّلاَ ... تَبَارَكَ رَحْمَاناً رَحِيماً وَمَوْئِلَا", "chapter": 1 }, { "id": 2, "number": 2, "text": "وَثَنَّيْتُ صَلَّى اللهُ رَبِّي عَلَى الِرَّضَا ... مُحَمَّدٍ الْمُهْدى إلَى النَّاسِ مُرْسَلَا", "chapter": 1 }, { "id": 3, "number": 3, "text": "وَعِتْرَتِهِ ثُمَ الصَّحَابَةِ ثُمّ مَنْ ... تَلاَهُمْ عَلَى اْلإِحْسَانِ بِالخَيْرِ وُبَّلَا", "chapter": 1 }, { "id": 4, "number": 4, "text": "وَثَلَّثْتُ أنَّ اْلَحَمْدَ لِلهِ دائِماً ... وَمَا لَيْسَ مَبْدُوءًا بِهِ أجْذَمُ الْعَلَا", "chapter": 1 }, { "id": 5, "number": 5, "text": "وَبَعْدُ فَحَبْلُ اللهِ فِينَا كِتَابُهُ ... فَجَاهِدْ بِهِ حِبْلَ الْعِدَا مُتَحَبِّلَا", "chapter": 1 }, { "id": 6, "number": 6, "text": "وَأَخْلِقْ بهِ إذْ لَيْسَ يَخْلُقُ جِدَّةً ... جَدِيداً مُوَاليهِ عَلَى الْجِدِّ مُقْبِلَا", "chapter": 1 }, { "id": 7, "number": 7, "text": "وَقَارِئُهُ الْمَرْضِيُّ قَرَّ مِثَالُهُ ... كاَلاتْرُجِّ حَالَيْهِ مُرِيحًا وَمُوكِلَا", "chapter": 1 }, { "id": 8, "number": 8, "text": "هُوَ الْمُرْتَضَى أَمًّا إِذَا كَانَ أُمَّهً ... وَيَمَّمَهُ ظِلُّ الرَّزَانَةِ قَنْقَلَا", "chapter": 1 }, { "id": 9, "number": 9, "text": "هُوَ الْحُرُّ إِنْ كانَ الْحَرِيَّ حَوَارِياً ... لَهُ بِتَحَرِّيهِ إلَى أَنْ تَنَبَّلَا", "chapter": 1 }, { "id": 10, "number": 10, "text": "وَإِنَّ كِتَابَ اللهِ أَوْثَقُ شَافِعٍ ... وَأَغْنَى غَنَاءٍ وَاهِباً مُتَفَضِّلَا", "chapter": 1 }, { "id": 11, "number": 11, "text": "وَخَيْرُ جَلِيسٍ لاَ يُمَلُّ حَدِيثُهُ ... وَتَرْدَادُهُ يَزْدَادُ فِيهِ تَجَمُّلاً", "chapter": 1 }, { "id": 12, "number": 12, "text": "وَحَيْثُ الْفَتى يَرْتَاعُ فِي ظُلُمَاتِهِ ... مِنَ اْلقَبرِ يَلْقَاهُ سَناً مُتَهَلِّلاً", "chapter": 1 }, { "id": 13, "number": 13, "text": "هُنَالِكَ يَهْنِيهِ مَقِيلاً وَرَوْضَةً ... وَمِنْ أَجْلِهِ فِي ذِرْوَةِ الْعِزِّ يُجْتُلَى", "chapter": 1 }, { "id": 14, "number": 14, "text": "يُنَاشِدُ في إرْضَائِهِ لحبِيِبِهِ ... وَأَجْدِرْ بِهِ سُؤْلاً إلَيْهِ مُوَصَّلَا", "chapter": 1 }, { "id": 15, "number": 15, "text": "فَيَا أَيُّهَا الْقَارِي بِهِ مُتَمَسِّكاً ... مُجِلاًّ لَهُ فِي كُلِّ حَالٍ مُبَجِّلا", "chapter": 1 }, { "id": 16, "number": 16, "text": "هَنِيئاً مَرِيئاً وَالِدَاكَ عَلَيْهِما ... مَلاَبِسُ أَنْوَارٍ مِنَ التَّاجِ وَالحُلاْ", "chapter": 1 }, { "id": 17, "number": 17, "text": "فَما ظَنُّكُمْ بالنَّجْلِ عِنْدَ جَزَائِهِ ... أُولَئِكَ أَهْلُ اللهِ والصَّفَوَةُ المَلَا", "chapter": 1 }, { "id": 18, "number": 18, "text": "أُولُو الْبِرِّ وَالإِحْسَانِ وَالصَّبْرِ وَالتُّقَى ... حُلاَهُمُ بِهَا جَاءَ الْقُرَانُ مُفَصَّلَا", "chapter": 1 }, { "id": 19, "number": 19, "text": "عَلَيْكَ بِهَا مَا عِشْتَ فِيهَا مُنَافِساً ... وَبِعْ نَفْسَكَ الدُّنْيَا بِأَنْفَاسِهَا الْعُلَا", "chapter": 1 }, { "id": 20, "number": 20, "text": "جَزَى اللهُ بِالْخَيْرَاتِ عَنَّا أَئِمَّةً ... لَنَا نَقَلُوا القُرَآنَ عَذْباً وَسَلْسَلَا", "chapter": 1 }, { "id": 21, "number": 21, "text": "فَمِنْهُمْ بُدُورٌ سَبْعَةٌ قَدْ تَوَسَّطَتْ ... سَمَاءَ الْعُلَى واَلْعَدْلِ زُهْراً وَكُمَّلَا", "chapter": 1 }, { "id": 22, "number": 22, "text": "لَهَا شُهُبٌ عَنْهَا اُسْتَنَارَتْ فَنَوَّرَتْ ... سَوَادَ الدُّجَى حَتَّى تَفَرَّق وَانْجَلَا", "chapter": 1 }, { "id": 23, "number": 23, "text": "وَسَوْفَ تَرَاهُمْ وَاحِداً بَعْدَ وَاحِدٍ ... مَعَ اثْنَيْنِ مِنْ أَصْحَابِهِ مُتَمَثِّلَا", "chapter": 1 }, { "id": 24, "number": 24, "text": "تَخَيَّرَهُمْ نُقَّادُهُمْ كُلَّ بَارِعٍ ... وَلَيْسَ عَلَى قُرْآنِهِ مُتَأَكِّلَا", "chapter": 1 }, { "id": 25, "number": 25, "text": "فَأَمَّا الْكَرِيمُ السِّرِّ في الطيِّبِ نَافِعٌ ... فَذَاكَ الَّذِي اخْتَارَ الْمَدينَةَ مَنْزِلَا", "chapter": 1 }, { "id": 26, "number": 26, "text": "وَقَالُونُ عِيْسَى ثُمَّ عُثْمانُ وَرْشُهُمْ ... بِصُحْبَتِهِ المَجْدَ الرَّفِيعَ تَأَثَّلَا", "chapter": 1 }, { "id": 27, "number": 27, "text": "وَمَكَّةُ عَبْدُ اللهِ فِيهَا مُقَامُهُ ... هُوَ اُبْنُ كَثِيرٍ كاثِرُ الْقَوْمِ مُعْتَلَا", "chapter": 1 }, { "id": 28, "number": 28, "text": "رَوى أَحْمَدُ الْبَزِّيْ لَهُ وَمُحَمَّدٌ ... عَلَى سَنَدٍ وَهْوَ المُلَقَّبُ قُنْبُلَا", "chapter": 1 }, { "id": 29, "number": 29, "text": "وَأَمَّا الإْمَامُ المَازِنِيُّ صَرِيحُهُمْ ... أَبُو عَمْرٍو الْبَصْرِيْ فَوَالِدُهُ الْعَلَا", "chapter": 1 }, { "id": 30, "number": 30, "text": "أَفَاضَ عَلَى يَحْيَى الْيَزِيْدِيِّ سَيْبَهُ ... فَأَصْبَحَ بِالْعَذْبِ الْفُرَاتِ مُعَلَّلَا", "chapter": 1 }, { "id": 31, "number": 31, "text": "أَبُو عُمَرَ الدُّورِي وَصَالِحُهُمْ أَبُو ... شُعَيْبٍ هُوَ السُّوسِيُّ عَنْهُ تَقَبَّلَا", "chapter": 1 }, { "id": 32, "number": 32, "text": "وَأَمَّا دِمَشْقُ الشَّامِ دَارُ ابْنِ عَامِرٍ ... فَتْلِكَ بِعَبْدِ اللهِ طَابَتْ مُحَلَّلَا", "chapter": 1 }, { "id": 33, "number": 33, "text": "هِشَامٌ وَعَبْدُ اللهِ وَهْوَ انْتِسَابُهُ ... لِذَكْوَانَ بِالإِسْنَادِ عَنْهُ تَنَقَّلَا", "chapter": 1 }, { "id": 34, "number": 34, "text": "وَبِالْكُوفَةِ الْغَرَّاءِ مِنْهُمْ ثَلاَتَةٌ ... أَذَاعُوا فَقَدْ ضَاعَتْ شَذاً وَقَرَنْفُلَا", "chapter": 1 }, { "id": 35, "number": 35, "text": "فَأَمَّا أَبُو بَكْرٍ وَعَاصِمٌ اسْمُهُ ... فَشُعْبَةُ رَاوِيهِ المُبَرِّزُ أَفْضَلَا", "chapter": 1 }, { "id": 36, "number": 36, "text": "وَذَاكَ ابْنُ عَيَّاشٍ أَبُو بَكْرٍ الرِّضَا ... وَحَفْصٌ وَبِاْلإتْقَانِ كانَ مُفضَّلَا", "chapter": 1 }, { "id": 37, "number": 37, "text": "وَحَمْزَةُ مَا أَزْكاهُ مِنْ مُتَوَرِّعٍ ... إِمَاماً صَبُوراً لِلقُرانِ مُرَتِّلَا", "chapter": 1 }, { "id": 38, "number": 38, "text": "رَوَى خَلَفٌ عَنْهُ وَخَلاَّدٌ الَّذِي ... رَوَاهُ سُلَيْمٌ مُتْقِناً وَمُحَصَّلَا", "chapter": 1 }, { "id": 39, "number": 39, "text": "وَأَمَّا عَلِيٌّ فَالْكِسَائِيُّ نَعْتُهُ ... لِمَا كانَ في الإِحْرَامِ فِيهِ تَسَرْبَلَا", "chapter": 1 }, { "id": 40, "number": 40, "text": "رَوَى لَيْثُهُمْ عَنْهُ أَبُو الْحَارِثِ الرِّضاَ ... وَحَفْصٌ هُوَ الدُّورِيْ وَفيِ الذِّكْرِ قَدْ خَلَا", "chapter": 1 }, { "id": 41, "number": 41, "text": "أَبُو عَمْرِهِمْ والْيحْصَبِيُّ ابْنُ عَامِرٍ ... صَرِيحٌ وَبَاقِيهِمْ أَحَاطَ بِهِ الْوَلَا", "chapter": 1 }, { "id": 42, "number": 42, "text": "لَهُمْ طُرُقٌ يُهْدَى بِهَا كُلُّ طَارِقٍ ... وَلاَ طَارِقٌ يُخْشَى بِهاَ مُتَمَحِّلاً", "chapter": 1 }, { "id": 43, "number": 43, "text": "وَهُنَّ الَّلوَاتِي لِلْمُوَاتِي نَصَبْتُهاَ ... مَنَاصِبَ فَانْصَبْ فِي نِصَابِكَ مُفْضِلَا", "chapter": 1 }, { "id": 44, "number": 44, "text": "وَهَا أَنَا ذَا أَسْعَى لَعَلَّ حُرُوفَهُمْ ... يَطُوعُ بِهَا نَظْمُ الْقَوَافِيْ مُسَهِّلَا", "chapter": 1 }, { "id": 45, "number": 45, "text": "جَعَلْتُ أَبَا جَادٍ عَلَى كُلِّ قَارِئٍ ... دَلِيلاً عَلَى المَنْظُومِ أَوَّلَ أَوَّلَا", "chapter": 1 }, { "id": 46, "number": 46, "text": "وَمِنْ بَعْدِ ذِكْرِي الْحَرْفَ أُسْمِي رِجَالَهُ ... مَتَى تَنْقَضِي آتِيكَ بِالْوَاوِ فَيْصَلَا", "chapter": 1 }, { "id": 47, "number": 47, "text": "سِوَى أَحْرُفٍ لاَ رِيبَةٌ فِي اتِّصَالِهَا ... وَبالَّلفْظِ أَسْتَغْنِي عَنِ الْقَيْدِ إِنْ جَلَا", "chapter": 1 }, { "id": 48, "number": 48, "text": "وَرُبَّ مَكاَنٍ كَرَّرَ الْحَرْفَ قَبْلَهَا ... لِمَا عَارِضٍ وَالأَمْرُ لَيْسَ مُهَوِّلاَ", "chapter": 1 }, { "id": 49, "number": 49, "text": "وَمِنْهُنَّ لِلْكُوفِيِّ ثَاءٌ مُثَلَّتٌ ... وَسِتَّتُهُمْ بِالْخَاءِ لَيْسَ بِأَغْفَلَا", "chapter": 1 }, { "id": 50, "number": 50, "text": "عَنَيْتُ الأُلَى أَثْبَتُّهُمْ بَعْدَ نَافِعٍ ... وَكُوفٍ وَشَامٍ ذَالُهُمْ لَيْسَ مُغْفَلَا", "chapter": 1 }, { "id": 51, "number": 51, "text": "وَكُوفٍ مَعَ المَكِّيِّ بِالظَّاءِ مُعْجَماً ... وَكُوفٍ وَبَصْرٍ غَيْنُهُمْ لَيْسَ مُهْمَلَا", "chapter": 1 }, { "id": 52, "number": 52, "text": "وَذُو النَّقْطِ شِينٌ لِلْكِسَائِي وَحَمْزَةٍ ... وَقُلْ فِيهِمَا مَعْ شُعْبَةٍ صُحْبَةٌ تَلَا", "chapter": 1 }, { "id": 53, "number": 53, "text": "صِحَابٌ هَمَا مَعْ حَفْصِهِمْ عَمَّ نَافِعٌ ... وَشَامٍ سَمَا فِي نَافِعٍ وَفَتَى الْعَلَا", "chapter": 1 }, { "id": 54, "number": 54, "text": "وَمَكٍّ وَحَقٌّ فِيهِ وَابْنِ الْعَلاَءِ قُلْ ... وَقُلْ فِيهِمَا وَالْيَحْصُبِي نَفَرٌ حَلَا", "chapter": 1 }, { "id": 55, "number": 55, "text": "وَحِرْمِيٌّ الْمَكِّيُّ فِيهِ وَنَافِعٍ ... وَحِصْنٌ عَنِ الْكُوفِي وَنَافِعِهِمْ عَلَا", "chapter": 1 }, { "id": 56, "number": 56, "text": "وَمَهْماَ أَتَتْ مِنْ قَبْلُ أَوْ بَعْدُ كِلْمَةٌ ... فَكُنْ عِنْدَ شَرْطِي وَاقْضِ بِالْوَاوِ فَيْصَلَا", "chapter": 1 }, { "id": 57, "number": 57, "text": "وَمَا كانَ ذَا ضِدٍّ فَإِنِّي بَضِدِّهِ ... غَنّيٌّ فَزَاحِمْ بِالذَّكاءِ لِتَفْضُلَا", "chapter": 1 }, { "id": 58, "number": 58, "text": "كَمَدٍّ وَإِثْبَاتٍ وَفَتْحٍ وَمُدْغَمٍ ... وَهَمْزٍ وَنَقْلٍ وَاخْتِلاَسٍ تَحَصَّلَا", "chapter": 1 }, { "id": 59, "number": 59, "text": "وَجَزْمٍ وَتَذْكِيرٍ وَغَيْبٍ وَخِفَّةٍ ... وَجَمْعٍ وَتَنْوِينٍ وَتَحْرِيكٍ اْعَمِلَا", "chapter": 1 }, { "id": 60, "number": 60, "text": "وَحَيْثُ جَرَى التَّحْرِيكُ غَيْرَ مُقَيَّدٍ ... هُوَ الْفَتْحُ وَالإِسْكانُ آخَاهُ مَنْزِلَا", "chapter": 1 }, { "id": 61, "number": 61, "text": "وَآخَيْتُ بَيْنَ النُّونِ وَالْيَا وَفَتْحِهِمْ ... وَكَسْرٍ وَبَيْنَ النَّصْبِ وَالخَفْضِ مُنْزِلَا", "chapter": 1 }, { "id": 62, "number": 62, "text": "وَحَيْثُ أَقُولُ الضَّمُّ وَالرَّفْعُ سَاكِتا ... فَغَيْرُهُمُ بِالْفَتْحِ وَالنَّصْبِ أَقْبَلَا", "chapter": 1 }, { "id": 63, "number": 63, "text": "وَفي الرَّفْعِ وَالتَّذْكِيرِ وَالْغَيْبِ جُمْلَةٌ ... عَلَى لَفْظِهَا أَطْلَقْتُ مَنْ قَيَّدَ الْعُلَا", "chapter": 1 }, { "id": 64, "number": 64, "text": "وَقبْلَ وبَعْدَ الْحَرْفِ آتِي بِكُلِّ مَا ... رَمَزْتُ بِهِ فِي الْجَمْعِ إِذْ لَيْسَ مُشْكِلاَ", "chapter": 1 }, { "id": 65, "number": 65, "text": "وَسَوْفَ أُسَمِّي حَيْثُ يَسْمَحُ نَظْمُهُ ... بِهِ مُوضِحاً جِيْداً مُعَمًّا وَمُخْوَلَا", "chapter": 1 }, { "id": 66, "number": 66, "text": "وَمَنْ كانَ ذَا بَابٍ لَهُ فِيهِ مَذْهَبٌ ... فَلاَ بُدَّ أَنْ يُسْمَى فَيُدْرَى وَيُعْقَلَا", "chapter": 1 }, { "id": 67, "number": 67, "text": "أَهَلَّتْ فَلَبَّتْهَا المَعَانِي لُبَابُهاَ ... وَصُغْتُ بِهَا مَا سَاغَ عَذْباً مُسَلْسَلَا", "chapter": 1 }, { "id": 68, "number": 68, "text": "وَفي يُسْرِهَا التَّيْسِيرُ رُمْتُ اخْتَصَارَهُ ... فَأَجْنَتْ بِعَوْنِ اللهِ مِنْهُ مُؤَمَّلَا", "chapter": 1 }, { "id": 69, "number": 69, "text": "وَأَلْفَافُهَا زَادَتْ بِنَشْرِ فَوَائِدٍ ... فَلَفَّتْ حَيَاءً وَجْهَهَا أَنْ تُفَضَّلَا", "chapter": 1 }, { "id": 70, "number": 70, "text": "وَسَمَّيْتُهاَ \"حِرْزَ الأَمَانِيْ\" تَيَمُّنا ... وَوَجْهَ التَّهانِي فَاهْنِهِ مُتَقبِّلَا", "chapter": 1 }, { "id": 71, "number": 71, "text": "وَنَادَيْتُ أللَّهُمَّ يَا خَيْرَ سَامِعٍ ... أَعِذْنِي مِنَ التَّسْمِيعِ قَوْلاً وَمِفْعَلَا", "chapter": 1 }, { "id": 72, "number": 72, "text": "إِلَيكَ يَدِي مِنْكَ الأَيَادِي تَمُدُّهَا ... أَجِرْنِي فَلاَ أَجْرِي بِجَوْرٍ قَأَخْطَلَا", "chapter": 1 }, { "id": 73, "number": 73, "text": "أَمِينَ وَأَمْناً لِلأَمِينِ بِسِرِّهَا ... وَإنْ عَثَرَتْ فَهْوَ الأَمُونُ تَحَمُّلَا", "chapter": 1 }, { "id": 74, "number": 74, "text": "أَقُولُ لِحُرٍ وَالْمُرُوءةُ مَرْؤُهَا ... لإخْوَتِهِ الْمِرْآةُ ذُو النُّورِ مِكْحَلَا", "chapter": 1 }, { "id": 75, "number": 75, "text": "أَخي أَيُّهَا الْمُجْتَازُ نَظْمِي بِبَابِهِ ... يُنَادَى عَلَيْهِ كَاسِدَ السُّوْقِ أَجْمِلَا", "chapter": 1 }, { "id": 76, "number": 76, "text": "وَظُنَّ بِهِ خَيْراً وَسَامِحْ نَسِيجَهُ ... بِالاِغْضاَءِ وَالْحُسْنَى وَإِنْ كانَ هَلْهَلَا", "chapter": 1 }, { "id": 77, "number": 77, "text": "وَسَلِّمْ لإِحْدَى الْحُسْنَيَيْنِ إِصَابَةٌ ... وَالاُخْرَى اجْتِهادٌ رَامَ صَوْباً فَأَمْحَلَا", "chapter": 1 }, { "id": 78, "number": 78, "text": "وَإِنْ كانَ خَرْقُ فَادَّرِكْهُ بِفَضْلَةٍ ... مِنَ الْحِلْمِ ولْيُصْلِحْهُ مَنْ جَادَ مِقْوَلَا", "chapter": 1 }, { "id": 79, "number": 79, "text": "وَقُلْ صَادِقًا لَوْلاَ الْوِئَامُ وَرُوحُهُ ... لَطاَحَ الأَنَامُ الْكُلُّ فِي الْخُلْفِ وَالْقِلَا", "chapter": 1 }, { "id": 80, "number": 80, "text": "وَعِشْ سَالِماً صَدْراً وَعَنْ غِيبَةٍ فَغِبْ ... تُحَضَّرْ حِظَارَ الْقُدْسِ أَنْقَى مُغَسَّلاَ", "chapter": 1 }, { "id": 81, "number": 81, "text": "وَهذَا زَمَانُ الصَّبْرِ مَنْ لَكَ بِالَّتِي ... كَقَبْضٍ عَلَى جَمْرٍ فَتَنْجُو مِنَ الْبلَا", "chapter": 1 }, { "id": 82, "number": 82, "text": "وَلَوْ أَنَّ عَيْناً سَاعَدتْ لتَوَكَّفَتْ ... سَحَائِبُهَا بِالدَّمْعِ دِيْماً وَهُطَّلَا", "chapter": 1 }, { "id": 83, "number": 83, "text": "وَلكِنَّها عَنْ قَسْوَةِ الْقَلْبِ قَحْطُهاَ ... فَيَا ضَيْعَةَ الأَعْمَارِ تَمْشِى سَبَهْلَلَا", "chapter": 1 }, { "id": 84, "number": 84, "text": "بِنَفسِي مَنِ اسْتَهْدَىَ إلَى اللهِ وَحْدَهُ ... وَكانَ لَهُ الْقُرْآنُ شِرْباً وَمَغْسَلَا", "chapter": 1 }, { "id": 85, "number": 85, "text": "وَطَابَتْ عَلَيْهِ أَرْضُهُ فَتفَتَّقَتْ ... بِكُلِّ عَبِيرٍ حِينَ أَصْبَحَ مُخْضَلَا", "chapter": 1 }, { "id": 86, "number": 86, "text": "فَطُوْبَى لَهُ وَالشَّوْقُ يَبْعَثُ هَمُّهُ ... وَزَنْدُ الأَسَى يَهْتَاجُ فِي الْقَلْبِ مُشْعِلَا", "chapter": 1 }, { "id": 87, "number": 87, "text": "هُوَ المُجْتَبَى يَغْدُو عَلَى النَّاسِ كُلِّهِمْ ... قَرِيباً غَرِيباً مُسْتَمَالاً مُؤَمَّلَا", "chapter": 1 }, { "id": 88, "number": 88, "text": "يَعُدُّ جَمِيعَ النَّاسِ مَوْلىً لأَنَّهُمْ ... عَلَى مَا قَضَاهُ اللهُ يُجْرُونَ أَفْعُلَا", "chapter": 1 }, { "id": 89, "number": 89, "text": "يَرَى نَفْسَهُ بِالذَّمِّ أَوْلَى لأَنَّهَا ... عَلَى المَجْدِ لَمْ تَلْعقْ مِنَ الصَّبْرِ وَالْأَلَا", "chapter": 1 }, { "id": 90, "number": 90, "text": "وَقَدْ قِيلَ كُنْ كَالْكَلْبِ يُقْصِيهِ ... أَهْلُهُ وَمَا يَأْتَلِي فِي نُصْحِهِمْ مُتَبَذِّلَا", "chapter": 1 }, { "id": 91, "number": 91, "text": "لَعَلَّ إِلهَ الْعَرْشِ يَا إِخْوَتِي يَقِي ... جَمَاعَتَنَا كُلَّ المَكاَرِهِ هُوَّلَا", "chapter": 1 }, { "id": 92, "number": 92, "text": "وَيَجْعَلُنَا مِمَّنْ يَكُونُ كِتاَبُهُ ... شَفِيعاً لَهُمْ إِذْ مَا نَسُوْهُ فَيمْحَلَا", "chapter": 1 }, { "id": 93, "number": 93, "text": "وَبِاللهِ حَوْلِى وَاعْتِصَامِي وَقُوَّتِى ... وَمَالِيَ إِلاَّ سِتْرُهُ مُتَجَلِّلَا", "chapter": 1 }, { "id": 94, "number": 94, "text": "فَيَا رَبِّ أَنْتَ اللهُ حَسْبِي وَعُدَّتِي ... عَلَيْكَ اعْتِمَادِي ضَارِعاً مُتَوَكِّلَا", "chapter": 1 }, { "id": 95, "number": 95, "text": "إِذَا مَا أَرَدْتَ الدَّهْرَ تَقْرَأُ فَاسْتَعِذْ ... جِهَاراً مِنَ الشَّيْطَانِ بِاللهِ مُسْجَلاَ", "chapter": 1 }, { "id": 96, "number": 96, "text": "عَلَى مَا أَتَى فِي النَّحْلِ يُسْراً ... وَإِنْ تَزِدْ لِرَبِّكَ تَنْزِيهاً فَلَسْتَ مُجَهَّلَا", "chapter": 1 }, { "id": 97, "number": 97, "text": "وَقَدْ ذَكَرُوا لَفْظَ الرَّسُولِ فَلَمْ يَزِدْ ... وَلَوْ صَحَّ هذَا النَّقْلُ لَمْ يُبْقِ مُجْمَلَا", "chapter": 1 }, { "id": 98, "number": 98, "text": "وَفِيهِ مَقَالٌ فِي الأُصُولِ فُرُوعُهُ ... فَلاَ تَعْدُ مِنْهَا بَاسِقاً وَمُظَلِّلَا", "chapter": 1 }, { "id": 99, "number": 99, "text": "وَإِخْفَاؤُهُ فَصْلٌ أَبَاهُ وَعُاَتُنَا ... وَكَمْ مِنْ فَتىً كالْمَهْدَوِي فِيهِ أَعْمَلَا", "chapter": 1 }, { "id": 100, "number": 100, "text": "وَبَسْمَلَ بَيْنَ السُّورَتَيْنِ بِسُنَّةٍ ... رِجَالٌ نَمَوْهاَ دِرْيَةً وَتَحَمُّلَا", "chapter": 1 }, { "id": 101, "number": 101, "text": "وَوَصْلُكَ بَيْنَ السُّورَتَيْنِ فَصَاحَةٌ ... وَصِلْ وَاسْكُتَنْ كُلٌّ جَلاَيَاهُ حَصَّلَا", "chapter": 1 }, { "id": 102, "number": 102, "text": "وَلاَ نَصَّ كَلاَّ حُبَّ وجْهٌ ذَكَرْتُهُ ... وَفِيهاَ خِلاَفٌ جِيدُهُ وَاضِحُ الطُّلَا", "chapter": 1 }, { "id": 103, "number": 103, "text": "وسَكْتُهُمُ الْمُخْتَارُ دُونَ تَنَفُّسٍ ... وَبَعْضُهُمُ فِي الأَرْبِعِ الزُّهْرِ بَسْمَلَا", "chapter": 1 }, { "id": 104, "number": 104, "text": "لَهُمْ دُونَ نَصٍّ وَهْوَ فِيهِنَّ سَاكِتٌ ... لِحَمْزَةَ فَافْهَمْهُ وَلَيْسَ مُخَذَّلَا", "chapter": 1 }, { "id": 105, "number": 105, "text": "وَمَهْمَا تَصِلْهَا أَوْ بَدَأْتَ بَرَاءَةً ... لِتَنْزِيْلِهاَ بالسَّيْفِ لَسْتَ مُبَسْمِلَا", "chapter": 1 }, { "id": 106, "number": 106, "text": "وَلاَ بُدَّ مِنْهاَ فِي ابْتِدَائِكَ سُورَةً ... سِوَاهاَ وَفي الأَجْزَاءِ خَيَّرَ مَنْ تَلَا", "chapter": 1 }, { "id": 107, "number": 107, "text": "وَمَهْمَا تَصِلْهَا مَعْ أَوَاخِرِ سُورَةٍ فَلاَ تَقِفَنَّ الدَّهْرَ فِيهاَ فَتَثْقُلَا", "chapter": 1 }, { "id": 108, "number": 108, "text": "وَمَالِكِ يَوْمِ الدِّينِ رَاوِيهِ نَاَصِرٌ ... وَعَنْدَ سِرَاطٍ وَالسِّرَاطَ لِ قُنْبُلَا", "chapter": 1 }, { "id": 109, "number": 109, "text": "بِحَيْثُ أَتَى وَالصَّادُ زَاياً اشِمَّهَا ... لَدَى خَلَفٍ وَاشْمِمْ لِخَلاَّدِ الاَوَّلَا", "chapter": 1 }, { "id": 110, "number": 110, "text": "عَلَيْهِمْ إِلَيْهِمْ حَمْزَةٌ وَلَدَيْهِموُ ... جَمِيعاً بِضَمِّ الْهاءِ وَقْفاً وَمَوْصِلاَ", "chapter": 1 }, { "id": 111, "number": 111, "text": "وَصِلْ ضَمَّ مِيمِ الْجَمْعِ قَبْلَ مُحَرَّكٍ ... دِرَاكاً وَقاَلُونٌ بِتَخْيِيرِهِ جَلَا", "chapter": 1 }, { "id": 112, "number": 112, "text": "وَمِنْ قَبْلِ هَمْزِ الْقَطْعِ صِلْهَا لِوَرْشِهِمْ ... وَأَسْكَنَهاَ الْبَاقُونَ بَعْدُ لِتَكْمُلَا", "chapter": 1 }, { "id": 113, "number": 113, "text": "وَمِنْ دُونِ وَصْلٍ ضَمُّهَا قَبْلَ سَاكِنٍ ... لِكُلٍ وَبَعْدَ الْهَاءِ كَسْرُ فَتَى الْعَلَا", "chapter": 1 }, { "id": 114, "number": 114, "text": "مَعَ الْكَسْرِ قَبْلَ الْهَا أَوِ الْيَاءِ سَاكِناً ... وَفي الْوَصْلِ كَسْرُ الْهَاءِ بالضَّمِّ شَمْلَلَا", "chapter": 1 }, { "id": 115, "number": 115, "text": "كَمَا بِهِمُ الأَسْبَابُ ثُمَّ عَلَيْهِمُ الْـ ... قِتَالُ وَقِفْ لِلْكُلِّ بِالْكَسْرِ مُكْمِلَا", "chapter": 1 }, { "id": 116, "number": 116, "text": "وَدُونَكَ الاُِدْغَامَ الْكَبِيرَ وَقُطْبُهُ ... أَبُو عَمْرٍو الْبَصْرِيُّ فِيهِ تَحَفَّلَا", "chapter": 1 }, { "id": 117, "number": 117, "text": "فَفِي كِلْمَةٍ عَنْهُ مَنَاسِككُّمْ وَمَا ... سَلَككُّمْ وَبَاقِي الْبَابِ لَيْسَ مُعَوَّلَا", "chapter": 1 }, { "id": 118, "number": 118, "text": "وَمَا كَانَ مِنْ مِثْلَيْنِ فِي كِلْمَتَيْهِمَا ... فَلاَ بُدَّ مِنْ إدْغَامِ مَا كانَ أَوَّلَا", "chapter": 1 }, { "id": 119, "number": 119, "text": "كَيَعْلَمُ مَا فِيهِ هُدًى وَطُبِعْ عَلَى ... قُلُوبِهِمُ وَالْعَفْوَ وَأْمُرْ تَمَثَّلَا", "chapter": 1 }, { "id": 120, "number": 120, "text": "إِذَا لَمْ يَكُنْ تَا مُخْبِرٍ أَوْ مُخَاطَبٍ ... أوِ الْمُكْتَسِي تنْوِينَهُ أَوْ مُثَقَّلَا", "chapter": 1 }, { "id": 121, "number": 121, "text": "كَكُنْتُ تُرَاباً أَنْتَ تُكْرِهُ وَاسِعٌ ... عَلِيمٌ وَأَيْضاً تَمَّ مِيقاَتُ مُثِّلَا", "chapter": 1 }, { "id": 122, "number": 122, "text": "وَقَدْ أَظْهَرُوا فِي الْكَافِ يَحْزُنْكَ كُفْرُهُ ... إِذِ النُّونُ تُخْفَى قَبْلَهَا لِتُجَمَّلَا", "chapter": 1 }, { "id": 123, "number": 123, "text": "وَعِنْدَهُمُ الْوَجْهَانِ في كُلِّ مَوْضِعٍ ... تَسَمَّى لِأَجْلِ الْحَذْفِ فِيهِ مُعَلَّلَا", "chapter": 1 }, { "id": 124, "number": 124, "text": "كَيَبْتَغِ مَجْزُوماً وَإِنْ يَكُ كاذِبا ... وَيَخْلُ لَكُمْ عَنْ عَالِمٍ طَيِّبِ الْخَلَا", "chapter": 1 }, { "id": 125, "number": 125, "text": "وَيَا قَوْمِ مَالِي ثُمَّ يَا قَوْمِ مَنْ بِلاَ ... خِلاَفٍ عَلَى الْإِدْغَامِ لاَ شَكَّ أُرْسِلَا", "chapter": 1 }, { "id": 126, "number": 126, "text": "وَإِظْهَارُ قَوْمٍ آلَ لُوطٍ لِكَوْنِهِ ... قَلِيلَ حُرُوفٍ رَدَّه مَنُْ تَنَبَّلَا", "chapter": 1 }, { "id": 127, "number": 127, "text": "بِإِدْغاَمِ لَكَ كَيْدًا وَلَوْ حَجَّ مُظْهِرٌ ... بِإِعْلاَلِ ثَانِيهِ إِذَا صَحَّ لاَعْتَلَا", "chapter": 1 }, { "id": 128, "number": 128, "text": "فَإِبْدَالُهُ مِنْ هَمْزَةٍ هَاءٌ اَصْلُهَا ... وَقَدْ قَالَ بَعْضُ النَّاسِ مِنْ وَاوٍ ابْدِلَا", "chapter": 1 }, { "id": 129, "number": 129, "text": "وَوَاوَ هُوَ الْمَضْمومُ هَاءً َكَهُو وَمَنْ ... فَأَدْغِمْ وَمَنْ يُظْهِرْ فَبِالْمَدِّ عَلَّلَا", "chapter": 1 }, { "id": 130, "number": 130, "text": "وَيَأْتِيَ يَوْمٌ أَدْغَمُوهُ وَنَحْوَهُ ... وَلاَ فَرْقَ يُنْجِي مَنْ عَلَى الْمَدِّ عَوَّلَا", "chapter": 1 }, { "id": 131, "number": 131, "text": "وَقَبْلَ يَئِسْنَ الْيَاءُ في الَّلاءِ عَارِضٌ ... سُكُونًا أَوَ اصْلاً فَهُوَ يُظْهِرُ مُسْهِلَا", "chapter": 1 }, { "id": 132, "number": 132, "text": "وَإِنْ كِلْمَةٌ حَرْفَانِ فِيهَا تَقَارَبَا ... فإِدْغَامُهُ لِلْقَافِ في الْكافِ مُجْتَلَا", "chapter": 1 }, { "id": 133, "number": 133, "text": "وَهذَا إِذَا مَا قَبْلَهُ مُتَحَرِّكٌ ... مُبِينٌ وَبَعْدَ الْكافِ مِيمٌ تَخَلَّلَا", "chapter": 1 }, { "id": 134, "number": 134, "text": "كَيَرْزُقُكُمْ وَاثقَكُّمُوا وَخَلَقكُّمُو ... وَمِيثَاقَكُمْ أَظْهِرْ وَنَرْزُقُكَ انْجَلَا", "chapter": 1 }, { "id": 135, "number": 135, "text": "وَاِدْغَامُ ذِي التَّحْرِيمِ طَلَّقَكُنَّ قُلْ ... أَحَقُّ وَبِالتَّأْنِيثِ وَالْجَمْعِ أُثْقِلَا", "chapter": 1 }, { "id": 136, "number": 136, "text": "وَمَهْماَ يَكُونَا كِلْمَتَيْنِ فَمُدْغِمٌ ... أَوَائِلَ كِلْمِ الْبَيْتِ بَعْدُ عَلَى الْوِلَا", "chapter": 1 }, { "id": 137, "number": 137, "text": "شِفَا لَمْ تَضِقْ نَفْسًا بِهَا رُمْ دَوَا ضنٍ ... ثَوَى كانَ ذَا حُسْنٍ سَأَى مِنْهُ قَدْ جَلَا", "chapter": 1 }, { "id": 138, "number": 138, "text": "إِذَا لَمْ يُنَوَّنْ أَوْ يَكُنْ تَا مُخَاطَبٍ ... وَمَا لَيْسَ مَجْزُومًا وَلاَ مُتَثَقِّلَا", "chapter": 1 }, { "id": 139, "number": 139, "text": "فَزُحْزِحْ عَنِ النَّارِ الَّذِي حَاهُ مُدْغَمٌ ... وَفي الْكاَفِ قَافٌ وَهْوَ في الْقَافِ أُدْخِلَا", "chapter": 1 }, { "id": 140, "number": 140, "text": "خَلَقْ كُلَّ شَيْءٍ لَكْ قُصُوراً وأُظْهِرَا ... إِذَا سَكَنَ الْحَرْفُ الَّذِي قَبْلُ أُقْبِلاَ", "chapter": 1 }, { "id": 141, "number": 141, "text": "وَفي ذِي المَعَارِجِ تَعْرُجُ الْجِيمُ مُدْغَمٌ ... وَمِنْ قَبْلُ أَخْرَج شَّطْأَهُ قَدْ تَثَقَّلَا", "chapter": 1 }, { "id": 142, "number": 142, "text": "وَعِنْدَ سَبِيلاً شِينُ ذِي الْعَرْشِ مُدْغَمٌ ... وَضَادَ لِبَعْضِ شَأْنِهِمْ مُدْغَمًا تَلَا", "chapter": 1 }, { "id": 143, "number": 143, "text": "وَفي زُوِّجَتْ سِينُ النُّفُوسِ وَمُدْغَمٌ ... لَهُ الرَّاْسُ شَيْبًا بِاخْتِلاَفٍ تَوَصَّلَا", "chapter": 1 }, { "id": 144, "number": 144, "text": "وَلِلدَّالِ كِلْمٌ تُرْبُ سَهْلٍ ذَكَا شَذاً ... ضَفَا ثَمَّ زُهْدٌ صِدْقُهُ ظَاهِرٌ جلَا", "chapter": 1 }, { "id": 145, "number": 145, "text": "وَلَمْ تُدَّغَمْ مَفْتُوحَةً بَعْدَ سَاكِنٍ ... بِحَرْفٍ بِغَيْرِ التَّاءِ فَاعْلَمْهُ وَاعْمَلَا", "chapter": 1 }, { "id": 146, "number": 146, "text": "وَفِي عَشْرِهَا وَالطَّاءِ تُدْغَمُ تَاؤُهَا ... وَفي أَحْرُفٍ وَجْهَانِ عَنْهُ تَهَلَّلَا", "chapter": 1 }, { "id": 147, "number": 147, "text": "فَمَعْ حُمِّلُوا التَّوْرَاةَ ثُمَّ الزَّكَاةَ قُلْ ... وَقُلْ آتِ ذَا الْ وَلْتَأْتِ طَائِفَةٌ عَلَا", "chapter": 1 }, { "id": 148, "number": 148, "text": "وَفي جِئْتِ شَيْئًا أَظْهَرُوا لِخِطَابِهِ ... وَنُقْصَانِهِ وَالْكَسْرُ الاِدْغَامَ سَهَّلَا", "chapter": 1 }, { "id": 149, "number": 149, "text": "وَفي خَمْسَةٍ وَهْيَ الأَوائِلُ ثَاؤُهَا ... وَفي الصَّادِ ثُمَّ السِّينِ ذَالٌ تَدَخَّلَا", "chapter": 1 }, { "id": 150, "number": 150, "text": "وَفي الَّلامِ رَاءٌ وَهْيَ في الرَّا وَأُظْهِرَا ... إِذا انْفَتَحَا بَعدَ المُسَكَّنِ مُنْرَلَا", "chapter": 1 }, { "id": 151, "number": 151, "text": "سِوَى قالَ ثُمَّ النُّونُ تُدْغَمُ فِيهِمَا ... عَلَى إِثْرِ تَحْرِيكٍ سِوَى نَحْنُ مُسْجَلَا", "chapter": 1 }, { "id": 152, "number": 152, "text": "وَتُسْكُنُ عَنْهُ الْمِيمُ مِنْ قَبْلِ بَائِهَا ... عَلَى إِثْرِ تَحْرِيكٍ فَتَخْفَى تَنَزُّلَا", "chapter": 1 }, { "id": 153, "number": 153, "text": "وَفي مَنْ يَشَاءُ با يُعَذِّبُ حَيْثُمَا ... أَتَى مُدْغَمٌ فَادْرِ الأُصُولَ لِتَأْصُلَا", "chapter": 1 }, { "id": 154, "number": 154, "text": "وَلاَ يَمْنَعُ الإِدْغامُ إِذْ هُوَ عَارِضٌ ... إِمَالَةَ كَالأَبْرَارِ وَالنَّارِ أَثْقِلَا", "chapter": 1 }, { "id": 155, "number": 155, "text": "وَأَشْمِمْ وَرُمْ فِي غَيْرِ بَاءٍ وَمِيمِهَا ... مَعَ الْبَاءِ أَوْ مِيمٍ وَكُنْ مُتَأَمِّلاَ", "chapter": 1 }, { "id": 156, "number": 156, "text": "وَإِدْغَامُ حَرْفٍ قَبْلَهُ صَحَّ سَاكِنٌ ... عَسِيرٌ وَبِالإِخْفَاءِ طَبَّقَ مَفْصِلَا", "chapter": 1 }, { "id": 157, "number": 157, "text": "خُذِ الْعَفْوَ وَأْمُرْ ثُمَّ مِنْ بَعْدِ ظُلْمِهِ ... وَفي المَهْدِ ثُمَّ الخُلْدِ وَالْعِلْمِ فَاشْمُلَا", "chapter": 1 }, { "id": 158, "number": 158, "text": "وَلَمْ يَصِلُوا هَا مُضْمَرٍ قَبْلَ سَاكِنٍ ... وَمَا قَبْلَهُ التَّحْرِيكُ لِلْكُلِّ وُصِّلَا", "chapter": 1 }, { "id": 159, "number": 159, "text": "وَمَا قَبْلَهُ التَّسْكِينُ لاِبْنِ كَثِيرِهِمْ ... وَفِيهِ مُهَاناً مَعْهُ حَفْصٌ أَخُو وِلَا", "chapter": 1 }, { "id": 160, "number": 160, "text": "وَسَكِّنْ يُؤَدِّهْ مَعْ نُوَلِّهْ وَنُصْلِهِ ... وَنُؤْتِهِ مِنْهَا فَاعَتَبِرْ صَافِياً حَلَا", "chapter": 1 }, { "id": 161, "number": 161, "text": "وَعَنْهُمْ وَعَنْ حَفْصٍ فَأَلْقِهْ وَيَتَّقِهْ ... حَمَى صَفْوَهُ قَوْمٌ بِخُلْفٍ وَأَنْهَلَا", "chapter": 1 }, { "id": 162, "number": 162, "text": "وَقُلْ بسُكُونِ الْقَافِ وَالْقَصْرِ حَفْصُهُمْ ... وَيَاْتِهْ لَدَى طه بِالاِسْكَانِ يُجْتَلَا", "chapter": 1 }, { "id": 163, "number": 163, "text": "وَفي الْكُلِّ قَصْرُ الْهَاءِ بَانَ لِسَانُهُ ... بخُلْفٍ وَفِي طَهَ بِوَجْهَيْنِ بُجِّلَا", "chapter": 1 }, { "id": 164, "number": 164, "text": "وَإِسْكَانُ يَرْضَهُ يُمْنُهُ لُبْسُ طَيِّبٍ ... بِخُلْفِهِمِاَ وَالْقَصُرَ فَاذْكُرْهُ نَوْفَلَا", "chapter": 1 }, { "id": 165, "number": 165, "text": "لَهُ الرَّحْبُ وَالزِّلْزَالُ خَيْراً يَرَهْ بِهَا ... وَشَرًّا يَرَهْ حَرْفَيْهِ سَكِّنْ لِيَسْهُلَا", "chapter": 1 }, { "id": 166, "number": 166, "text": "وَعى نَفَرٌ أَرْجِئْهُ بِالْهَمْزِ سَاكِنا ... وَفي الْهَاءِ ضَمٌّ لَفَّ دَعْوَاهُ حَرْمَلَا", "chapter": 1 }, { "id": 167, "number": 167, "text": "وَأَسْكِنْ نَصِيراً فَازَ وَاكْسِرْ لِغَيْرِهِمْ ... وَصِلْهَا جوَاداً دُونَ رَيْبٍ لِتُوصَلَا", "chapter": 1 }, { "id": 168, "number": 168, "text": "إِذَا أَلِفٌ أَوْ يَاؤُهَا بَعْدَ كَسْرَةٍ ... أَوِ الْوَاوُ عَنْ ضَمٍّ لَقِي الْهَمْزَ طُوِّلَا", "chapter": 1 }, { "id": 169, "number": 169, "text": "فَإِنْ يَنْفَصِلْ فَالْقَصْرَ بَادِرْهُ طَالِباً ... بِخُلْفِهِماَ يُرْوِيكَ دَرًّا وَمُخْضَلَا", "chapter": 1 }, { "id": 170, "number": 170, "text": "كَجِئَ وَعَنْ سُوءٍ وَشَاءَ اتِّصَالُهُ ... وَمَفْصُولُهُ في أُمِّهَا أَمْرُهُ إِلَى", "chapter": 1 }, { "id": 171, "number": 171, "text": "وَمَا بَعْدَ هَمْزٍ ثَابِتٍ أَوْ مُغَيَّرٍ ... فَقَصْرٌ وَقَدْ يُرْوَى لِوَرْش مُطَوَّلَا", "chapter": 1 }, { "id": 172, "number": 172, "text": "وَوَسَّطَهُ قَوْمٌ كَآمَنَ هَؤُلاَ ... ءِآلِهَةً آتى لِلاِيْمَانِ مُثِّلَا", "chapter": 1 }, { "id": 173, "number": 173, "text": "سِوى يَاءِ إِسْرَاءيِلَ أَوْ بَعْدَ سَاكِنٍ ... صَحِيحٍ كَقُرْآنِ وَمَسْئُولاً اسْأَلَا", "chapter": 1 }, { "id": 174, "number": 174, "text": "وَمَا بَعْدَ هَمْزِ الْوَصْلِ إيتِ ... وَبَعْضُهُمْ يُؤَاخِذُكُمْ آلانَ مُسْتَفْهِماً تَلَا", "chapter": 1 }, { "id": 175, "number": 175, "text": "وَعَادً الاُولَى وَابْنُ غَلْبُونَ طَاهِرٌ ... بِقَصْرِ جَمِيعِ الْبَاب قَالَ وَقَوَّلَا", "chapter": 1 }, { "id": 176, "number": 176, "text": "وَعَنْ كُلِّهِمْ بِالْمَدِّ مَا قَبْلَ سَاكِنٍ ... وعِنْدَ سُكُونِ الْوَقْفِ وَجْهَانِ أُصِّلا", "chapter": 1 }, { "id": 177, "number": 177, "text": "وَمُدَّ لَهُ عِنْدَ الْفَوَاتِحِ مُشْبِعا ... وَفي عَيْنٍ الْوَجْهَانِ وَالطُّولُ فُضِّلَا", "chapter": 1 }, { "id": 178, "number": 178, "text": "وَفي نَحْوِ طهَ الْقَصْرُ إِذْ لَيْسَ سَاكِنٌ ... وَمَا فِي أَلِفْ مِنْ حَرْفِ مَدٍ فَيُمْطَلَا", "chapter": 1 }, { "id": 179, "number": 179, "text": "وَإِنْ تَسْكُنِ الْيَا بَيْنَ فَتْحٍ وَهَمْزَةٍ ... بِكِلْمَةٍ أَوْ وَاوٌ فَوَجْهَانِ جُمِّلَا", "chapter": 1 }, { "id": 180, "number": 180, "text": "بِطُولٍ وَقَصْرٍ وَصْلُ وَرْشٍ وَوَقْفُهُ ... وَعِنْدَ سُكُونِ الْوَقْفِ لِلْكُلِّ أُعْمِلَا", "chapter": 1 }, { "id": 181, "number": 181, "text": "وَعَنْهُمْ سُقُوطُ الْمَدِّ فِيهِ وَوَرْشُهُمْ ... يُوَافِقُهُمْ فِي حَيْثُ لا هَمْزَ مُدْخَلَا", "chapter": 1 }, { "id": 182, "number": 182, "text": "وَفِي وَاوِ سَوْآتٍ خِلاَفٌ لِوَرْشِهِمْ ... وَعَنْ كُلٍ الْمَوْءُودَةُ اقْصُرْ وَمَوْئِلَا", "chapter": 1 }, { "id": 183, "number": 183, "text": "وَتَسْهِيلُ أُخْرَى هَمْزَتَيْنِ بِكِلْمةٍ ... سَمَا وَبِذَاتِ الْفتْحِ خُلْفٌ لِتَجْمُلَا", "chapter": 1 }, { "id": 184, "number": 184, "text": "وَقُلْ أَلِفاً عَنْ أَهْلِ مِصْرَ تَبَدَّلَّتْ ... لِوَرْشٍ وَفي بَغْدَادَ يُرْوَى مُسَهَّلَا", "chapter": 1 }, { "id": 185, "number": 185, "text": "وَحَقَّقَهَا فِي فُصِّلَتْ صُحْبَةٌ ءأَعْـ ... جَمِيٌّ وَالاْولَى أَسْقِطَنَّ لِتُسْهِلاَ", "chapter": 1 }, { "id": 186, "number": 186, "text": "وَهَمْزَة أَذْهَبْتُمْ فِي الاَحْقَافِ شُفِّعَتْ ... بِأُخْرَى كَمَا دَامَتْ وِصَالاً مُوَصَّلَا", "chapter": 1 }, { "id": 187, "number": 187, "text": "وَفِي نُونَ فِي أَنْ كَانَ شَفَّعَ حَمْزَةٌ ... وَشُعْبَةُ أَيْضاً وَالدِّمَشْقِيْ مُسَهِّلَا", "chapter": 1 }, { "id": 188, "number": 188, "text": "وَفِي آلِ عِمْرَانٍ عَنِ ابْنِ كَثِيرِهِمْ ... يُشَفَّعُ أَنْ يُؤْتَى إِلَى مَا تَسَهَّلَا", "chapter": 1 }, { "id": 189, "number": 189, "text": "وَطه وفِي الأَعْرَافِ وَالشُّعَرَا بِهَا ... ءَآمَنْتُمُ لِلكُلِّ ثَالِثًا ابْدِلَا", "chapter": 1 }, { "id": 190, "number": 190, "text": "وَحَقَّقَ ثَانٍ صُحْبَةٌ وَلِقُنْبُلٍ ... بِإِسْقَاطِهِ الأُولَى بِطَهَ تُقُبِّلَا", "chapter": 1 }, { "id": 191, "number": 191, "text": "وَفي كُلِّهَا حَفْصٌ وَأَبْدَلَ قُنْبُلٌ ... فِي اْلاَعْرَافِ مِنْهَا الْوَاوَ وَالْمُلْكِ مُوْصِلَا", "chapter": 1 }, { "id": 192, "number": 192, "text": "وَإِنْ هَمْزُ وَصْلٍ بَيْنَ لاَمٍ مُسَكَّنٍ ... وَهَمْزَةِ الاِسْتِفْهَامِ فَامْدُدْهُ مُبْدِلَا", "chapter": 1 }, { "id": 193, "number": 193, "text": "فَلِلْكُلِّ ذَا أَوْلى وَيَقْصُرُهُ الَّذِي ... يُسَهِّلُ عَنْ كُلِّ كَآلانَ مُثِّلَا", "chapter": 1 }, { "id": 194, "number": 194, "text": "وَلاَ مَدَّ بَيْنَ الْهَمْزَتَيْنِ هُنَا وَلاَ ... بِحَيْثُ ثَلاَثٌ يَتَّفِقْنَ تَنَزُّلَا", "chapter": 1 }, { "id": 195, "number": 195, "text": "وَأَضْرُبُ جَمْعِ الْهَمْزَتَيْنِ ثَلاَثَةٌ ... ءأَنْذَرْتَهُمْ أَمْ لَمْ أَئِنَّا أَءُنْزِلَا", "chapter": 1 }, { "id": 196, "number": 196, "text": "وَمَدُّكَ قَبْلَ الْفَتْحِ وَالْكَسْرِ حُجَّةٌ ... بِهَا لُذُّ وَقَبْلَ الْكَسْرِ خُلْفٌ لَهُ وَلَا", "chapter": 1 }, { "id": 197, "number": 197, "text": "وَفي سَبْعَةٍ لاَ خُلْفَ عَنْهُ بِمَرْيَمٍ ... وَفي حَرْفَيِ الأَعْرَافِ وَالشُّعَرَا الْعُلَا", "chapter": 1 }, { "id": 198, "number": 198, "text": "أَئِنَّكَ آئِفْكاً مَعًا فَوْقَ صَادِهَا ... وَفي فُصِّلَتْ حَرْفٌ وَبِالخُلْفِ سُهِّلَا", "chapter": 1 }, { "id": 199, "number": 199, "text": "وَآئِمَّةً بِالخُلْفِ قَدْ مَدَّ وَحْدَهُ ... وَسَهِّلْ سَمَا وَصْفاً وَفي النَّحْوِ أُبْدِلَا", "chapter": 1 }, { "id": 200, "number": 200, "text": "وَمَدُّكَ قَبْلَ الضَّمِّ لَبَّى حَبِيبُهُ ... بِخُلْفهِمَا بَرَّا وَجَاءَ لِيَفْصِلَا", "chapter": 1 }, { "id": 201, "number": 201, "text": "وَفي آلِ عِمْرَانَ رَووْا لِهِشَامِهِمْ ... كَحَفْصٍ وَفي الْبَاقِي كَقَالُونَ وَاعْتَلاَ", "chapter": 1 }, { "id": 202, "number": 202, "text": "وَأَسْقَطَ الأُولَى في اتِّفَاقِهِمَا مَعًا ... إِذَا كَانَتَا مِنْ كِلْمَتَيْنِ فَتَى الْعُلَا", "chapter": 1 }, { "id": 203, "number": 203, "text": "كَجَا أَمْرُنَا مِنَ السَّماَ إِنَّ أَوْلِيَا ... أُولَئِكَ أَنْوَاعُ اتِّفَاقٍ تَجَمَّلَا", "chapter": 1 }, { "id": 204, "number": 204, "text": "وَقَالُونُ وَالْبَزِّيُّ في الْفَتْحِ وَافَقَا ... وَفي غَيْرِهِ كَالْياَ وَكَالْوَاوِ سَهَّلَا", "chapter": 1 }, { "id": 205, "number": 205, "text": "وَبِالسُّوءِ إِلاَّ أَبْدَلاَ ثُمَّ أَدْغَمَا ... وَفِيهِ خِلاَفٌ عَنْهُمَا لَيْسَ مُقْفَلَا", "chapter": 1 }, { "id": 206, "number": 206, "text": "وَالاُخْرَى كَمَدٍّ عِنْدَ وَرْشٍ وَقُنْبُلٍ ... وَقَدْ قِيلَ مَحْضُ المَدِّ عَنْهَا تَبَدَّلَا", "chapter": 1 }, { "id": 207, "number": 207, "text": "وَفي هؤُلاَ إِنْ وَالْبِغَا إِنْ لِوَرْشِهِمْ ... بِيَاءٍ خَفِيفِ الْكَسْرِ بَعْضُهُمْ تَلَا", "chapter": 1 }, { "id": 208, "number": 208, "text": "وَإِنْ حَرْفُ مَدٍّ قَبْلَ هَمْزٍ مُغَيَّرٍ ... يَجُزْ قَصْرُهُ وَالْمَدُّ مَا زَالَ أَعْدَلَا", "chapter": 1 }, { "id": 209, "number": 209, "text": "وَتَسْهِيلُ الاُخْرَى في اخْتِلاَفِهِماَ سَمَا ... تَفِيءَ إِلَى مَعْ جَاءَ أُمَّةً اُنْزِلَا", "chapter": 1 }, { "id": 210, "number": 210, "text": "نَشَاءُ أَصَبْنَا والسَّماءِ أَوِ ائْتِنَا ... فَنَوْعَانِ قُلْ كالْيَا وَكَالْوَاوِ سُهِّلَا", "chapter": 1 }, { "id": 211, "number": 211, "text": "وَنَوْعَانِ مِنْهَا أُبْدِلاَ مِنْهُمَا وَقُلْ ... يَشَاءُ إِلى كالْيَاءِ أَقْيَسُ مَعْدِلَا", "chapter": 1 }, { "id": 212, "number": 212, "text": "وَعَنْ أَكْثَرِ الْقُرَّاءِ تُبْدَلُ وَاوُهَا ... وَكُلٌّ بِهَمْزِ الْكُلِّ يَبْدَا مُفَصِّلَا", "chapter": 1 }, { "id": 213, "number": 213, "text": "وَالاِبْدَالُ مَحْضٌ وَالْمُسَهَّلُ بَيْنَ مَا ... هُوَ الْهَمْزُ وَالحَرْفُ الَّذِي مِنهُ أُشْكِلَا", "chapter": 1 }, { "id": 214, "number": 214, "text": "إِذَا سَكَنَتْ فَاءً مِنَ الْفِعْلِ هَمْزَةٌ ... فَوَرْشٌ يُرِيهَا حَرْفَ مَدٍّ مُبَدِّلَا", "chapter": 1 }, { "id": 215, "number": 215, "text": "سِوَى جُمْلَةِ الإِيوَاءِ وَالْوَاوُ عَنْهُ إِنْ ... تَفَتَّحَ إِثْرَ الضَّمِّ نَحْوُ مُؤَجَّلَا", "chapter": 1 }, { "id": 216, "number": 216, "text": "وَيُبْدَلُ لِلسُّوسِيِّ كُلُّ مُسَكَّنٍ ... مِنَ الْهَمْزِ مَدًّا غَيْرَ مَجْزُومٍ اْهْمِلاَ", "chapter": 1 }, { "id": 217, "number": 217, "text": "تَسُؤْ وَنَشَأْ سِتٌّ وَعَشْرٌ يَشَأ وَمَعْ ... يُهَيِّئْ وَنَنْسَأْهَا يُنَبَّأْ تَكَمَّلَا", "chapter": 1 }, { "id": 218, "number": 218, "text": "وَهَيِّئْ وَأَنْبِئْهُمْ وَنَبِّئْ بِأَرْبَعٍ ... وَأَرْجِئْ مَعًا وَاقْرَأْ ثَلاَثًا فَحَصِّلَا", "chapter": 1 }, { "id": 219, "number": 219, "text": "وتُؤْوِي وَتُؤْوِيهِ أَخَفُّ بِهَمْزِهِ ... وَرِئْيًا بِتَرْكِ الْهَمْزِ يُشْبِهُ الامْتِلَا", "chapter": 1 }, { "id": 220, "number": 220, "text": "وَمُؤْصَدَةٌ أَوْصَدتُّ يُشْبِهُ كُلُّهُ ... تَخَيَّرَهُ أَهْلُ الأَدَاءِ مُعَلِّلَا", "chapter": 1 }, { "id": 221, "number": 221, "text": "وَبَارِئِكُمُ بِالْهَمْزِ حَالَ سُكُونِهِ ... وَقَالَ ابْنَ غَلْبُونٍ بِيَاءٍ تَبَدَّلَا", "chapter": 1 }, { "id": 222, "number": 222, "text": "وَوَالاَهُ في بِئْرٍ وَفي بِئْسَ وَرْشُهُمْ ... وَفي الذِّئْبِ وَرْشٌ وَالْكِسَائِي فَأَبْدَلَا", "chapter": 1 }, { "id": 223, "number": 223, "text": "وَفي لُؤْلُؤٍ في العُرْفِ وَالنُّكْرِ شُعْبَةٌ ... وَيَأْلِتْكُمُ الدُّورِي وَالاِبْدَالُ يُجْتَلَا", "chapter": 1 }, { "id": 224, "number": 224, "text": "وَوَرْشٌ لِئَلاَّ والنَّسِيءُ بِيَائِهِ ... وَأَدْغَمَ في يَاءِ النَّسِىءِ فَثَقَّلَا", "chapter": 1 }, { "id": 225, "number": 225, "text": "وَإِبْدَالُ أُخْرَى الْهَمْزَتَيْنِ لِكُلِّهِمْ ... إِذَا سَكَنَتْ عَزْمٌ كَآدَمَ أُوهِلَا", "chapter": 1 }, { "id": 226, "number": 226, "text": "وَحَرِّكْ لِوَرْشٍ كُلَّ سَاكِنٍ آخِرٍ ... صَحِيحٍ بِشَكْلِ الْهَمْزِ واحْذِفْهُ مُسْهِلَا", "chapter": 1 }, { "id": 227, "number": 227, "text": "وَعَنْ حَمْزَةٍ في الْوَقْفِ خُلْفٌ وَعِنْدَهُ ... رَوَى خَلَفٌ في الْوَصْلِ سَكْتًا مُقَلَّلَا", "chapter": 1 }, { "id": 228, "number": 228, "text": "وَيَسْكُتُ في شَيْءٍ وَشَيْئًا وَبَعْضُهُمْ ... لَدَى الَّلامِ لِلتَّعْرِيفِ عَنْ حَمْزَةٍ تَلَا", "chapter": 1 }, { "id": 229, "number": 229, "text": "وَشَيْءٍ وَشَيْئًا لَمْ يَزِدْ وَلِنَافِعٍ ... لَدَى يُونُسٍ آلانَ بِالنَّقْلِ نُقِّلَا", "chapter": 1 }, { "id": 230, "number": 230, "text": "وَقُلْ عَادًا الأُوْلَى بِإِسْكَانِ لامِهِ ... وَتَنْوِينُهُ بِالْكَسْرِ كَاسِيهِ ظَلَّلَا", "chapter": 1 }, { "id": 231, "number": 231, "text": "وَأَدْغَمَ بَاقِيهِمْ وَبِالنَّقْلِ وَصْلُهُمْ ... وَبَدْؤُهُمُ وَالْبَدْءُ بِالأَصْلِ فُضِّلاَ", "chapter": 1 }, { "id": 232, "number": 232, "text": "لِقَالُونَ وَالْبَصْرِي وَتُهْمَزُ وَاوُهُ ... لِقَالُونَ حَالَ النَّقْلِ بَدْءًا وَمَوْصِلَا", "chapter": 1 }, { "id": 233, "number": 233, "text": "وَتَبْدَاْ بِهَمْزِ الْوَصْلِ في النَّقْلِ كُلِّهِ ... وَإِنْ كُنْتَ مُعْتَدًّا بِعَارِضِهِ فَلَا", "chapter": 1 }, { "id": 234, "number": 234, "text": "وَنقْلُ رِدًا عَنْ نَافِعٍ وَكِتَابِيَهْ ... بِالاِسْكانِ عَنْ وَرْشٍ أَصَحُّ تَقَبَّلَا", "chapter": 1 }, { "id": 235, "number": 235, "text": "وَحَمْزَةُ عِنْدَ الْوَقْفِ سَهَّلَ هَمْزَهُ ... إِذَا كَانَ وَسْطًا أَوْ تَطَرَّفَ مَنْزِلَا", "chapter": 1 }, { "id": 236, "number": 236, "text": "فَأَبْدِلْهُ عَنْهُ حَرْفَ مَدِّ مُسَكِّنًا ... وَمِنْ قَبْلِهِ تَحْرِيكُهُ قَدْ تَنَزَّلَا", "chapter": 1 }, { "id": 237, "number": 237, "text": "وَحَرِّكْ بِهِ مَا قَبْلَهُ مَتَسَكِّنًا ... وَأَسْقِطْهُ حَتَّى يَرْجِعَ اللَّفْظُ أَسْهَلَا", "chapter": 1 }, { "id": 238, "number": 238, "text": "سِوَى أَنَّهُ مِنْ بَعْدِ مَا أَلِفٍ جَرى ... يُسَهِّلُهُ مَهْمَا تَوَسَّطَ مَدْخَلَا", "chapter": 1 }, { "id": 239, "number": 239, "text": "وَيُبْدِلُهُ مَهْمَا تَطَرَّفَ مِثْلَهُ ... وَيَقْصُرُ أَوْ يَمْضِي عَلَى الْمَدِّ أَطْوَلَا", "chapter": 1 }, { "id": 240, "number": 240, "text": "وَيُدْغِمُ فِيهِ الْوَاوَ وَالْيَاءَ مُبْدِلا ... إِذَا زِيدَتَا مِنْ قَبْلُ حَتَّى يُفَصَّلَا", "chapter": 1 }, { "id": 241, "number": 241, "text": "وَيُسْمِعُ بَعْدَ الْكَسْرِ وَالضَّمِّ هَمْزَهُ ... لَدى فَتْحِهِ يَاءًا وَوَاوًا مُحَوَّلَا", "chapter": 1 }, { "id": 242, "number": 242, "text": "وَفي غَيْرِ هذَا بَيْنَ بَيْنَ وَمِثْلُهُ ... يَقُولُ هِشَامٌ مَا تَطَرَّفَ مُسْهِلَا", "chapter": 1 }, { "id": 243, "number": 243, "text": "وَرِءْيَا عَلَى إِظْهَارِهِ وَإِدْغَامِهِ ... وَبَعْضٌ بِكَسْرِ الْهَا لِيَاءٍ تَحَوَّلَا", "chapter": 1 }, { "id": 244, "number": 244, "text": "كَقَوْلِكَ أَنْبِئْهُمْ وَنَبِّئْهُمُ وَقَدْ ... رَوَوْا أَنَّهُ بِالخَطِّ كانَ مُسَهِّلَا", "chapter": 1 }, { "id": 245, "number": 245, "text": "فَفِي الْيَا يَلِي والْوَاوِ وَالحَذْفِ رَسْمَهُ ... وَالاَخْفَشُ بَعْدَ الْكَسْرِ والضَّمِّ أَبْدَلَا", "chapter": 1 }, { "id": 246, "number": 246, "text": "بِيَاءٍ وَعَنْهُ الْوَاوُ في عَكْسِهِ وَمَنْ ... حَكَى فِيهِمَا كَالْيَا وَكَالْوَاوِ أَعْضَلاَ", "chapter": 1 }, { "id": 247, "number": 247, "text": "وَمْسْتَهْزِءُونَ الْحَذْفُ فِيهِ وَنَحْوُهُ ... وَضَمٌّ وَكَسْرٌ قَبْلُ قِيلَ وَأُخْمِلاً", "chapter": 1 }, { "id": 248, "number": 248, "text": "وَمَا فِيهِ يُلْفَى وَاسِطاً بِزَوَائِدٍ ... دَخَلْنَ عَلَيْهِ فِيهِ وَجْهَانِ أُعْمِلَا", "chapter": 1 }, { "id": 249, "number": 249, "text": "كَمَا هَا وَيَا وَالَّلامِ وَالْبَا وَنَحْوِهَا ... وَلاَمَاتِ تَعْرِيفٍ لِمَنْ قَدْ تَأَمَّلَا", "chapter": 1 }, { "id": 250, "number": 250, "text": "وَأَشْمِمْ وَرُمُ فِيمَا سِوى مُتَبَدِّلٍ ... بِهَا حَرْفَ مَدٍّ وَاعْرِفِ الْبَابَ مَحْفِلاً", "chapter": 1 }, { "id": 251, "number": 251, "text": "وَمَا وَاوٌ اَصْلِيٌّ تَسَكَّنَ قَبْلَهُ ... أوِ الْيَا فَعَنْ بَعْضٍ بِالاِدْغَامِ حُمِّلَا", "chapter": 1 }, { "id": 252, "number": 252, "text": "وَمَا قَبْلَهُ التَحْرِيكُ أَوْ أَلِفٌ مُحَرْ ... رَكاً طَرَفاً فَالْبَعْضُ بالرَّوْمِ سَهَّلَا", "chapter": 1 }, { "id": 253, "number": 253, "text": "وَمَنْ لَمْ يَرُمْ وَاعَتدَّ مَحْضاً سُكُونَهُ ... وَألْحَقَ مَفْتُوحاً فَقَدْ شَذَّ مُوغِلَا", "chapter": 1 }, { "id": 254, "number": 254, "text": "وَفِي الْهَمْزِ أَنْحَاءٌ وَعِنْدَ نُحَاتِهِ ... يُضِيءُ سَنَاهُ كُلَّمَا اسْوَدَّ أَلْيَلَا", "chapter": 1 }, { "id": 255, "number": 255, "text": "سأَذْكُرُ أَلْفَاظًا تَلِيهَا حُرُوفُهَا ... بالاِظْهَارِ وَالإدْغَامِ تُرْوىَ وَتُجْتَلَا", "chapter": 1 }, { "id": 256, "number": 256, "text": "فَدُونَكَ إِذْ فِي بَيْتهَا وَحُرُوفَهَا ... وَمَا بَعْدُ بالتَقْييدِ قُدْهُ مُذَلَّلَا", "chapter": 1 }, { "id": 257, "number": 257, "text": "سَأُسْمِي وَبَعْدَ الْوَاوِ تَسْمُو حُرُوفُ مَنْ ... تَسَمَّى عَلَى سِيمَا تَرُوقُ مُقَبَّلَا", "chapter": 1 }, { "id": 258, "number": 258, "text": "وَفِي دَالِ قَدْ أَيْضًا وَتَاءِ مُؤَنَّثٍ ... وَفِي هَلْ وَبَلْ فَاحْتَلْ بِذِهْنِكَ أَحْيَلَا", "chapter": 1 }, { "id": 259, "number": 259, "text": "نَعَمْ إِذْ تَمَشَّتْ زَيْنَبٌ صَالَ دَلُّهَا ... سَمِيَّ جَمَالٍ وَاصِلاً مَنْ تَوَصَّلا", "chapter": 1 }, { "id": 260, "number": 260, "text": "فإِظْهَارُهَا أجْرى دوَامَ نَسِيْمِهَا ... وَأَظْهَرَ رَيَّا قوْلِهِ وَاصِفٌ جَلَا", "chapter": 1 }, { "id": 261, "number": 261, "text": "وَأَدْغَمَ ضَنْكاً وَاصِلٌ تُومَ دُرِّه ... وَأَدْغَمْ مُوْلَى وُجْدُهُ دائمٌ وَلَا", "chapter": 1 }, { "id": 262, "number": 262, "text": "وَقَدْ سَحَبَتْ ذيْلاً ضَفَا ظَلَّ زَرْنَبٌ ... جلَتْهُ صبَاهُ شاَئِقاً وَمُعَلَّلَا", "chapter": 1 }, { "id": 263, "number": 263, "text": "فَاظْهَرَهَا نَجمٌ بدَا دَلَّ وَاضِحاً ... وَأَدْغَمَ وَرْشٌ ضَرَّ ظمْآنَ وَامْتَلَا", "chapter": 1 }, { "id": 264, "number": 264, "text": "وَأَدْغَمَ مُرْوٍ وَاكِفٌ ضَيْرَ ذَابِلٍ ... زَوَى ظِلَّهُ وَغْرٌ تَسَدَّاهُ كَلْكلَا", "chapter": 1 }, { "id": 265, "number": 265, "text": "وَفِي حَرْفِ زَيَّنَا خِلاَفٌ وَمُظْهِرٌ ... هِشَامٌ بِصَادٍ حَرْفَهُ مُتَحمِّلَا", "chapter": 1 }, { "id": 266, "number": 266, "text": "وَأَبْدَتْ سَنَا ثَغْرٍ صَفَتْ زُرْقُ ظَلْمِهِ ... جمَعْنَ وُرُوداً بَارِداً عَطِرَ الطِّلَا", "chapter": 1 }, { "id": 267, "number": 267, "text": "فإِظْهَارُهُ دُرٌّ نَمَتْهُ بُدُورُهُ ... وَأَدْغَمَ وَرْشٌ ظَافِراً وَمُخَوِّلَا", "chapter": 1 }, { "id": 268, "number": 268, "text": "وَأَظْهَرَ كهْفٌ وَافِرٌ سَيْبُ جُودِهِ ... زَكيٌّ وَفيٌّ عُصْرَةً وَمُحَلَّلَا", "chapter": 1 }, { "id": 269, "number": 269, "text": "وَاظْهَرَ رَاوِيهِ هِشَامٌ لَهُدِّمَتْ ... وَفِي وَجَبَتْ خُلْفُ ابْنِ ذَكْوانَ يُفْتَلَا", "chapter": 1 }, { "id": 270, "number": 270, "text": "أَلاَ بَلْ وَهَلْ تَرْوِي ثَنَا ظَعْنِ زَيْنَبٍ ... سَمِيرَ نَوَاهَا طِلْحَ ضُرٍ وَمُبْتَلَا", "chapter": 1 }, { "id": 271, "number": 271, "text": "فَأَدْغَمَهَا رَاوٍ وَأَدْغَمَ فَاضِلٌ ... وَقُورٌ ثنَاهُ سَرَّ تَيْماً وَقَدْ حَلَا", "chapter": 1 }, { "id": 272, "number": 272, "text": "وَبَلْ فِي النِّسَا خَلاَّدُهُمْ بِخِلاَفِهِ ... وَفِي هَلْ تَرَى الإدْغَامُ حُبَّ وَحُمِّلَا", "chapter": 1 }, { "id": 273, "number": 273, "text": "وَأَظْهِرْ لَدَى وَاعٍ نَبِيلٍ ضَمَانُهُ ... وَفِي الرَّعْدِ هَلْ وَاسْتَوْفِ لاَ زَاجِراً هَلاُ", "chapter": 1 }, { "id": 274, "number": 274, "text": "وَلاَ خُلفَ فِي الإِدْغَامِ إِذْ ذَلَّ ظاَلِمٌ ... وَقَدْ تيَّمَتْ دَعْدٌ وَسِيماً تَبَتَّلَا", "chapter": 1 }, { "id": 275, "number": 275, "text": "وَقَامَتْ تُرِيْهِ دُمْيَةٌ طِيْبَ وَصْفِهَا ... وَقُلْ بَلْ وَهَلْ رَاهَا لَبِيْبٌ وَيَعْقِلَا", "chapter": 1 }, { "id": 276, "number": 276, "text": "وَمَا أَوْلُ الْمِثْلَينِ فِيهِ مُسَكَّنٌ ... فَلاَ بُدَّ مِنْ إِدْغَامِهِ مُتَمَثِّلاَ", "chapter": 1 }, { "id": 277, "number": 277, "text": "وَإِدْغَامُ بَاءِ الْجَزْمِ فِي الْفَاءٍ قَدْ رَسَا ... حَمِيداً وَخَيِّرْ فِي يَتُبْ قاَصِداً وَلَا", "chapter": 1 }, { "id": 278, "number": 278, "text": "وَمَعْ جَزْمِهِ يَفْعَلْ بِذَلِكَ سَلَّمُوا ... وَنَخْسِفْ بِهِمْ رَاعَوْا وَشَذَّا تَثَقُّلاً", "chapter": 1 }, { "id": 279, "number": 279, "text": "وَعُذْتُ عَلَى إِدْغَامِهِ وَنَبَذْتُهاَ ... شَوَاهِدُ حَمَّادٍ وَأَورِثْتُمُوا حَلَا", "chapter": 1 }, { "id": 280, "number": 280, "text": "لَهُ شَرْعُهُ وَالرَّاءُ جَزْماً بِلاَمِهاَ ... كَوَاصِبرْ لِحُكْمِ طالَ بُالْخُلْفُ يَذْبُلَا", "chapter": 1 }, { "id": 281, "number": 281, "text": "وَيَاسِيْنَ أَظْهِرْ عنْ فَتًى حَقُهُ بَدَا ... وَنُونَ وَفيهِ الْخُلْفُ عَنْ وَرْشِهِمْ خَلَا", "chapter": 1 }, { "id": 282, "number": 282, "text": "وَحِرْمِيُّ نَصْرٍ صَادَ مَرْيَمَ مَنْ يُرِدْ ... ثَوَابَ لَبِثْتَ الْفَرْدَ وَالجَمْعُ وَصَّلَا", "chapter": 1 }, { "id": 283, "number": 283, "text": "وَطاسِيْنَ عِنْدَ الْمِيْمِ فَازَ اتَخَذْتُمْ ... أَخَذْتُمْ وَفِي الإِفْرَادِ عاشَرَ دَغْفَلَا", "chapter": 1 }, { "id": 284, "number": 284, "text": "وَفِي ارْكَبْ هُدَى بَرٍّ قَرِيبٍ بِخُلْفِهِمْ ... كَمَا ضاَعَ جاَ يَلْهَثْ لَهُ دَارِ جُهَّلَا", "chapter": 1 }, { "id": 285, "number": 285, "text": "وَقَالُونُ ذُو خُلْفٍ وَفِي الْبَقَرَهْ فَقُلْ ... يُعَذِّبْ دَنَا بِالْخُلْفِ جَوْداً وَمُوبِلَا", "chapter": 1 }, { "id": 286, "number": 286, "text": "وَكُلُّهُمُ التَّنْوينَ وَالنُّونَ أَدْغَمُوا ... بِلاَ غُنَّةٍ فِي اللاَّمِ وَالرَّا لِيَجْمُلَا", "chapter": 1 }, { "id": 287, "number": 287, "text": "وَكُلٌّ بِيَنْمُو أَدْغَمُوا مَعَ غُنَّةٍ ... وَفِي الْوَاوِ وَالْيَا دُونَهَا خَلَفٌ تَلَا", "chapter": 1 }, { "id": 288, "number": 288, "text": "وَعِنْدَهُمَا لِلكُلِّ أَظْهِرْ بِكِلْمَةٍ ... مَخَافَةَ إِشْبَاهِ الْمُضَاعَفِ أَثْقَلَا", "chapter": 1 }, { "id": 289, "number": 289, "text": "وَعِنْدَ حُرُوفِ الْحَلْقِ لِلكُلِ أُظْهِرَا ... أَلاَ هاَجَ حُكْمٌ عَمَّ خاَلِيْهِ غُفَّلَا", "chapter": 1 }, { "id": 290, "number": 290, "text": "وَقَلْبُهُمَا مِيْماً لَدَى الْبَا وَأَخْفِيَا ... عَلَى غُنَّةٍ عِنْدَ الْبَوَاقِي لِيَكْمُلَا", "chapter": 1 }, { "id": 291, "number": 291, "text": "وَحَمْزَةُ مِنْهُمْ وَالْكِسَائِيُّ بَعْدَهُ ... أَمَالاَ ذَوَاتِ الْياَءِ حَيْثُ تأَصَّلَا", "chapter": 1 }, { "id": 292, "number": 292, "text": "وَتَثْنِيَةُ الأَسْماءِ تَكْشِفُهَا وَإِنْ ... رَدَدْتَ إِلَيْكَ الْفِعْلَ صَادَفْتَ مَنْهلَا", "chapter": 1 }, { "id": 293, "number": 293, "text": "هَدَى وَاشْتَرَاهُ وَالْهَوَى وَهُدَاهُمُ ... وَفِي أَلِفِ التَّأْنِيثِ فِي الْكُلِّ مَيَّلَا", "chapter": 1 }, { "id": 294, "number": 294, "text": "وَكَيْفَ جَرَتْ فَعْلَى فَفِيهَا وُجُودُهَا ... وَإِنْ ضُمَّ أَوْ يُفْتَحْ فَعَالَى فَحَصِّلَا", "chapter": 1 }, { "id": 295, "number": 295, "text": "وَفِي اسْمٍ فِي الاِستِفْهَامِ أَنَّى وَفِي مَتَى ... مَعاً وَعَسَى أَيْضاً أَمَالاَ وَقُلْ بَلَى", "chapter": 1 }, { "id": 296, "number": 296, "text": "وَمَا رَسَمُوا بالْيَاءِ غَيْرَ لَدَى وَمَا ... زَكَى وَإِلَى مِنْ بَعْدُ حَتَّى وَقُلْ عَلَى", "chapter": 1 }, { "id": 297, "number": 297, "text": "وَكُلُّ ثُلاَثِيٍّ يَزِيْدُ فَإِنَّهُ ... مُمَالٌ كَزَكَّاهَا وَأَنْجَى مَعَ ابْتَلَى", "chapter": 1 }, { "id": 298, "number": 298, "text": "وَلَكِنَّ أَحْيَا عَنْهُمَا بَعْدَ وَاوِهِ ... وَفِيمَا سَوَاهُ لِلكِسَائِيِّ مُيِّلاً", "chapter": 1 }, { "id": 299, "number": 299, "text": "وَرُءْيَايَ وَالرءُيَا وَمَرْضَاتِ كَيْفَمَا ... أَتَى وَخَطَايَا مِثْلُهُ مُتَقَبَّلاً", "chapter": 1 }, { "id": 300, "number": 300, "text": "وَمَحْيَاهُمُو أَيْضًا وَحَقَّ تُقَاتِهِ ... وَفِي قَدْ هَدَانِي لَيْسَ أمْرُكَ مُشْكِلَا", "chapter": 1 }, { "id": 301, "number": 301, "text": "وَفِي الْكَهْفِ أَنْسَانِيْ وَمِنْ قَبْلُ جَاءَ مَنْ ... عَصَانِيْ وَأَوْصَانِيْ بِمَرْيَمَ يُجْتَلَا", "chapter": 1 }, { "id": 302, "number": 302, "text": "وَفِيهَا وَفِي طَاسِيْنَ آتَانِيَ الَّذِي ... أَذَعْتُ بِهِ حَتَّى تَضَوَّعَ مَنْدَلَا", "chapter": 1 }, { "id": 303, "number": 303, "text": "وَحَرَفُ تَلاَهَا مَعْ طَحَاهَا وَفِي سَجَى ... وَحَرْفُ دَحَاهَا وَهْيَ بِالْوَاوِ تُبْتَلَا", "chapter": 1 }, { "id": 304, "number": 304, "text": "وَأَمَّا ضُحَاهَا وَالضُّحَى وَالرِّبا مَعَ الْـ ... قُوَى فَأَمَالاَهَا وَبِالْوَاوِ تُخْتَلَا", "chapter": 1 }, { "id": 305, "number": 305, "text": "وَرُؤيَاكَ مَعْ مَثْوَايَ عَنْهُ لِحَفْصِهِمْ ... وَمَحْيَايَ مِشْكَاةٍ هُدَايَ قَدِ انجَلَا", "chapter": 1 }, { "id": 306, "number": 306, "text": "وَممَّا أَمَالاَهُ أَوَاخِرُ آيِ مَا ... بطِه وَآيِ الْنَّجْمِ كَيْ تَتَعَدَّلاَ", "chapter": 1 }, { "id": 307, "number": 307, "text": "وَفِي الشَّمْسِ وَالأَعْلَى وَفِي اللَّيْلِ وَالضُّحَى ... وَفِي اقْرَأَ وَفِي وَالنَّازِعَاتِ تَمَيَّلَا", "chapter": 1 }, { "id": 308, "number": 308, "text": "وَمِنْ تَحْتِهَا ثُمَّ الْقِيَامَةِ فِي الْـ ... مَعَارِجِ يا مِنْهَالُ أَفْلَحْتَ مُنْهِلَا", "chapter": 1 }, { "id": 309, "number": 309, "text": "رَمَى صُحْبَةٌ أَعْمَى فِي الاِسْراءِ ثَانِيًا ... سُوًى وَسُدًى فِي الْوَقْفِ عَنْهُمْ تَسَبَّلاِ", "chapter": 1 }, { "id": 310, "number": 310, "text": "وَرَاءُ تَراءَى فازَ فِي شُعَرَائِهِ ... وَأَعْمى فِي الاِسْرا حُكْمُ صُحْبَةٍ اَوَّلَا", "chapter": 1 }, { "id": 311, "number": 311, "text": "وَمَا بَعْدَ رَاءٍ شَاعَ حُكْمًا وَحَفْصُهُمْ ... يُوَالِي بِمَجْرَاهَا وَفي هُودَ أُنْزِلَا", "chapter": 1 }, { "id": 312, "number": 312, "text": "نَأَى شَرْعُ يُمْنٍ بِاخْتِلاَفٍ وَشُعْبَةٌ ... في الاِسْرَا وَهُمْ وَالنُّونُ ضَوْءُ سَنًا تَلَا", "chapter": 1 }, { "id": 313, "number": 313, "text": "إِنَاهُ لَهُ شَافٍ وَقُلْ أَوْ كِلاَهُمَا ... شَفَا وَلِكَسْرٍ أَوْ لِيَاءٍ تَمَيَّلَا", "chapter": 1 }, { "id": 314, "number": 314, "text": "وَذُو الرَّاءِ وَرْشٌ بَيْنَ بَيْنَ وَفي أَرَا ... كَهُمْ وَذَوَاتِ الْيَا لَهُ الْخُلْفُ جُمِّلَا", "chapter": 1 }, { "id": 315, "number": 315, "text": "وَلكِنْ رُءُوسُ الآيِ قَدْ قَلَّ فَتْحُهَا ... لَهُ غَيْرَ مَاهَا فِيهِ فَاحْضُرْ مُكَمَّلَا", "chapter": 1 }, { "id": 316, "number": 316, "text": "وَكَيْفَ أَتَتْ فَعْلَى وَآخِرُ آيِ مَا ... تَقَدَّمَ لِلبَصْرِيْ سِوَى رَاهُمَا اعْتَلَا", "chapter": 1 }, { "id": 317, "number": 317, "text": "وَيَا وَيْلَتَى أَنَّى وَيَا حَسْرَتَى طَوَوْا ... وعَنْ غَيْرِهِ قِسْهَا وَيَا أَسَفَى الْعُلَا", "chapter": 1 }, { "id": 318, "number": 318, "text": "وَكَيْفَ الثُّلاَثِيْ غَيْرَ زَاغَتْ بِمَاضِيٍ ... أَمِلْ خَابَ خَافُوا طَابَ ضَاقَتْ فَتُجْمِلَا", "chapter": 1 }, { "id": 319, "number": 319, "text": "وَحَاقَ وَزَاغُوا جَاءَ شَاءَ وَزَادَ فُزْ ... وَجَاءَ ابْنُ ذَكْوَانٍ وَفِي شَاءَ مَيَّلَا", "chapter": 1 }, { "id": 320, "number": 320, "text": "فَزَادَهُمُ الأُولَى وَفِي الْغَيْرِ خُلْفُهُ ... وَقُلْ صُحْبَةٌ بَلْ رَانَ وَاصْحَبْ مُعَدَّلَا", "chapter": 1 }, { "id": 321, "number": 321, "text": "وَفِي أَلِفَاتٍ قَبْلَ رَا طَرَفٍ أَتَتْ ... بِكَسْرٍ أَمِلْ تُدْعَى حَمِيداً وَتُقْبَلاَ", "chapter": 1 }, { "id": 322, "number": 322, "text": "كَأَبْصَارِهِمْ وَالدَّارِ ثُمَّ الْحِمَارِ مَعْ ... حِمَارِكَ وَالْكُفَّارِ وَاقْتَسْ لِتَنْضُلَا", "chapter": 1 }, { "id": 323, "number": 323, "text": "وَمَعْ كَافِرِينَ الْكافِرِينَ بِيَائِهِ ... وَهَارٍ رَوَى مُرْوٍ بِخُلْفٍ صَدٍ حَلَا", "chapter": 1 }, { "id": 324, "number": 324, "text": "بَدَارِ وَجَبَّارِينَ وَالْجَارِ تَمَّمُوا ... وَوَرْشٌ جَمِيعَ الْبَابِ كَانَ مُقَلِّلَا", "chapter": 1 }, { "id": 325, "number": 325, "text": "وَهذَانِ عَنْهُ بِاخْتِلاَفٍ وَمَعَهُ في الْـ ... بَوَارِ وَفي الْقَهَّارِ حَمْزَةُ قَلَّلَا", "chapter": 1 }, { "id": 326, "number": 326, "text": "وَإِضْجَاعُ ذِي رَاءَيْنِ حَجَّ رُوَاتُهُ ... كَالأَبْرَارِ وَالتَّقْلِيلُ جادَلَ فَيْصَلَا", "chapter": 1 }, { "id": 327, "number": 327, "text": "وَإِضْجَاعُ أَنْصَارِي تَمِيمٌ وَسَارِعُوا ... نُسَارِعُ وَالْبَارِي وَبَارِئِكُمْ تَلَا", "chapter": 1 }, { "id": 328, "number": 328, "text": "وَآذَانِهِمْ طُغْيَانِهِمْ وَيُسَارِعُـ ... ونَ آذَانِنَا عَنْهُ الْجَوَارِي تَمَثَّلَا", "chapter": 1 }, { "id": 329, "number": 329, "text": "يُوَارِي أُوَارِي فِي العُقُودِ بِخُلْفِهِ ... ضِعَافًا وَحَرْفَا النَّمْلِ آتِيكَ قُوَّلَا", "chapter": 1 }, { "id": 330, "number": 330, "text": "بِخُلْفٍ ضَمَمْنَاهُ مَشَارِبُ لَامِعٌ ... وَآنِيَةٍ فِي هَلْ أَتَاكَ لِأَعْدَلَا", "chapter": 1 }, { "id": 331, "number": 331, "text": "وَفِي الْكَافِرُونَ عَابِدُونَ وَعَابِدٌ ... وَخُلْفُهُمُ في النَّاسِ في الْجَرِّ حُصِّلَا", "chapter": 1 }, { "id": 332, "number": 332, "text": "حِمَارِكَ وَالمِحْرَابِ إِكْرَاهِهِنَّ وَالْـ ... حِمَارِ وَفي الإِكْرَامِ عِمْرَانَ مُثِّلَا", "chapter": 1 }, { "id": 333, "number": 333, "text": "وَكُلٌّ بِخُلْفٍ لاِبْنِ ذَكْوَانَ غَيْرَ مَا ... يُجَرُّ مِنَ الْمِحْرَابِ فَاعْلَمْ لِتَعْمَلَا", "chapter": 1 }, { "id": 334, "number": 334, "text": "وَلاَ يَمْنَعُ الإِسْكَانُ فِي الْوَقْفِ عَارِضًا ... إِمَالَةَ مَا لِلكَسْرِ فِي الْوَصْلِ مُيِّلَا", "chapter": 1 }, { "id": 335, "number": 335, "text": "وَقَبْلَ سُكُونٍ قِفْ بِمَا فِي أُصُولِهِمْ ... وَذُو الرَّاءِ فِيهِ الخُلْفُ في الْوَصْلِ يُجُتَلَا", "chapter": 1 }, { "id": 336, "number": 336, "text": "كَمُوسَى الْهُدَى عِيسَى ابْنَ مَرْيَمَ والْقُرَى الْ ... لَتِي مَعَ ذِكْرَى الدَّارِ فَافْهَمْ مُحَصِّلَا", "chapter": 1 }, { "id": 337, "number": 337, "text": "وَقَدْ فَخَّمُوا التَّنْوِينَ وَقْفاً وَرَقَّقُوا ... وَتَفْخِيمُهُمْ في النَّصْبِ أَجْمَعُ أَشْمُلاَ", "chapter": 1 }, { "id": 338, "number": 338, "text": "مُسَمَّى وَمَوْلًى رَفْعُهُ مَعْ جَرِّهِ ... وَمَنْصُوبُهُ غُزَّى وَتَتْرًا تَزَيَّلَا", "chapter": 1 }, { "id": 339, "number": 339, "text": "وَفِي هَاءِ تَأْنِيثِ الْوُقُوفِ وَقَبْلَهَا ... مُمَالُ الْكِسَائِيْ غَيْرَ عَشْرٍ لِيَعْدِلَا", "chapter": 1 }, { "id": 340, "number": 340, "text": "وَيَجْمَعُهَا حَقٌ ضِغَاطُ عَصٍ خَظَا ... وَأَكْهَرُ بَعْدَ الْيَاءِ يَسْكُنُ مُيِّلَا", "chapter": 1 }, { "id": 341, "number": 341, "text": "أَوِ الْكَسْرِ وَالإِسْكَانُ لَيْسَ بِحَاجِزٍ ... وَيَضْعُفُ بَعْدَ الْفَتْحِ وَالضَّمِّ أَرْجُلَا", "chapter": 1 }, { "id": 342, "number": 342, "text": "لَعِبْرَهْ مِائَهْ وِجْهَهْ وَلَيْكَهْ وَبَعْضُهُمْ ... سِوى أَلِفٍ عِنْدَ الْكِسَائِيِّ مَيَّلَا", "chapter": 1 }, { "id": 343, "number": 343, "text": "وَرَقَّقَ وَرْشٌ كُلَّ رَاءٍ وَقَبْلَهَا ... مُسَكَّنَةً يَاءٌ أَوِ الْكَسْرُ مُوصَلَا", "chapter": 1 }, { "id": 344, "number": 344, "text": "وَلَمْ يَرَ فَصْلاً سَاكِنًا بَعْدَ كَسْرَةٍ ... سِوى حَرْفِ الاِسْتِعْلاَ سِوَى الْخَا فَكَمَّلَا", "chapter": 1 }, { "id": 345, "number": 345, "text": "وَفَخَّمَهَا في الأَعْجَمِيِّ وَفِي إِرَمْ ... وَتَكْرِيرِهَا حَتَّى يُرى مُتَعَدِّلَا", "chapter": 1 }, { "id": 346, "number": 346, "text": "وَتَفْخِيمُهُ ذِكْرًا وَسِتْرًا وَبَابَهُ ... لَدى جِلَّةِ الأَصْحَابِ أَعْمَرُ أَرْحُلَا", "chapter": 1 }, { "id": 347, "number": 347, "text": "وَفي شَرَرٍ عَنْهُ يُرَقِّقُ كُلُّهُمْ ... وَحَيْرَانَ بِالتَّفْخِيمِ بَعْضٌ تَقَبَّلَا", "chapter": 1 }, { "id": 348, "number": 348, "text": "وَفي الرَّاءِ عَنْ وَرْشٍ سِوَى مَا ذَكَرْتُهُ ... مَذَاهِبُ شَذَّتْ فِي الأَدَاءِ تَوَقُّلَا", "chapter": 1 }, { "id": 349, "number": 349, "text": "وَلاَ بُدَّ مِنْ تَرْقِيِقِهاَ بَعْدَ كَسْرَةٍ ... إِذَا سَكَنَتْ ياَ صَاحِ لِلسَّبْعَةِ المَلا", "chapter": 1 }, { "id": 350, "number": 350, "text": "وَمَا حَرْفُ الاِسْتِعْلاَءُ بَعْدُ فَرَاؤُهُ ... لِكُلِّهِمُ التَّفْخِيمُ فِيهاَ تَذَلَّلَا", "chapter": 1 }, { "id": 351, "number": 351, "text": "وَيَجْمَعُهاَ قِظْ خُصَّ ضَغْطٍ وَخُلْفُهُمْ ... بِفِرْقٍ جَرى بَيْنَ المَشَايِخِ سَلْسَلَا", "chapter": 1 }, { "id": 352, "number": 352, "text": "وَمَا بَعْدَ كَسْرٍ عَارِضٍ أَوْ مُفَصَّلٍ ... فَفَخِّمْ فَهذاَ حُكْمُهُ مُتَبَذِّلاَ", "chapter": 1 }, { "id": 353, "number": 353, "text": "وَمَا بَعْدَهُ كَسْرٌ أَوِ الْيَا فَمَا لَهُمْ ... بِتَرْقِيقِهِ نَصٌّ وَثِيقٌ فَيَمْثُلَا", "chapter": 1 }, { "id": 354, "number": 354, "text": "وَمَا لِقِيَاسٍ فِي الْقِرَاءة مَدْخَلٌ ... فَدُونَكَ مَا فِيهِ الرِّضاَ مُتَكَفِّلَا", "chapter": 1 }, { "id": 355, "number": 355, "text": "وَتَرْقِيقُهاَ مَكْسُورَةً عِنْدَ وَصْلِهِمْ ... وَتَفْخِيمُهاَ في الْوَقْفِ أَجْمَعُ أَشْمُلَا", "chapter": 1 }, { "id": 356, "number": 356, "text": "وَلكِنَّهَا في وَقْفِهِمْ مَعَ غَيْرِهاَ ... تُرَقَّقُ بَعْدَ الْكَسْرِ أَوْ مَا تَمَيَّلَا", "chapter": 1 }, { "id": 357, "number": 357, "text": "أَوِ الْيَاء تَأْتِي بِالسُّكُونِ وَرَوْمُهُمْ ... كَمَا وَصْلِهِمْ فَابْلُ الذَّكَاءَ مُصَقَّلَا", "chapter": 1 }, { "id": 358, "number": 358, "text": "وَفِيماَ عَدَا هذَا الَّذِي قَدْ وَصَفْتُهُ ... عَلَى الأَصْلِ بِالتَّفْخِيمِ كُنْ مُتَعَمِّلَا", "chapter": 1 }, { "id": 359, "number": 359, "text": "وَغَلَّظَ وَرْشٌ فَتْحَ لاَمٍ لِصَادِهاَ ... أَوِ الطَّاءِ أَوْ لِلظَّاءِ قَبْلُ تَنَزُّلَا", "chapter": 1 }, { "id": 360, "number": 360, "text": "إِذَا فُتِحَتْ أَوْ سُكِّنَتْ كَصَلاتِهِمْ ... وَمَطْلَعِ أَيْضًا ثمَّ ظَلَّ وَيُوصَلَا", "chapter": 1 }, { "id": 361, "number": 361, "text": "وَفي طَالَ خُلْفٌ مَعْ فِصَالاً وَعِنْدَماَ ... يُسَكَّنُ وَقْفاً وَالمُفَخَّمُ فُضِّلَا", "chapter": 1 }, { "id": 362, "number": 362, "text": "وَحُكْمُ ذَوَاتِ الْياَءِ مِنْهاَ كَهذِهِ ... وَعِنْدَ رُءُوسِ الآيِ تَرْقِيقُهاَ اعْتَلَا", "chapter": 1 }, { "id": 363, "number": 363, "text": "وَكُلٌّ لَدَى اسْمِ اللهِ مِنْ بَعْدِ كَسْرَةٍ ... يُرَقِّقُهَا حَتَّى يَرُوقَ مُرَتَّلَا", "chapter": 1 }, { "id": 364, "number": 364, "text": "كَمَا فَخَّمُوهُ بَعْدَ فَتْحٍ وَضَمَّةٍ ... فَتَمَّ نِظَامُ الشَّمْلِ وَصْلاً وَفَيْصَلَا", "chapter": 1 }, { "id": 365, "number": 365, "text": "وَالاِسْكَانُ أَصْلُ الْوَقْفِ وَهْوَ اشْتِقَاقُهُ ... مِنَ الْوَقْفِ عَنْ تَحْرِيكِ حَرْفٍ تَعَزَّلَا", "chapter": 1 }, { "id": 366, "number": 366, "text": "وَعِنْدَ أَبِي عَمْرٍو وَكُوفِيِّهِمْ بِهِ ... مِنَ الرَّوْمِ وَالإِشْمَامِ سَمْتٌ تَجَمَّلَا", "chapter": 1 }, { "id": 367, "number": 367, "text": "وَأَكْثَرُ أَعْلاَمِ الْقُرَانِ يَرَاهُما ... لِسَائِرِهِمْ أَوْلَى الْعَلاَئِقِ مِطْوَلاَ", "chapter": 1 }, { "id": 368, "number": 368, "text": "وَرَوْمُكَ إِسْمَاعُ المُحَرَّكِ وَاقِفًا ... بِصَوْتٍ خَفِيٍّ كُلَّ دَانٍ تَنَوَّلَا", "chapter": 1 }, { "id": 369, "number": 369, "text": "وَالاِشْمَامُ إِطْبَاقُ الشِّفَاهِ بُعَيْدَ مَا ... يُسَكَّنُ لاَ صَوْتٌ هُنَاكَ فَيَصْحَلَا", "chapter": 1 }, { "id": 370, "number": 370, "text": "وَفِعْلُهُماَ في الضَّمِّ وَالرَّفْعِ وَارِدٌ ... وَرَوْمُكَ عِنْدَ الْكَسْرِ وَالْجَرِّ وُصِّلَا", "chapter": 1 }, { "id": 371, "number": 371, "text": "وَلَمْ يَرَهُ في الْفَتْحِ وَالنَّصْبِ قَارِئٌ ... وَعِنْدَ إِمَامِ النَّحْوِ في الْكُلِّ أُعْمِلَا", "chapter": 1 }, { "id": 372, "number": 372, "text": "وَمَا نُوِّعَ التَّحْرِيكُ إِلاَّ لِلاَزِمٍ ... بِنَاءً وَإِعْرَاباً غَداَ مُتَنَقِّلَا", "chapter": 1 }, { "id": 373, "number": 373, "text": "وَفي هَاءِ تَأْنِيثٍ وَمِيمِ الْجَمِيعِ قُلْ ... وَعَارِضِ شَكْلٍ لَمْ يَكُوناَ لِيَدْخُلَا", "chapter": 1 }, { "id": 374, "number": 374, "text": "وَفي الْهَاءِ لِلإِضْمَارِ قَوْمٌ أَبَوْهُمَا ... وَمِنْ قَبْلِهِ ضَمٌّ أَوِ الْكَسْرُ مُثِّلَا", "chapter": 1 }, { "id": 375, "number": 375, "text": "أَوُ امَّاهُمَا وَاوٌ وَيَاءٌ وَبَعْضُهُمْ ... يُرَى لَهُمَا فِي كُلِّ حَالٍ مُحَلِّلَا", "chapter": 1 }, { "id": 376, "number": 376, "text": "وَكُوفِيُّهُمْ وَالْمَازِنِيُّ وَنَافِعٌ ... عُنُوا بِاتَّبَاعِ الْخَطِّ فِي وَقْفِ الاِبْتِلَا", "chapter": 1 }, { "id": 377, "number": 377, "text": "وَلاِبْنِ كَثِيٍر يُرْتَضَى وَابْنِ عَامِرٍ ... وَمَا اخْتَلَفُوا فِيهِ حَرٍ أَنْ يُفَصَّلَا", "chapter": 1 }, { "id": 378, "number": 378, "text": "إِذَا كُتِبَتْ بِالتَّاءِ هَاءُ مُؤَنَّثٍ ... فَبِالْهَاءِ قِفْ حَقَّا رِضًى وَمُعَوِّلَا", "chapter": 1 }, { "id": 379, "number": 379, "text": "وَفي اللاَّتَ مَعْ مَرْضَاتِ مَعْ ذَاتَ بَهْجَةٍ ... وَلاَتَ رِضًى هَيْهَاتَ هَادِيِه رُفِّلَا", "chapter": 1 }, { "id": 380, "number": 380, "text": "وَقِفْ يَا أَبَهْ كُفْؤًا دَنَا وَكَأَيِّنِ الْـ ... وُقُوفُ بِنُونٍ وَهْوَ بِالْيَاءِ حُصِّلَا", "chapter": 1 }, { "id": 381, "number": 381, "text": "وَمَالِ لَدَى الْفُرْقَانِ وَالْكَهْفِ وَالنِّسَا ... وَسَالَ عَلَى مَا حَجَّ وَالْخُلْفُ رُتِّلَا", "chapter": 1 }, { "id": 382, "number": 382, "text": "وَيَا أَيُّهَا فَوْقَ الدُّخَانِ وَأَيُّهَا ... لَدَى النُّورِ وَالرِّحْمنِ رَافَقْنَ حُمَّلاَ", "chapter": 1 }, { "id": 383, "number": 383, "text": "وَفي الْهَا عَلَى الإِتْبَاعِ ضَمَّ ابْنُ عَامِرٍ ... لَدَى الْوَصْلِ وَالْمَرْسُومِ فِيهِنَّ أَخْيَلَا", "chapter": 1 }, { "id": 384, "number": 384, "text": "وَقِفْ وَيْكَأَنَّهْ وَيْكَأَنَّ بِرَسْمِهِ ... وَبِالْيَاءِ قِفْ رِفْقًا وَبِالْكَافِ حُلِّلَا", "chapter": 1 }, { "id": 385, "number": 385, "text": "وَأَيًّا بأَيًّا مَا شَفَا وَسِوَاهُمَا ... بِمَا وَبِوَادِي النَمْلِ بِالْيَا سَناً تَلَا", "chapter": 1 }, { "id": 386, "number": 386, "text": "وَفِي مَهْ وَمِمَّهْ قِفْ وَعَمَّهْ لِمَهْ بِمَهْ ... بِخُلْفٍ عَنِ الْبَزِّيِّ وَادْفَعْ مُجَهِّلَا", "chapter": 1 }, { "id": 387, "number": 387, "text": "وَلَيْسَتْ بِلاَمِ الْفِعْلِ يَاءُ إِضَافَةٍ ... وَمَا هِيَ مِنْ نَفْسِ اْلأُصُولِ فَتُشْكِلَا", "chapter": 1 }, { "id": 388, "number": 388, "text": "وَلكِنَّهَا كالْهَاءِ وَالْكَافِ كُلُّ مَا ... تَلِيهِ يُرى لِلْهَاءِ وَالْكَافِ مَدْخَلَا", "chapter": 1 }, { "id": 389, "number": 389, "text": "وَفي مِائَتَيْ ياَءٍ وَعَشْرٍ مُنِيفَةٍ ... وَثِنْتَيْنِ خُلْفُ الْقَوْمِ أَحْكِيهِ مُجْمَلَا", "chapter": 1 }, { "id": 390, "number": 390, "text": "فَتِسْعُونَ مَعْ هَمْزٍ بِفَتْحٍ وَتِسَعُهَا ... سَما فَتْحُهاَ إَلاَّ مَوَاضِعَ هُمَّلَا", "chapter": 1 }, { "id": 391, "number": 391, "text": "فَأَرْنِي وَتَفْتِنِّي اتَّبِعْنِي سُكُونُهاَ ... لِكُلٍ وَتَرْحَمْنِي أَكُنْ وَلَقَدْ جَلَا", "chapter": 1 }, { "id": 392, "number": 392, "text": "ذَرُونِيَ وَادْعُونِيْ اذْكُرُونِيَ فَتْحُهاَ ... دَوَاءٌ وَأَوْزِعْنِي مَعاً جَادَ هُطَّلَا", "chapter": 1 }, { "id": 393, "number": 393, "text": "لِيَبْلُوَنِي مَعْهُ سَبِيلِي لِنَافِعٍ ... وَعَنْهُ وَلِلْبصْرِي ثَمَانٍ تُنُخِّلَا", "chapter": 1 }, { "id": 394, "number": 394, "text": "بِيُوسُفَ إِنيِّ الأَوَّلاَنِ وَلِيْ بِها ... وَضَيْفِْي وَيَسِّرْ لِيْ وَدُونِيْ تَمَثَّلَا", "chapter": 1 }, { "id": 395, "number": 395, "text": "وَيَاءَانِ في اجْعَلْ لِي وَأَرْبَعٌ إِذْ حَمَتْ ... هُدَاهاَ وَلكِنِّي بِهاَ اثْناَنِ وُكِّلَا", "chapter": 1 }, { "id": 396, "number": 396, "text": "وَتَحْتِي وَقُلْ في هُودَ إِنِّي أَرَاكُمُو ... وَقُلْ فَطَرَنْ في هُودَ هَادِيِهِ أَوْصَلَا", "chapter": 1 }, { "id": 397, "number": 397, "text": "وَيَحْزُنُنِيْ حِرْمِيُّهُمْ تَعِدَانِنِي ... حَشَرْتَنِيَ أَعْمى تَأْمُرُونِيَ وَصَّلاَ", "chapter": 1 }, { "id": 398, "number": 398, "text": "أَرَهْطِي سَمَا مَوْلىً وَمَالِي سَمَا لِوىً ... لَعَلِّي سَمَا كُفْؤًا مَعِي نَفْرُ الْعُلَا", "chapter": 1 }, { "id": 399, "number": 399, "text": "عِمَادٌ وَتَحْتَ النَّمْلِ عِنْدِيَ حُسْنُهُ ... إِلَى دُرِّهِ بِالْخُلْفِ وَافَقَ مُوهَلَا", "chapter": 1 }, { "id": 400, "number": 400, "text": "وثِنْتَانِ مَعْ خَمْسِينَ مَعْ كَسْرِ هَمْزَةٍ ... بِفتْحٍ أُولِي حُكْمٍ سِوَى مَا تَعَزَّلَا", "chapter": 1 }, { "id": 401, "number": 401, "text": "بَنَاتِيْ وَأَنْصَارِيْ عِبَادِيْ وَلَعْنَتِيْ ... وَمَا بَعْدَهُ إِنْ شَاءَ بِالْفَتْحِ أُهْمِلَا", "chapter": 1 }, { "id": 402, "number": 402, "text": "وَفِي إِخْوَتِيْ وَرْشٌ يَدِيْ عَنْ أُولِيْ حِمَى ... وَفِي رُسُلِي أَصْلٌ كَسَا وَافِيَ الْمُلَا", "chapter": 1 }, { "id": 403, "number": 403, "text": "وَأُمِّيْ وَأَجْرِيْ سُكِّنَا دِينُ صُحْبَةٍ ... دُعَاءِيْ وَآباءِيْ لِكُوفٍ تَجَمَّلَا", "chapter": 1 }, { "id": 404, "number": 404, "text": "وَحُزْنِيْ وَتَوْفِيقِيْ ظِلاَلٌ وَكُلُّهُمْ ... يُصَدِّقْنِيَ أَنْظِرْنِيْ وَأَخَّرْتَنِي إِلَى", "chapter": 1 }, { "id": 405, "number": 405, "text": "وَذُرِّيَّتِي يَدْعُونَنِيْ وَخِطَابُهُ ... وَعَشْرٌ يَلِيهَا الْهَمْزُ بِالضَّمِّ مُشْكَلَا", "chapter": 1 }, { "id": 406, "number": 406, "text": "فَعَنْ نَافِعٍ فَافْتَحْ وَأَسْكِنْ لِكُلِّهِمْ ... بِعَهْدِي وَآتُونِي لتَفْتَحَ مُقْفَلَا", "chapter": 1 }, { "id": 407, "number": 407, "text": "وَفِي اللاَّمِ لِلتَّعْرِيفِ أَرْبَعُ عَشْرَةٍ ... فَإِسْكَانُهَا فَاشٍ وَعَهْدِيَ فِي عُلَا", "chapter": 1 }, { "id": 408, "number": 408, "text": "وَقُلْ لِعِبَادِيْ كَانَ شَرْعاً وَفِي النِّدَا ... حِمًى شَاعَ آيَاتِيْ كَمَا فَاحَ مَنْزِلَا", "chapter": 1 }, { "id": 409, "number": 409, "text": "فَخَمْسَ عِبَادِي اعْدُدْ وَعَهْدِيْ أَرَادَنِيْ ... وَرَبِّي الَّذِي آتَانِ آياتِيَ الْحُلَا", "chapter": 1 }, { "id": 410, "number": 410, "text": "وَأَهْلَكَنِيْ مِنْهَا وَفِي صَادَ مَسَّنِي ... مَعَ الأَنَبِيَا رَبِّيْ فِي الاَعْرَافِ كَمَّلَا", "chapter": 1 }, { "id": 411, "number": 411, "text": "وَسَبْعٌ بِهَمْزِ الْوَصْلِ فَرْدًا وَفَتْحُهُمْ ... أَخِيْ مَعَ إِنِّيْ حَقَّهُ لَيْتَنِيْ حَلَا", "chapter": 1 }, { "id": 412, "number": 412, "text": "وَنَفْسِيْ سَمَا ذِكْرِيْ سَمَا قَوْمِيَ الرِّضَا ... حَمِيدُ هُدىً بَعْدِيْ سَمَا صَفْوُهُ وِلاَ", "chapter": 1 }, { "id": 413, "number": 413, "text": "وَمَعْ غَيْرِ هَمْزٍ فِي ثَلاَثيِنَ خُلْفُهُمْ ... وَمَحْيايَ جِيْ بالْخُلْفِ وَالْفَتْحُ خُوِّلَا", "chapter": 1 }, { "id": 414, "number": 414, "text": "وَعَمَّ عُلاً وَجْهِيْ وَبَيْتِيْ بِنُوحِ عَنْ ... لِوىً وَسِوَاهُ عُدَّ أَصْلاً لِيُحْفَلَا", "chapter": 1 }, { "id": 415, "number": 415, "text": "وَمَعْ شُرَكَاءِيْ مِنْ وَرَاءِيْ دَوَّنُوا ... وَلِيْ دِينِ عَنْ هَادٍ بِخُلْفٍ لَهُ الْحُلَا", "chapter": 1 }, { "id": 416, "number": 416, "text": "مَمَاتِيْ أَتَى أَرْضِيْ صِرَاطِيْ ابْنُ عَامِرٍ ... وَفِي النَّمْلِ مَالِي دُمْ لِمَنْ رَاقَ نَوْفَلَا", "chapter": 1 }, { "id": 417, "number": 417, "text": "وَلِيْ نَعْجَةٌ مَا كَانَ لِيْ اثْنَيْنِ مَعْ مَعِيْ ... ثَمَانٍ عُلاً وَالظُّلَّةُ الثَّانِ عَنْ جِلَا", "chapter": 1 }, { "id": 418, "number": 418, "text": "وَمَعْ تُوْمِنُوا لِي يُؤْمِنُوا بِيْ جَاوَيَا ... عِبَادِيَ صِفْ وَالْحَذْفُ عَنْ شَاكِرٍ دَلَا", "chapter": 1 }, { "id": 419, "number": 419, "text": "وَفَتْحُ وَلِيْ فِيهَا لِوَرْشٍ وَحَفْصِهِمْ ... وَمَالِيَ فِي يَاسِيْنَ سَكِّنْ فَتَكْمُلَا", "chapter": 1 }, { "id": 420, "number": 420, "text": "وَدُونَكَ يَاءَاتٍ تُسَمَّى زَوَائِدَا ... لأَنْ كُنَّ عَنْ خَطِّ المَصَاحِفِ مَعْزِلَا", "chapter": 1 }, { "id": 421, "number": 421, "text": "وَتَثْبُتُ في الْحَالَيْنَ دُرَّا لَوَامِعاَ ... بِخُلْفٍ وَأُولَى النَّمْلِ حَمْزَةُ كَمَّلَا", "chapter": 1 }, { "id": 422, "number": 422, "text": "وَفِي الْوَصْلِ حَمَّادٌ شَكُورٌ إِمَامُهُ ... وَجُمْلَتُهاَ سِتُونَ وَاثْنَانِ فَاعْقِلَا", "chapter": 1 }, { "id": 423, "number": 423, "text": "فَيَسْرِي إِلى الدَّاعِ الْجَوَارِ المُنَادِ يَهْـ ... ـدِيَنْ يُؤْتِيَنْ مَعْ أَنْ تُعَلِّمَنِي وِلَا", "chapter": 1 }, { "id": 424, "number": 424, "text": "وَأَخَّرْتَنِي الإِسْراَ وَتَتَّبِعَنْ سَما ... وَفي الْكَهْفِ نَبْغِيْ يَأْتِ فِي هُودَ رُفِّلَا", "chapter": 1 }, { "id": 425, "number": 425, "text": "سَماَ وَدُعَاءِيْ فِي جَنَا حُلْوِ هَدْيِهِ ... وَفي اتَّبِعُونِيْ أَهْدِكُمْ حَقُّهُ بَلَا", "chapter": 1 }, { "id": 426, "number": 426, "text": "وَإِنْ تَرَنِيْ عَنْهُمْ تُمِدُّونَنِيْ سَماَ ... فَرِيقاً وَيَدْعُ الدَّاعِ هَاكَ جَناً حَلَا", "chapter": 1 }, { "id": 427, "number": 427, "text": "وَفي الْفَجْرِ بِالْوَادِيْ دَناَ جَرَيَانُهُ ... وَفي الْوَقْفِ بِالْوَجْهَيْنِ وَافَقَ قُنْبُلاَ", "chapter": 1 }, { "id": 428, "number": 428, "text": "وَأَكْرَمَنِيْ مَعْهُ أَهَانَنِ إِذْ هَدَى ... وَحَذْفُهُمَا لِلْمَازِنِيْ عُدَّ أَعْدَلَا", "chapter": 1 }, { "id": 429, "number": 429, "text": "وَفي النَّمْلِ آتانِيْ وَيُفْتَحُ عَنْ أُوِلِيْ ... حِمىً وَخِلافُ الْوَقْفِ بَيْنَ حُلاً عَلَا", "chapter": 1 }, { "id": 430, "number": 430, "text": "وَمَعْ كَالْجَوَابِ الْبَادِ حَقٌّ جَناَهُماَ ... وَفي الْمُهْتَدِ الإِسْرَا وَتَحْتُ أَخُو حُلَا", "chapter": 1 }, { "id": 431, "number": 431, "text": "وَفي اتَّبَعَنْ فِي آلِ عِمْرَانَ عَنْهُمَا ... وَكِيدُونِ فِي الأَعْرَافَ حَجَّ لِيُحْمَلَا", "chapter": 1 }, { "id": 432, "number": 432, "text": "بِخُلْفٍ وَتُؤْتُونِيْ بِيُوسُفَ حَقُّهُ ... وَفي هُودَ تَسْأَلْنِيْ حَوَارِيْهِ جَمَّلَا", "chapter": 1 }, { "id": 433, "number": 433, "text": "وَتُخْزُونِ فِيهاَ حَجَّ أَشْرَكْتُمُونِ قَدْ ... هَدَانِ اتَّقُونِ يَا أُولِي اخْشَوْنِ مَعْ وَلَا", "chapter": 1 }, { "id": 434, "number": 434, "text": "وَعَنْهُ وَخَافُونِ وَمَنْ يَتَّقِيْ زَكاَ ... بِيُوسُفَ وَافَى كَالصَّحِيحَ مُعَلَّلَا", "chapter": 1 }, { "id": 435, "number": 435, "text": "وَفِي المُتَعَالِي دُرُّهُ وَالتَّلاًقِ وَالتْـ ... تَنَادِ دَرَا بَاغِيهِ بِالْخُلْفِ جُهِّلَا", "chapter": 1 }, { "id": 436, "number": 436, "text": "وَمَعْ دَعْوَةَ الدَّاعِ دَعَانِي حَلاَ جَنا ... وَلَيْسَا لِقَالُونٍ عَنِ الْغُرِّ سُبَّلَا", "chapter": 1 }, { "id": 437, "number": 437, "text": "نَذِيرِيْ لِوَرْشٍ ثُمَّ تُرْدِينِ تَرْجُمُو ... نِ فَاعْتَزِلُونِ سِتَّةٌ نُذُرِي جَلَا", "chapter": 1 }, { "id": 438, "number": 438, "text": "وَعِيدِيْ ثَلاَثٌ يُنْقِذُونِ يُكَذِّبُو ... نِ قَالَ نَكِيرِيْ أَرْبَعٌ عَنْهُ وُصِّلَا", "chapter": 1 }, { "id": 439, "number": 439, "text": "فَبَشِّرْ عِبَادِي افْتَحْ وَقِفْ سَاكِناً يَدا ... وَوَاتَّبِعُونِي حَجَّ في الزُّخْرُفِ الْعَلَا", "chapter": 1 }, { "id": 440, "number": 440, "text": "وَفي الْكَهْفِ تَسْأَلْنِيْ عَنِ الْكُلِّ يَاؤُهُ ... عَلَى رَسْمِهِ وَالْحَذْفُ بِالْخُلْفِ مُثِّلَا", "chapter": 1 }, { "id": 441, "number": 441, "text": "وَفي نَرْتَعِيْ خُلْفٌ زَكاَ وَجَمِيعُهُمْ ... بِالإِثْبَاتِ تَحْتَ النَّمْلِ يَهْدِيَنِي تَلَا", "chapter": 1 }, { "id": 442, "number": 442, "text": "فَهذِي أُصُولُ الْقَوْمِ حَالَ اطِّرَادِهاَ ... أَجَابَتْ بِعَوْنِ اللهِ فَانْتَظَمَتْ حُلَا", "chapter": 1 }, { "id": 443, "number": 443, "text": "وَإِنِّي لأَرْجُوهُ لِنَظْمِ حُرُوفِهِمْ ... نَفَائِسَ أَعْلاَقٍ تُنَفِّسُ عُطَّلاَ", "chapter": 1 }, { "id": 444, "number": 444, "text": "سَأَمِضي عَلَى شَرْطِي وَبِاللهِ أَكْتَفِي وَماَ خَابَ ذُو جِدٍّ إِذَا هُوَ حَسْبَلَا", "chapter": 1 }, { "id": 445, "number": 445, "text": "وَمَا يَخْدَعُونَ الْفَتْحُ مِنْ قَبْلِ سَاكِنٍ ... وَبَعْدُ ذَكَا وَالْغَيْرُ كَالْحَرْفِ أَوَّلَا", "chapter": 1 }, { "id": 446, "number": 446, "text": "وخَفَّفَ كُوفٍ يَكْذِبُونَ وَيَاؤُهُ ... بِفَتْحٍ وَلِلْبَاقِينَ ضُمَّ وَثُقِّلَا", "chapter": 1 }, { "id": 447, "number": 447, "text": "وَقِيْلَ وَغِيْضَ ثُمَّ جِيءَ يُشِمُّهَا ... لَدَى كَسْرِهَا ضَمَّا رِجَالٌ لِتَكْمُلَا", "chapter": 1 }, { "id": 448, "number": 448, "text": "وَحِيلَ بِإِشْمَامٍ وَسِيقَ كَمَا رَسَا ... وَسِيءَ وَسِيئَتْ كَانَ رَاوِيهِ أَنْبَلَا", "chapter": 1 }, { "id": 449, "number": 449, "text": "وَهَا هُوَ بَعْدَ الْوَاوِ وَالْفَا وَلاَمِهَا ... وَهَا هِيَ أَسْكِنْ رَاضِياً بَارِداً حَلَا", "chapter": 1 }, { "id": 450, "number": 450, "text": "وَثُمَّ هْوَ رِفْقًا بَانَ وَالضَّمُّ غَيْرُهُمُ ... وَكَسْرٌ وَعَنْ كُلٍّ يُمِلَّ هُوَ انْجَلَا", "chapter": 1 }, { "id": 451, "number": 451, "text": "وَفِي فَأَزَلَّ اللاَّمَ خَفِّفْ لِحَمْزَةٍ ... وَزِدْ أَلِفًا مِنْ قَبْلِهِ فَتُكَمِّلَا", "chapter": 1 }, { "id": 452, "number": 452, "text": "وَآدَمَ فَارْفَعْ نَاصِباً كَلِمَاتِهِ ... بِكَسْرٍ وَلِلْمَكِّيِّ عَكْسٌ تَحَوَّلَا", "chapter": 1 }, { "id": 453, "number": 453, "text": "وَيُقْبَلُ الاُولى أَنَّثُوا دُونَ حَاجِزٍ ... وَعَدْنَا جَمِيعاً دُونَ مَا أَلِفَ حَلَا", "chapter": 1 }, { "id": 454, "number": 454, "text": "وَإِسْكَانُ بَارِئْكُمْ وَيَأْمُرُكُمْ لَهُ ... وَيَأْمُرُهُمْ أَيْضاً وَتَأْمُرُهُمْ تَلَا", "chapter": 1 }, { "id": 455, "number": 455, "text": "وَيَنْصُرُكُمْ أَيْضاً وَيُشْعِرُكُمْ وَكَمْ ... جَلِيلٍ عَنِ الْدُّورِيِّ مُخْتَلِساً جَلَا", "chapter": 1 }, { "id": 456, "number": 456, "text": "وَفِيهَا وَفِي الأَعْرَافِ نَغْفِرْ بِنُونِهِ ... وَلاَ ضَمَّ وَاكْسِرْ فَاءَهُ حِينَ ظَلَّلَا", "chapter": 1 }, { "id": 457, "number": 457, "text": "وَذَكِّرْ هُنَا أَصْلاً وَلِلشَّامِ أَنَّثُوا ... وَعَنْ نَافِعٍ مَعْهُ في الاَعْرَافِ وُصِّلَا", "chapter": 1 }, { "id": 458, "number": 458, "text": "وَجَمْعاً وَفَرْداً فِي النَّبِيءِ وَفي النُّبُو ... ءَةِ الْهَمْزَ كُلٌّ غَيْرَ نَافِعٍ ابْدَلاَ", "chapter": 1 }, { "id": 459, "number": 459, "text": "وَقَالُونُ فِي الأَحْزَابِ فِي لِلنَّبيِّ مَعْ ... بُيُوتَ النَّبيِّ الْيَاءَ شَدَّدَ مُبْدِلَا", "chapter": 1 }, { "id": 460, "number": 460, "text": "وَفي الصَّابِئِينَ الْهَمْزُ وَالصَّابِئُونَ خُذْ ... وَهُزْؤاً وَكُفْؤاً في السَّوَاكِنِ فُصِّلَا", "chapter": 1 }, { "id": 461, "number": 461, "text": "وَضُمَّ لِبَاقِيهِمْ وَحَمْزَةُ وَقْفُهُ ... بِوَاوٍ وَحَفْصٌ وَاقِفاً ثُمَّ مُوصِلَا", "chapter": 1 }, { "id": 462, "number": 462, "text": "وَبِالْغَيْبِ عَمَّا تَعْمَلُونَ هُنَا دَنَا ... وَغَيْبُكَ في الثَّانِي ِإلَى صَفْوَهِ دَلَا", "chapter": 1 }, { "id": 463, "number": 463, "text": "خَطِيئَتُهُ التَّوْحِيدُ عَنْ غَيْرِ نَافِعٍ ... وَلاَ يَعْبُدُونَ الْغَيْبُ شَايَعَ دُخْلُلَا", "chapter": 1 }, { "id": 464, "number": 464, "text": "وَقُلْ حَسَناً شُكْراً وَحُسْناً بِضَمِّهِ ... وَسَاكِنِهِ الْبَاقُونَ وَاحْسِنْ مُقَوِّلَا", "chapter": 1 }, { "id": 465, "number": 465, "text": "وَتَظَّاهَرُونَ الظَّاءُ خُفِّفَ ثَابِتاً ... وَعَنْهُمْ لَدَى التَّحْرِيمِ أَيْضاً تَحَلَّلَا", "chapter": 1 }, { "id": 466, "number": 466, "text": "وَحَمْزَةُ أَسْرَى فِي أُسَارَى وَضَمُّهُمْ ... تُفَادُوهُمُو وَالْمَدُّ إِذْ رَاقَ نُفِّلَا", "chapter": 1 }, { "id": 467, "number": 467, "text": "وَحَيْثُ أَتَاكَ الْقُدْسُ إِسُكَانُ دَالِهِ ... دَوَاءٌ وَلِلْبَاقِينَ بِالضَّمِّ أُرْسِلَا", "chapter": 1 }, { "id": 468, "number": 468, "text": "وَيُنْزِلُ خَفِّفْهُ وَتُنْزِلُ مِثْلُهُ ... وَنُنْزِلُ حَقٌّ وَهْوَ في الْحِجْرِ ثُقِّلَا", "chapter": 1 }, { "id": 469, "number": 469, "text": "وَخُفِّفَ لِلْبَصْرِيْ بِسُبْحَانَ وَالَّذِي ... في الاَنْعَامِ لِلْمَكِّيْ عَلَى أَنْ يُنَزِّلَا", "chapter": 1 }, { "id": 470, "number": 470, "text": "وَمُنْزِلُهَا التَّخْفِيفُ حَقٌّ شِفَاؤُهُ ... وَخُفِّفَ عَنْهُمْ يُنْزِلُ الْغَيْثَ مُسْجَلَا", "chapter": 1 }, { "id": 471, "number": 471, "text": "وَجِبْرِيلَ فَتْحُ الْجِيمِ وَالرَّا وَبَعْدَهَا ... وَعَى هَمْزَةً مَكْسُورَةً صُحْبَةٌ وِلَا", "chapter": 1 }, { "id": 472, "number": 472, "text": "بِحَيْثُ أَتَى وَالْيَاءَ يَحْذِفُ شُعْبَةٌ ... وَمَكِيُّهُمْ في الْجِيمِ بالْفَتْحِ وُكِّلَا", "chapter": 1 }, { "id": 473, "number": 473, "text": "وَدَعْ يَاءَ مِيكَائِيلَ وَالْهَمْزَ قَبْلَهُ ... عَلَى حُجَّةٍ وَالْيَاءُ يُحْذَفُ أَجْمَلَا", "chapter": 1 }, { "id": 474, "number": 474, "text": "وَلكِنْ خَفَيفٌ وَالشَّيَاطِينُ رَفْعُهُ ... كَمَا شَرَطُوا وَالْعَكْسُ نَحْوٌ سَمَا الْعُلاَ", "chapter": 1 }, { "id": 475, "number": 475, "text": "وَنَنْسَخْ بِهِ ضَمٌّ وَكَسْرٌ كَفَى وَنُنْـ ... سِهَا مِثْلُهُ مِنْ غَيْرِ هَمْزٍ ذَكَتْ إِلَى", "chapter": 1 }, { "id": 476, "number": 476, "text": "عَلِيمٌ وَقَالُوا الْوَاوُ اْلاُولَى سُقُوطُهَا ... وَكُنْ فَيَكُونُ النَّصْبُ في الرَّفْعِ كُفِّلَا", "chapter": 1 }, { "id": 477, "number": 477, "text": "وَفي آلِ عِمْرَانٍ في الاُولَى وَمَرْيَمٍ ... وَفِي الطَّوْلِ عَنْهُ وَهْوَ بِاللَّفْظِ أُعْمِلَا", "chapter": 1 }, { "id": 478, "number": 478, "text": "وَفي النَّحْلِ مَعْ يس بِالْعَطْفِ نَصْبُهُ ... كَفَى رَاوِياً وَانْقَادَ مَعْنَاهُ يَعْمَلَا", "chapter": 1 }, { "id": 479, "number": 479, "text": "وَتُسْأَلُ ضَمُّوا التَّاءَ وَالَّلامَ حَرَّكُوا ... بِرَفْعٍ خُلُوداً وَهْوَ مِنْ بَعْدِ نَفْيِ لَا", "chapter": 1 }, { "id": 480, "number": 480, "text": "وَفيهاَ وَفي نَصِّ النِّساَءِ ثَلاَثَةٌ ... أَوَاخِرُ إَبْرَاهَامَ لَاَحَ وَجَمَّلَا", "chapter": 1 }, { "id": 481, "number": 481, "text": "وَمَعْ آخِرِ الأَنْعَامِ حَرْفَا بَرَاءَةٍ ... أَخِيراً وَتَحْتَ الرَّعْدِ حَرْفٌ تَنَزَّلَا", "chapter": 1 }, { "id": 482, "number": 482, "text": "وَفي مَرْيَمٍ وَالنَّحْلِ خَمْسَةُ أَحْرُفٍ ... وَآخِرُ مَا فِي الْعَنْكَبُوتِ مُنَزَّلَا", "chapter": 1 }, { "id": 483, "number": 483, "text": "وَفي النَّجْمَ وَالشُّورَى وَفي الذَّارِيَاتِ وَالْـ ... حَدِيدِ وَيَرْوِي في امْتِحَانِهِ الاَوَّلَا", "chapter": 1 }, { "id": 484, "number": 484, "text": "وَوَجْهَانِ فِيهِ لاِبْنِ ذَكْوَانَ هَهُنَا ... وَوَاتَّخِذُوا بِالْفَتْحِ عَمَّ وَأَوْغَلَا", "chapter": 1 }, { "id": 485, "number": 485, "text": "وَأَرْنَا وَأَرْنِيْ سَاكِنَا الْكَسْرِ دُمْ يَداً ... وَفي فُصِّلَتْ يُرْوِي صَفاً دَرِّهِ كُلَا", "chapter": 1 }, { "id": 486, "number": 486, "text": "وَأَخْفَاهُمَا طَلْقٌ وَخِفُّ ابْنِ عَامِرٍ ... فَأُمْتِعُهُ أَوْصَى بِوَصَّى كَمَا اعْتَلَا", "chapter": 1 }, { "id": 487, "number": 487, "text": "وَفي أَمْ يَقُولُونَ الْخِطَابُ كَمَا عَلاَ ... شَفَا وَرَءُوفٌ قَصْرُ صُحْبَتِهِ حَلَا", "chapter": 1 }, { "id": 488, "number": 488, "text": "وَخَاطَبَ عَمَّا يَعْمَلُونَ كَمَا شَفَا ... وَلاَمُ مُوَلِّيْهَا عَلَى الْفَتْحِ كُمِّلَا", "chapter": 1 }, { "id": 489, "number": 489, "text": "وَفي يَعْمَلُونَ الْغَيْبُ حَلَّ وَسَاكِنٌ ... بِحَرْفَيْهِ يَطَّوَّعْ وَفي الطَّاءِ ثُقِّلاَ", "chapter": 1 }, { "id": 490, "number": 490, "text": "وَفي التَّاءِ يَاءٌ شَاعَ وَالرِّيحَ وَحَّدَا ... وَفي الكَهْفِ مَعْهَا وَالشَّرِيعَةِ وَصَّلَا", "chapter": 1 }, { "id": 491, "number": 491, "text": "وَفي النَّمْلِ وَاْلأَعْرَافِ وَالرُّومِ ثَانِياً ... وَفَاطِرِ دُمْ شُكْراً وَفي الْحِجْرِ فُصِّلَا", "chapter": 1 }, { "id": 492, "number": 492, "text": "وَفي سُورَةِ الشُّورَى وَمِنْ تَحْتِ رَعْدِهِ ... خُصُوصٌ وَفي الْفُرْقَانِ زَاكِيهِ هَلَّلَا", "chapter": 1 }, { "id": 493, "number": 493, "text": "وَأَيُّ خِطَابٍ بَعْدُ عَمَّ وَلَوْ تَرَى ... وَفي إِذْ يَرَوْنَ الْيَاءُ بِالضَّمِّ كُلِّلَا", "chapter": 1 }, { "id": 494, "number": 494, "text": "وَحَيْثُ أَتَي خُطُوَاتٌ الطَّاءُ سَاكِنٌ ... وَقُلْ ضَمُّهُ عَنْ زَاهِدً كَيْفَ رَتَّلَا", "chapter": 1 }, { "id": 495, "number": 495, "text": "وَضَمُّكَ أَولَى السَّاكِنَيْنِ لِثَالِثٍ ... يُضَمُّ لُزُوماً كَسْرُهُ فِي نَدٍ حَلَا", "chapter": 1 }, { "id": 496, "number": 496, "text": "قُلِ ادْعُوا أَوِ انْقُصْ قَالَتِ اخْرُجْ أَنِ اعْبُدُوا ... وَمَحْظُوراً انْظُرْ مَعْ قَدِ اسْتُهْزِئَ اعْتَلَا", "chapter": 1 }, { "id": 497, "number": 497, "text": "سِوى أَوْ وَقُلْ لاِبْنِ الْعَلاَ وَبِكَسْرِهِ ... لِتَنْوِيِنهِ قالَ ابْنُ ذَكْوَانَ مُقْوِلَا", "chapter": 1 }, { "id": 498, "number": 498, "text": "بِخُلْفٍ لَهُ فِي رَحْمَةٍ وَخَبِيثَةٍ ... وَرَفْعُكَ لَيْسَ الْبِرُّ يُنْصَبُ فِي عُلَا", "chapter": 1 }, { "id": 499, "number": 499, "text": "وَلكِنْ خَفِيفٌ وَارْفَعِ اْلبِرَّ عَمَّ فِيـ ... هِماَ وَمُوَصٍّ ثِقْلُهُ صَحَّ شُلْشُلَا", "chapter": 1 }, { "id": 500, "number": 500, "text": "وَفِدْيَةُ نَوِّنْ وَارْفَعِ الْخَفْضَ بَعْدُ فِي ... طَعَامٍ لَدى غُصْنِ دَنَا وَتَذَلَّلَا", "chapter": 1 }, { "id": 501, "number": 501, "text": "مَسَاكِينَ مَجْمُوعاً وَلَيْسَ مُنَوَّناً ... وَيُفْتَحُ مِنْهُ النُّونُ عَمَّ وَأَبْجَلَا", "chapter": 1 }, { "id": 502, "number": 502, "text": "وَنَقْلُ قُرَانٍ وَالْقُرَانِ دَوَاؤُنَا ... وَفِي تُكْمِلُوا قُلْ شُعْبَةُ الْمِيمَ ثَقَّلَا", "chapter": 1 }, { "id": 503, "number": 503, "text": "وَكَسْرُ بُيُوتٍ وَالْبُيُوتَ يُضَمُّ عَنْ ... حِمَى جِلَّةٍ وَجْهاً عَلَى الأَصْلِ أَقْبَلَا", "chapter": 1 }, { "id": 504, "number": 504, "text": "وَلاَ تَقْتُلُوهُمْ بَعْدَهُ يَقْتُلُوكُمُو ... فَإِنْ قَتَلُوكُمْ قَصْرُهاَ شَاعَ وَانْجَلَا", "chapter": 1 }, { "id": 505, "number": 505, "text": "وَبِالرَّفْعِ نَوِّنْهُ فَلاَ رَفَثٌ وَلاَ ... فُسُوقٌ وَلاَ حَقًّا وَزَانَ مُحَمَّلاَ", "chapter": 1 }, { "id": 506, "number": 506, "text": "وَفَتْحُك سِينَ السِّلْمِ أَصْلُ رِضًى دَنَا ... وَحَتَّى يَقُولَ الرَّفْعُ فِي اللاَّمِ أُوِّلَا", "chapter": 1 }, { "id": 507, "number": 507, "text": "وَفي التَّاء فَاضْمُمْ وَافْتَحِ الْجِيمَ تَرْجِعُ الْ ... أُمُورُ سَمَا نَصَّا وَحَيْثُ تَنَزَّلَا", "chapter": 1 }, { "id": 508, "number": 508, "text": "وَإِثْمٌ كَبِيرٌ شَاعَ بِالثَّا مُثَلَّثًا ... وَغَيْرُهُمَا بِالَبَاءِ نُقْطَةٌ اسْفَلَا", "chapter": 1 }, { "id": 509, "number": 509, "text": "قُلِ الْعَفْوَ لِلْبَصْرِيِّ رَفْعٌ وَبَعْدَهُ ... لأَعْنَتْكُمْ بِالْخُلْفِ أَحْمَدُ سَهَّلَا", "chapter": 1 }, { "id": 510, "number": 510, "text": "وَيَطْهُرْنَ فِي الطَّاءِ السُّكُونُ وَهَاؤُهُ ... يُضَمُّ وَخَفَّا إِذْ سَمَا كَيْفَ عُوِّلَا", "chapter": 1 }, { "id": 511, "number": 511, "text": "وَضَمُّ يَخَافاَ فَازَ وَالْكُلُّ أَدْغَمُوا ... تُضَارِرْ وَضَمَّ الرَّاءَ حَقٌّ وَذُو جَلَا", "chapter": 1 }, { "id": 512, "number": 512, "text": "وَقَصْرُ أَتَيْتُمْ مِنْ رِباً وَأَتَيْتمُو ... هُنَا دَارَ وَجْهاً لَيْسَ إِلاَ مُبَجَّلَا", "chapter": 1 }, { "id": 513, "number": 513, "text": "مَعاً قَدْرُ حَرِّكْ مِنْ صَحَابٍ وَحَيْثُ جَا ... يُضَمُّ تَمَسُّوهُنَّ وَامْدُدْهُ شُلْشُلَا", "chapter": 1 }, { "id": 514, "number": 514, "text": "وَصِيَّةً ارْفَعْ صَفْوُ حِرْمِيِّهِ رِضىً ... وَيَبْصُطُ عَنْهُمْ غَيْرَ قُنْبُلِ اعْتَلَا", "chapter": 1 }, { "id": 515, "number": 515, "text": "وَبِالسِّينِ بَاقِيِهِمْ وَفي الْخَلْقِ بَصْطَةً ... وَقُلْ فِيهِماَ الوَجْهَانِ قَوْلاَ مُوَصَّلَا", "chapter": 1 }, { "id": 516, "number": 516, "text": "يُضَاعِفَهُ ارْفَعْ فِي الْحَدِيدِ وَهَهُنَا ... سَماَ شُكْرُهُ وَالْعَيْنُ في الْكُلِّ ثُقِّلَا", "chapter": 1 }, { "id": 517, "number": 517, "text": "كَماَ دَارَ وَاقْصُرْ مَعْ مُضَعَّفَةٍ وَقُلْ ... عَسَيْتُمْ بِكَسْرِ السِّينِ حَيْثُ أَتى انْجَلَا", "chapter": 1 }, { "id": 518, "number": 518, "text": "دِفَاعُ بِهاَ وَالْحَجِّ فَتْحٌ وَسَاكِنٌ ... وَقَصْرٌ خُصُوصًا غَرْفَةً ضَمَّ ذُو وِلاِ", "chapter": 1 }, { "id": 519, "number": 519, "text": "وَلاَ بَيْعَ نَوِّنْهُ وَلاَ خُلَّةٌ وَلاَ ... شَفَاعَةَ وَارْفَعْهُنَّ ذَا أُِسْوَةٍ تَلَا", "chapter": 1 }, { "id": 520, "number": 520, "text": "وَلاَ لَغْوَ لاَ تَأْثِيمَ لاَ بَيْعَ مَعْ وَلاَ ... خِلاَلَ بِإِبْرَاهِيمَ وَالطُّورِ وُصِّلاَ", "chapter": 1 }, { "id": 521, "number": 521, "text": "وَمَدُّ أَناَ في الْوَصْلَ مَعْ ضَمِّ هَمْزَةٍ ... وَفَتْحٍ أَتَى وَالْخُلْفُ في الْكَسْرِ بُجِّلَا", "chapter": 1 }, { "id": 522, "number": 522, "text": "وَنُنْشِزُهَا ذَاكٍ وَبِالرَّاءِ غَيْرُهُمْ ... وَصِلْ يَتَسَنَّهْ دُونَ هَاءٍ شَمَرْدَلَا", "chapter": 1 }, { "id": 523, "number": 523, "text": "وَبِالْوَصْلِ قَالَ اعْلَمْ مَعَ الْجَزْمِ شَافِعٌ ... فَصُرْهُنَّ ضَمُّ الصَّادِ بِالْكَسْرِ فُصِّلَا", "chapter": 1 }, { "id": 524, "number": 524, "text": "وَجُزْءاً وَجُزْءٌ ضَمَّ الاِسْكَانَ صِفْ وَحَيْـ ... ثُماَ أُكْلُهَا ذِكْراً وَفي الْغَيْرِ ذُو حُلَا", "chapter": 1 }, { "id": 525, "number": 525, "text": "وَفي رُبْوَةٍ فِي الْمُؤْمِنِينِ وَههُناَ ... عَلَى فَتْحِ ضَمِّ الراءِ نَبَّهْثُ كُفَّلَا", "chapter": 1 }, { "id": 526, "number": 526, "text": "وَفي الْوَصْلِ لِلْبَزِّيِّ شَدِّدْ تَيَمَّمُوا ... وَتَاءَ تَوَفَّى فِي النِّسَا عَنْهُ مُجْمِلَا", "chapter": 1 }, { "id": 527, "number": 527, "text": "وَفي آلِ عِمْرَانٍ لَهُ لاَ تَفَرَّقُوا ... وَالاَنْعَامُ فِيهاَ فَتَفَرَّقَ مُثِّلَا", "chapter": 1 }, { "id": 528, "number": 528, "text": "وَعِنْدَ الْعُقُودِ التَّاءُ فِي لاَ تَعَاَوَنُوا ... وَيَرْوِى ثَلاَثاَ فِي تَلَقَّفُ مُثَّلَا", "chapter": 1 }, { "id": 529, "number": 529, "text": "تَنَزَّلُ عَنْهُ أَرْبَعٌ وَتَنَاصَرُو ... نَ نَارًا تَلَظَّى إِذْ تَلَقَّوْنَ ثُقِّلَا", "chapter": 1 }, { "id": 530, "number": 530, "text": "تَكَلَّمُ مَعْ حَرْفَيْ تَوَلَّوْا بِهُودِهاَ ... وَفي نُورِهَا وَالإِمْتِحاَنِ وَبَعْدَلَا", "chapter": 1 }, { "id": 531, "number": 531, "text": "فِي الاَنْفَالِ أَيْضًا ثُمَّ فِيهَا تَنَازَعُوا ... تَبَرَّجْنَ في الأَحْزَابِ مَعْ أَنْ تَبَدَّلَا", "chapter": 1 }, { "id": 532, "number": 532, "text": "وَفي التَّوْبَةِ الْغَرَّاءِ قُلْ هَلْ تَرَبَّصُو ... نَ عَنْهُ وَجَمْعُ السَّاكِنَيْنِ هُنَا انْجَلَى", "chapter": 1 }, { "id": 533, "number": 533, "text": "تَمَيَّزُ يَرْوِي ثُمَّ حَرْفَ تَخَيَّرُو ... نَ عَنْهُ تَلَهَّى قَبْلَهُ الْهَاءَ وَصَّلَا", "chapter": 1 }, { "id": 534, "number": 534, "text": "وَفي الْحُجُراتِ التَّاءُ فِي لِتَعَارَفُوا ... وَبَعْدَ وَلاَ حَرْفَانِ مِنْ قَبْلِهِ جَلَا", "chapter": 1 }, { "id": 535, "number": 535, "text": "وَكُنْتُمْ تَمَنَّوْنَ الَّذِي مَعْ تَفَكَّهُو ... نَ عَنْهُ عَلَى وَجْهَيْنِ فَافْهَمْ مُحَصِّلاَ", "chapter": 1 }, { "id": 536, "number": 536, "text": "نِعِمَّا مَعاً في النُّونِ فَتْحٌ كَمَا شَفَا ... وَإِخْفَاءُ كَسْرِ الْعَيْنِ صِيغَ بِهِ حُلَا", "chapter": 1 }, { "id": 537, "number": 537, "text": "وَيَا وَنُكَفِّرْ عَنْ كِرَامٍ وَجَزْمُهُ ... أَتَى شَافِيًا وَالْغَيْرُ بِالرَّفْعِ وُكِّلَا", "chapter": 1 }, { "id": 538, "number": 538, "text": "وَيَحْسَبُ كَسْرُ السِّينِ مُسْتَقبِلاً سَمَا ... رِضَاهُ وَلَمْ يَلْزَمْ قِيَاساً مُؤَصَّلَا", "chapter": 1 }, { "id": 539, "number": 539, "text": "وَقُلْ فَأْذَنُوا بِالْمَدِّ وَاكْسِرْ فَتىً صَفَا ... وَمَيْسَرَةٍ بِالضَّمِّ في السِّينِ أُصِّلَا", "chapter": 1 }, { "id": 540, "number": 540, "text": "وَتَصَّدَّقُوا خِفٌّ نَمَا تُرْجَعُونَ قُلْ ... بِضَمٍّ وَفَتْحٍ عَنْ سِوَى وَلَدِ الْعَلَا", "chapter": 1 }, { "id": 541, "number": 541, "text": "وَفي أَنْ تَضِلَّ الْكَسْرُ فَازَ وَخَفَّفُوا ... فَتُذْكِرَ حَقًّا وَارْفَعِ الرَّا فَتَعْدِلَا", "chapter": 1 }, { "id": 542, "number": 542, "text": "تِجَارَةٌ انْصِبْ رَفْعَهُ فِي النِّسَا ثَوَى ... وَحَاضِرةٌ مَعْهَا هُنَا عَاصِمٌ تَلَا", "chapter": 1 }, { "id": 543, "number": 543, "text": "وَحَقٌّ رِهَانٍ ضَمُّ كَسْرٍ وَفَتْحَةٍ ... وَقَصْرٌ وَيَغْفِرْ مَعْ يُعَذِّبْ سَمَا الْعُلَا", "chapter": 1 }, { "id": 544, "number": 544, "text": "شَذَا الْجَزْمِ وَالتَّوْحِيدُ فِي وَكِتَابِهِ ... شَرِيفٌ وَفي التَّحْرِيمِ جَمْعُ حِمىً عَلَا", "chapter": 1 }, { "id": 545, "number": 545, "text": "وَبَيْتِيْ وَعَهْدِيْ فَاذُكُرُونِي مُضَافُهَا ... وَرَبِّي وَبِيْ مِنِّيْ وَإِنِّيِ مَعاً حُلَا", "chapter": 1 }, { "id": 546, "number": 546, "text": "وَإِضْجَاعُكَ التَّوْرَاةَ مَا رُدَّ حُسْنُهُ ... وَقُلِّلَ فِي جَوْدٍ وَبِالْخُلْفِ بَلَّلَا", "chapter": 1 }, { "id": 547, "number": 547, "text": "وَفي تُغْلَبُونَ الْغَيْبُ مَعْ تُحْشَرُونَ فِي ... رِضًا وَتَرَوْنَ الْغَيْبُ خُصَّ وَخُلِّلَا", "chapter": 1 }, { "id": 548, "number": 548, "text": "وَرِضْوَانٌ اضْمُمْ غَيْرَ ثَانِي الْعُقُودِ كَسْـ ... ـرَهُ صَحَّ إِنَّ الدِّينَ بِالْفَتْحِ رُفِّلَا", "chapter": 1 }, { "id": 549, "number": 549, "text": "وَفي يُقْتلُونَ الثَّانِ قَالَ يُقَاتِلُو ... نَ حَمْزَةُ وَهْوَ الْحَبْرُ سَادَ مُقَتَّلَا", "chapter": 1 }, { "id": 550, "number": 550, "text": "وَفي بَلَدٍ مَيْتٍ مَعَ المَيْتِ خَفَّفُوا ... صَفَا نَفَرًا وَالمَيْتَةُ الْخِفُّ خُوِّلاَ", "chapter": 1 }, { "id": 551, "number": 551, "text": "وَمَيْتًا لَدَى الأَنْعَامِ وَالْحُجُرَاتِ خُذْ ... وَمَا لَمْ يَمُتْ لِلْكلِّ جَاءَ مُثَقَّلَا", "chapter": 1 }, { "id": 552, "number": 552, "text": "وَكَفَّلَهاَ الْكُوفِيْ ثَقِيلاً وَسَكَّنُوا ... وَضَعْتُ وَضَمُّوا سَاكِناً صَحَّ كُفَّلَا", "chapter": 1 }, { "id": 553, "number": 553, "text": "وَقُلْ زَكَرِيَّا دُونَ هَمْزِ جَمِيعِهِ ... صِحَابٌ وَرَفْعٌ غَيْرُ شُعْبَةَ الاُوَّلَا", "chapter": 1 }, { "id": 554, "number": 554, "text": "وَذَكِّرْ فَنَادَاهُ وأَضْجِعْهُ شَاهِداً ... وَمِنْ بَعْدُ أَنَّ اللهَ يُكْسَرُ فِي كِلَا", "chapter": 1 }, { "id": 555, "number": 555, "text": "مَعَ الْكَهْفِ وَالإِسْرَاءِ يَبْشُرُ كَمْ سَمَا ... نَعَمْ ضُمَّ حَرِّكْ وَاكْسِرِ الضَّمَّ أَثْقَلَا", "chapter": 1 }, { "id": 556, "number": 556, "text": "نعَمْ عَمَّ فِي الشُّورَى وَفي التَّوْبَةِ اعْكِسُوا ... لِحَمْزَةَ مَعْ كَافٍ مَعَ الْحِجْرِ أَوَّلَا", "chapter": 1 }, { "id": 557, "number": 557, "text": "نُعَلِّمُهُ بِالْيَاءِ نَصُّ أَئِمَّةٍ ... وَبِالْكَسْرِ أَنِّيْ أَخْلُقُ اعْتَادَ أَفْصَلَا", "chapter": 1 }, { "id": 558, "number": 558, "text": "وَفِي طَائِراً طَيْراً بِهاَ وَعُقُودِهاَ ... خُصُوصاً وَيَاءٌ فِي نُوَفِّيهِمُ عَلَا", "chapter": 1 }, { "id": 559, "number": 559, "text": "وَلاَ أَلِفٌ فِي هَا هَأَنْتُمْ زَكاَ جَناً ... وَسَهِّلْ أَخاَ حَمْدٍ وَكَمْ مُبْدِلٍ جَلَا", "chapter": 1 }, { "id": 560, "number": 560, "text": "وَفي هَائِهِ التَّنْبِيهُ مِنْ ثَابِتٍ هُدىً ... وَإِبْدَالُهُ مِنْ هَمْزَةٍ زَانَ جَمَّلَا", "chapter": 1 }, { "id": 561, "number": 561, "text": "وَيَحْتَمِلُ الْوَجْهَيْنِ عَنْ غَيْرِهِمْ وَكَمْ ... وَجِيهٍ بِهِ الْوَجْهَيْنِ لِلْكُلِّ حَمَّلَا", "chapter": 1 }, { "id": 562, "number": 562, "text": "وَيَقْصُرُ فِي التنْبِيهِ ذُو الْقَصْرِ مَذْهَباً ... وَذُو الْبَدَلِ الْوَجْهاَنِ عَنْهُ مُسَهِّلا", "chapter": 1 }, { "id": 563, "number": 563, "text": "وَضُمَّ وَحَرِّكْ تَعْلَمُونَ الْكِتَابَ مَعْ ... مُشَدَّدَةٍ مِنْ بَعْدُ بِالْكَسْرِ ذُلِّلَا", "chapter": 1 }, { "id": 564, "number": 564, "text": "وَرَفْعُ وَلاَ يَأْمُرْكُمُو رُوحُهُ سَماَ ... وَبِالتَّاءِ آتَيْناَ مَعَ الضَّمِّ خُوِّلَا", "chapter": 1 }, { "id": 565, "number": 565, "text": "وَكَسْرُ لِماَ فِيهِ وَبِالْغَيْبِ تُرْجَعُو ... نَ عَادَ وَفيِ تَبْغُونَ حَاكِيهِ عَوَّلاَ", "chapter": 1 }, { "id": 566, "number": 566, "text": "وَبِالْكَسْرِ حَجُّ الْبَيْتِ عَنْ شَاهِدٍ وَغَيْـ ... ـبُ مَا تَفْعَلُوا لَنْ تُكْفَرُوهُ لَهُمْ تَلَا", "chapter": 1 }, { "id": 567, "number": 567, "text": "يَضِرْكُمْ بِكَسْرِ الضَّادِ مَعْ جَزْمِ رَائِهِ ... سَماَ وَيَضُمُّ الْغَيْرُ وَالرَّاءَ ثَقَّلَا", "chapter": 1 }, { "id": 568, "number": 568, "text": "وَفِيماَ هُناَ قُلْ مُنْزِلِينَ وَمُنْزِلُو ... نَ لِلْيَحْصَبِي فِي الْعَنْكَبُوتِ مُثَقِّلَا", "chapter": 1 }, { "id": 569, "number": 569, "text": "وَحَقُّ نَصِيرٍ كَسْرُ وَاوِ مُسَوِّمِيـ ... ـنَ قُلْ سَارِعُوا لاَ وَاوَ قَبْلُ كَماَ انْجَلَى", "chapter": 1 }, { "id": 570, "number": 570, "text": "وَقَرْحٌ بِضَمِّ الْقَافِ وَالْقَرْحُ صُحْبَةٌ ... وَمَعْ مَدِّ كَائِنْ كَسْرُ هَمْزَتِهِ دَلَا", "chapter": 1 }, { "id": 571, "number": 571, "text": "وَلاَ يَاءَ مَكْسُوراً وَقَاتَلَ بَعْدَهُ ... يُمَدُّ وَفَتْحُ الضَّمِّ وَالْكَسْرِ ذُو وِلَا", "chapter": 1 }, { "id": 572, "number": 572, "text": "وَحُرِّكَ عَيْنُ الرُّعْبِ ضَمَّا كَمَا رَسَا ... وَرُعْباً وَيَغْشَى أَنَّثُوا شَائِعاً تَلَا", "chapter": 1 }, { "id": 573, "number": 573, "text": "وَقُلْ كُلَّهُ للهِ بِالرَّفْعِ حَامِداً ... بِمَا يَعْمَلُونَ الْغَيْبُ شَايَعَ دُخْلُلَا", "chapter": 1 }, { "id": 574, "number": 574, "text": "وَمِتُّمْ وَمِتْناَ مُتَّ فِي ضَمِّ كَسْرِهاَ ... صَفَا نَفَرٌ وِرْداً وَحَفْصٌ هُناَ اجْتَلَا", "chapter": 1 }, { "id": 575, "number": 575, "text": "وَبِالْغَيْبِ عَنْهُ تَجْمَعُونَ وَضُمَّ فِي ... يَغُلَّ وَفَتْحُ الضَّمِّ إِذْ شَاعَ كُفِّلَا", "chapter": 1 }, { "id": 576, "number": 576, "text": "بِمَا قُتِلُوا التَّشْدِيدُ لَبَّى وَبَعْدَهُ ... وَفي الْحَجِّ لِلشَّامِيْ وَالآخِرُ كَمَّلَا", "chapter": 1 }, { "id": 577, "number": 577, "text": "دَرَاكِ وَقَدْ قَالاَ فِي الاَنْعَامِ قَتَّلُوا ... وَبِالْخُلْفِ غَيْباً يَحْسَبَنَّ لَهُ وَلَا", "chapter": 1 }, { "id": 578, "number": 578, "text": "وَأَنَّ اكْسِرُوا رِفْقاً وَيَحْزُنُ غَيْرَ اْلاَنْـ ... ـبِيَاءِ بِضَمٍّ وَاكْسِرِ الضَّمَّ أَحْفَلَا", "chapter": 1 }, { "id": 579, "number": 579, "text": "وَخَاطَبَ حَرْفَا يَحْسِبَنَّ فَخُذْ وَقُلْ ... بِمَا يَعْمَلُونَ الْغَيْبُ حَقٌّ وَذُو مَلَا", "chapter": 1 }, { "id": 580, "number": 580, "text": "يَمِيزَ مَعَ الأنْفَالِ فَاكْسِرْ سُكُونَهُ ... وَشَدِّدْهُ بَعْدَ الْفَتْحِ وَالضَّمِّ شُلْشُلاَ", "chapter": 1 }, { "id": 581, "number": 581, "text": "سَنَكْتُبُ يَاءٌ ضُمَّ مَعْ فَتْحِ ضَمِّهِ ... وَقَتْلَ ارْفَعُوا مَعْ يَا نَقُولُ فَيَكْمُلَا", "chapter": 1 }, { "id": 582, "number": 582, "text": "وَبِالزُّبُرِ الشَّامِيْ كَذَا رَسْمُهُمْ وَبِالْـ ... كِتَابِ هِشَامٌ وَاكْشِفِ الرَّسْمَ مُجْمِلَا", "chapter": 1 }, { "id": 583, "number": 583, "text": "صَفَا حَقُّ غَيْبٍ يَكْتُمُونَ يُبَيِّنُنْنَ ... لاَ تَحْسَبَنَّ الْغَيْبُ كَيْفَ سَمَا اعْتَلَا", "chapter": 1 }, { "id": 584, "number": 584, "text": "وَحَقَّا بِضَمِّ الْبَا فَلاَ يَحْسِبُنَّهُمْ ... وَغَيْبٍ وَفِيهِ الْعَطْفُ أَوْ جَاءَ مُبْدَلَا", "chapter": 1 }, { "id": 585, "number": 585, "text": "هُناَ قَاتَلُوا أَخِّرْ شِفَاءً وَبَعْدُ فِي ... بَرَاءةَ أَخِّرْ يَقْتُلُونَ شَمَرْدَلَا", "chapter": 1 }, { "id": 586, "number": 586, "text": "وَيَاآتُها وَجْهِيْ وَإِنِّيْ كِلاَهُمَا ... وَمِنِّيَ وَاجْعَلْ لِيْ وَأَنْصَاريَ الْمِلَا", "chapter": 1 }, { "id": 587, "number": 587, "text": "وَكُوفِيُّهُمْ تَسَّاءَلُونَ مُخَفَّفًا ... وَحَمْزَةُ وَالأَرْحَامَ بِالْخَفْضِ جَمَّلَا", "chapter": 1 }, { "id": 588, "number": 588, "text": "وَقَصْرُ قِيَامًا عَمَّ يَصْلَوْنَ ضُمَّ كَمْ ... صَفَا نَافِعٌ بِالرَّفْعِ وَاحِدَةً جَلَا", "chapter": 1 }, { "id": 589, "number": 589, "text": "وَيُوصَى بِفَتْحِ الصَّادِ صَحَّ كَمَا دَنَا ... وَوَافَقَ حَفْصٌ فِي الأَخِيرِ مُجَمَّلَا", "chapter": 1 }, { "id": 590, "number": 590, "text": "وَفِي أُمًّ مَعْ فِي أُمِّهَا فَلأُمِّهِ ... لَدَى الْوَصْلِ ضَمُّ الهَمْزِ بِالْكَسْرِ شَمْلَلَا", "chapter": 1 }, { "id": 591, "number": 591, "text": "وَفي أُمَّهَاتِ النَّحْلِ وَالنُّورِ وَالزُّمَرْ ... مَعَ النَّجْمِ شَافٍ وَاكْسِرِ الْمِيمَ فَيْصَلَا", "chapter": 1 }, { "id": 592, "number": 592, "text": "وَنُدْخِلْهُ نُونٌ مَعْ طَلاَقٍ وَفَوْقُ مَعْ ... نُكَفِّرْ نُعَذِّبْ مَعْهُ في الْفَتْحِ إِذْ كَلَا", "chapter": 1 }, { "id": 593, "number": 593, "text": "وَهذَانِ هاتَيْنِ الَّلذَانِ الَّلذَيْنِ قُلْ ... يُشَدَّدُ لِلْمَكِّيْ فَذَانِكَ دُمْ حَلَا", "chapter": 1 }, { "id": 594, "number": 594, "text": "وَضَمَّ هُنَا كَرْهًا وَعِنْدَ بَرَاءةٍ ... شِهَابٌ وَفي الأَحْقَافِ ثُبِّتَ مَعْقِلَا", "chapter": 1 }, { "id": 595, "number": 595, "text": "وَفي الْكُلِّ فَافْتَحْ يَا مُبَيِّنَةٍ دَنَا ... صَحِيحًا وَكَسْرُ الْجَمْعِ كَمْ شَرَفًا عَلاَ", "chapter": 1 }, { "id": 596, "number": 596, "text": "وَفي مُحْصَنَاتٍ فاكْسِرِ الصَّادَ رَاوِيًا ... وَفي المُحْصَنَاتِ اكْسِرْ لَهُ غَيْرَ أَوَّلَا", "chapter": 1 }, { "id": 597, "number": 597, "text": "وَضَمٌّ وَكَسْرٌ فِي أَحَلَّ صِحَابُهُ ... وُجُوهٌ وَفِي أَحْصَنَّ عَنْ نَفَرِ الْعُلَا", "chapter": 1 }, { "id": 598, "number": 598, "text": "مَعَ الْحَجِّ ضَمُّوا مَدْخَلاً خَصَّهُ وَسَلْ ... فَسَلْ حَرَّكُوا بِالنَّقْلِ رَاشِدُهُ دَلَا", "chapter": 1 }, { "id": 599, "number": 599, "text": "وَفي عَاقَدَتْ قَصْرٌ ثَوَى وَمَعَ الْحَدِيـ ... ـدِ فَتْحُ سُكُونِ الْبُخْلِ وَالضَّمِّ شَمْلَلَا", "chapter": 1 }, { "id": 600, "number": 600, "text": "وَفي حَسَنَهْ حِرْمِيُّ رَفْعٍ وَضَمُّهُمْ ... تَسَوَّى نَماَ حَقًّا وَعَمَّ مُثَقَّلَا", "chapter": 1 }, { "id": 601, "number": 601, "text": "وَلاَمَسْتُمُ اقْصُرْ تَحْتَهاَ وَبِهاَ شَفاَ ... وَرَفْعُ قَلِيلٌ مِنْهُمُ النَّصْبَ كُلِّلَا", "chapter": 1 }, { "id": 602, "number": 602, "text": "وَأَنِّثْ يَكُنْ عَنْ دَارِمٍ تُظْلَمُونَ غَيْـ ... ـبُ شُهْدٍ دَنَا إِدْغَامُ بَيَّتَ فِي حُلَا", "chapter": 1 }, { "id": 603, "number": 603, "text": "وَإِشْمَامُ صَادٍ سَاكِنٍ قَبْلَ دَالِهِ ... كَأَصْدَقُ زَايًا شَاعَ وَارْتَاحَ أَشْمُلَا", "chapter": 1 }, { "id": 604, "number": 604, "text": "وَفِيهَا وَتَحْتَ الْفَتْحِ قُلْ فَتَثَبَّتُوا ... مِنَ الثَّبْتِ وَالْغَيْرُ الْبَيَانَ تَبَدَّلَا", "chapter": 1 }, { "id": 605, "number": 605, "text": "وَعَمَّ فَتًى قَصْرُ السَّلاَمَ مُؤَخَّراً ... وَغَيْرَ أُولِيْ بِالرَّفْعِ فِى حَقِّ نَهْشَلَا", "chapter": 1 }, { "id": 606, "number": 606, "text": "وَنُؤْتِيهِ بِالْيَا فِى حِمَاهُ وَضَمُّ يَدْ ... خُلُونَ وَفَتحُ الضَّمِّ حَقٌّ صِرًى حَلَا", "chapter": 1 }, { "id": 607, "number": 607, "text": "وَفي مَرْيَمٍ وَالطَّوْلِ الاَوَّلِ عَنْهُمُ ... وَفِي الثَّانِ دُمْ صَفْوًا وَفِي فَاطِرٍ حَلَا", "chapter": 1 }, { "id": 608, "number": 608, "text": "وَيَصَّالَحَا فَاضْمُمْ وَسَكِّنْ مُخَفِّفًا ... مَعَ الْقَصْرِ وَاكْسِرْ لاَمَهُ ثَابِتًا تَلَا", "chapter": 1 }, { "id": 609, "number": 609, "text": "وَتَلْوُوا بِحَذْفِ الْوَاوِ الاُولى وَلاَمَهُ ... فَضُمَّ سُكُونًا لَسْتَ فِيهِ مُجْهَّلَا", "chapter": 1 }, { "id": 610, "number": 610, "text": "وَنُزِّلَ فَتْحُ الضَّمِّ وَالْكَسْرِ حِصْنُهُ ... وَأُنْزِلَ عَنْهُمْ عَاصِمٌ بَعْدُ نُزِّلاَ", "chapter": 1 }, { "id": 611, "number": 611, "text": "وَيَا سَوْفَ تُؤْتِيِهِمْ عَزيزٌ وَحَمْزَةٌ ... سَيُؤتِيهِمُ فِي الدَّرْكِ كُوفٍ تَحَمَّلَا", "chapter": 1 }, { "id": 612, "number": 612, "text": "بِالاِسْكَانِ تَعْدُوا سَكِّنُوهُ وَخَفِّفُوا ... خُصُوصاً وَأَخْفَى الْعَيْنَ قَالُونُ مُسْهِلَا", "chapter": 1 }, { "id": 613, "number": 613, "text": "وَفي الانْبِياَ ضَمُّ الزَّبُورِ وَههُناَ ... زَبُوراً وَفي الإِسْراَ لِحَمْزَةَ أُسْجِلَا", "chapter": 1 }, { "id": 614, "number": 614, "text": "وَسَكِّنْ مَعًا شَنَآنُ صَحَّا كِلاَهُمَا ... وَفي كَسْرِ أَنَ صَدُّوكمْ حَامِدٌ دَلَا", "chapter": 1 }, { "id": 615, "number": 615, "text": "مَعَ الْقَصْرِ شَدِّدْ يَاءَ قَاسِيَةً شَفَا ... وَأَرْجُلِكُمْ بِالنَّصْبِ عَمَّ رِضاً عَلَا", "chapter": 1 }, { "id": 616, "number": 616, "text": "وَفي رُسُلُنَا مَعْ رُسْلُكُم ثُمَّ رُسْلُهُمْ ... وَفي سُبْلَنَا فِي الضَّمِّ الاِسْكَانُ حُصِّلَا", "chapter": 1 }, { "id": 617, "number": 617, "text": "وَفِي كَلِمَاتِ السُّحْتِ عَمَّ نُهَى فَتًى ... وَكَيْفَ أَتى أُذْنٌ بِهِ نَافِعٌ تَلَا", "chapter": 1 }, { "id": 618, "number": 618, "text": "وَرُحْمًا سِوَى الشَّامِي وَنُذْرًا صِحَابُهُمْ ... حَمَوْهُ وَنُكْرًا شَرْعُ حَقٍ لَهُ عُلَا", "chapter": 1 }, { "id": 619, "number": 619, "text": "وَنُكْرٍ دَنَا وَالْعَيْنُ فَارْفَعْ وَعَطْفَهَا ... رِضىً وَالْجُرُوحَ ارْفَعْ رِضى نَفَرٍ مَلَا", "chapter": 1 }, { "id": 620, "number": 620, "text": "وَحَمْزَةُ وَلْيَحْكُمْ بِكَسْرٍ وَنَصْبِهِ ... يُحَرِّكُهُ يَبْغُونَ خَاطَبَ كُمَّلَا", "chapter": 1 }, { "id": 621, "number": 621, "text": "وَقَبْلَ يَقُولَ الْوَاوُ غصْنٌ وَرَافِعٌ ... سِوَى ابْنِ الْعَلاَ مَنْ يَرْتَدِدْ عَمَّ مُرْسَلَا", "chapter": 1 }, { "id": 622, "number": 622, "text": "وَحُرِّكَ بِالإِدْغَامِ لِلْغَيْرِ دَالُهُ ... وَبِالْخَفْضِ وَالْكُفَّارَ رَاوِيهِ حَصَّلَا", "chapter": 1 }, { "id": 623, "number": 623, "text": "وَبَا عَبَدَ اضْمُمْ واَخْفِضِ التَّا بَعْدُ فُزْ ... رِسَالَتَهُ اجْمَعْ وَاكْسِرِ التَّا كَمَا اعْتَلَا", "chapter": 1 }, { "id": 624, "number": 624, "text": "صَفَا وَتَكُونُ الرَّفْعُ حَجَّ شُهُودُهُ ... وَعَقَّدْتُمُ التَّخْفِيفُ مِنْ صُحْبَةٍ وِلَا", "chapter": 1 }, { "id": 625, "number": 625, "text": "وَفي الْعَيْنِ فَامْدُدْ مُقْسِطاً فَجَزَاءُ نَوْ ... وِّنُوا مِثْلُ مَا فِي خَفْضِهِ الرَّفْعُ ثُمَّلاَ", "chapter": 1 }, { "id": 626, "number": 626, "text": "وَكَفَّارَةٌ نَوِّنْ طَعاَمِ بَرَفْعِ خَفْـ ... ـضِهِ دُمْ غِنىً وَاقْصُرْ قِيَامًا لَهُ مُلَا", "chapter": 1 }, { "id": 627, "number": 627, "text": "وَضَمَّ اسْتُحِقَّ افْتَحْ لَحِفْصٍ وَكَسْرَهُ ... وَفي الأَوْلَياَنِ الأَوَّلِينَ فَطِبْ صِلَا", "chapter": 1 }, { "id": 628, "number": 628, "text": "وَضَمَّ الْغُيُوبِ يَكْسِرَانِ عُيُوناً الْـ ... ـعُيُونِ شُيُوخاً دَانَهُ صُحْبَهٌ مِلَا", "chapter": 1 }, { "id": 629, "number": 629, "text": "جُيُوبِ مُنِيرٌ دُونَ شَكٍّ وَسَاحِرٌ ... بِسِحْرٌ بِهاَ مَعْ هُودَ وَالصَّفِّ شَمْلَلَا", "chapter": 1 }, { "id": 630, "number": 630, "text": "وَخَاطَبَ فِي هَلْ يَسْتَطِيعُ رُوَاتُهُ ... وَرَبُّكَ رَفْعُ الْبَاءِ بِالنَّصْبَ رُتِّلَا", "chapter": 1 }, { "id": 631, "number": 631, "text": "وَيَوْمَ بِرَفْعٍ خُذْ وَإِنِّي ثَلاَثُهاَ ... وَلِي وَيَدِيْ أُمِّيْ مُضَافَاتُهاَ الْعُلَا", "chapter": 1 }, { "id": 632, "number": 632, "text": "وَصُحْبَةُ يُصْرَفْ فَتْحُ ضَمٍّ وَرَاؤُهُ ... بِكَسْرٍ وَذَكِّرْ لَمْ يَكُنْ شَاعَ وَانْجَلَا", "chapter": 1 }, { "id": 633, "number": 633, "text": "وَفِتْنَتُهُمْ بالرَّفْعِ عَنْ دِينِ كَامِلٍ ... وَبَا رَبِّناَ بِالنَّصْبِ شَرَّفَ وُصَّلَا", "chapter": 1 }, { "id": 634, "number": 634, "text": "نُكَذِّبُ نَصْبُ الرَّفْعِ فَازَ عَلِيمُهُ ... وَفِي وَنَكُونَ انْصِبْهُ فِي كَسْبِهِ عُلَا", "chapter": 1 }, { "id": 635, "number": 635, "text": "وَلَلدَّارُ حَذْفُ اللاَّمِ الاُخْرَى ابْنُ عَامِرٍ ... وَالآخِرَةُ المَرْفُوعُ بِالْخِفْضِ وُكِّلَا", "chapter": 1 }, { "id": 636, "number": 636, "text": "وَعَمَّ عُلاً لاَ يَعْقِلُونَ وَتَحْتَهاَ ... خِطاَباً وَقُلْ فَي يُوسُفٍ عَمَّ نَيْطَلَا", "chapter": 1 }, { "id": 637, "number": 637, "text": "وَيَاسِينَ مِنْ أَصْلٍ وَلاَ يُكْذِبُونَكَ الْـ ... خَفِيفُ أَتى رُحْباً وَطَابَ تأَوُّلَا", "chapter": 1 }, { "id": 638, "number": 638, "text": "أَرَيْتَ فِي الاِسْتِفْهَامِ لاَ عَيْنَ رَاجِعٌ ... وَعَنْ نَافِعٍ سَهِّلْ وَكَمْ مُبْدِلٍ جَلَا", "chapter": 1 }, { "id": 639, "number": 639, "text": "إِذَا فُتِحَتْ شَدِّدْ لِشَامٍ وَهَهُنَا ... فَتَحْناَ وَفِي الأَعْرَافِ وَاقْتَرَبَتْ كِلَا", "chapter": 1 }, { "id": 640, "number": 640, "text": "وَبِالْغُدْوَةِ الشَّامِيُّ بِالضَّمِّ ههُناَ ... وَعَنْ أَلِفٍ وَاوٌ وَفِي الْكَهْفِ وَصَّلاَ", "chapter": 1 }, { "id": 641, "number": 641, "text": "وَإِنَّ بِفَتْحٍ عَمَّ نَصْراً وَبَعْدُ كَمْ ... نَماَ يَسْتَبِينَ صُحْبَةٌ ذَكَّرُوا وِلَا", "chapter": 1 }, { "id": 642, "number": 642, "text": "سَبِيلَ بِرَفْعٍ خُذْ وَيَقْضِ بِضَمِّ سَا ... كِنٍ مَعَ ضَمِّ الْكَسْرِ شَدِّدْ وَأَهْمِلَا", "chapter": 1 }, { "id": 643, "number": 643, "text": "نَعَمْ دُونَ إِلْبَاسٍ وَذكَّرَ مُضْجِعاً ... تَوَفَّاهُ وَاسْتَهْوَاهُ حَمْزَةُ مُنْسِلَا", "chapter": 1 }, { "id": 644, "number": 644, "text": "مَعاً خُفيَةً فِي ضَمِّهِ كَسْرُ شُعْبَةٍ ... وَأَنْجَيْتَ لِلْكًوِفِيِّ أَنْجَى تَحَوَّلَا", "chapter": 1 }, { "id": 645, "number": 645, "text": "قُلِ اللهُ يُنْجِيكُمْ يُثَقِّلُ مَعْهُمُ ... هِشَامٌ وَشَامٍ يُنْسِيَنَّكَ ثَقَّلَا", "chapter": 1 }, { "id": 646, "number": 646, "text": "وَحَرْفَيْ رَأَى كُلاًّ أَمِلْ مُزْنَ صُحْبَةٍ ... وَفِي هَمْزِهِ حُسْنٌ وَفِي الرَّاءِ يُجْتَلَا", "chapter": 1 }, { "id": 647, "number": 647, "text": "بِخُلْفٍ وَخُلْفٌ فِيهِماَ مَعَ مُضْمِرٍ ... مُصِيبٌ وَعَنْ عُثْمَانَ في الْكُلِّ قَلِّلَا", "chapter": 1 }, { "id": 648, "number": 648, "text": "وَقَبلَ السُّكُونِ الرَّا أَمِلْ فِي صَفاَ يَدٍ ... بِخُلْفٍ وَقُلْ فِي الْهَمْزِ خُلْفٌ يَقِي صِلَا", "chapter": 1 }, { "id": 649, "number": 649, "text": "وَقِفْ فِيهِ كَالأُولَى وَنَحْوُ رَأَتْ رَأَوْا ... رَأَيْتَ بِفَتْحِ الْكُلِّ وَقْفاً وَمَوْصِلَا", "chapter": 1 }, { "id": 650, "number": 650, "text": "وَخَفِّفَ نُوناً قَبْلَ فِي اللهِ مَنْ لَهُ ... بِخُلْفٍ أَتى وَالْحَذْفُ لَمْ يَكُ أَوَّلَا", "chapter": 1 }, { "id": 651, "number": 651, "text": "وَفي دَرَجَاتِ النُّونُ مَعْ يُوسُفٍ ... ثَوَى وَوَالَّليْسَعَ الْحَرْفاَنِ حَرِّكْ مُثَقِّلَا", "chapter": 1 }, { "id": 652, "number": 652, "text": "وَسَكِّنْ شِفَاءً وَاقْتَدِهْ حَذْفُ هَائِهِ ... شِفَاءً وَبِالتَّحْرِيكِ بِالْكَسْرِ كُفِّلَا", "chapter": 1 }, { "id": 653, "number": 653, "text": "وَمُدَّ بِخُلْفٍ مَاجَ وَالْكُلُّ وَاقِفٌ ... بِإِسْكَانِهِ يَذْكُو عَبِيرًا وَمَنْدَلَا", "chapter": 1 }, { "id": 654, "number": 654, "text": "وَتُبْدُونَهَا تُخْفُونَ مَعْ تَجْعَلُونَهُ ... عَلَى غَيْبِهِ حَقًّا وَيُنْذِرُ صَنْدَلَا", "chapter": 1 }, { "id": 655, "number": 655, "text": "وَبَيْنَكُمُ ارْفَعْ فِي صَفَا نَفَرٍ وَجَا ... عِلُ اقْصُرْ وَفَتْحُ الْكَسْرِ وَالرَّفْعِ ثُمِّلاَ", "chapter": 1 }, { "id": 656, "number": 656, "text": "وَعَنْهُمْ بِنَصْبِ اللَّيْلِ وَاكْسِرْ بِمُسْتَقَرْ ... رٌ الْقَافَ حَقًّا خَرَّقُوا ثِقْلُهُ انْجَلَا", "chapter": 1 }, { "id": 657, "number": 657, "text": "وَضَمَّانِ مَعْ يَاسِينَ فِي ثَمَرٍ شَفَا ... وَدَارَسْتَ حَقٌّ مَدُّهُ وَلَقَدْ حَلَا", "chapter": 1 }, { "id": 658, "number": 658, "text": "وَحَرِّكْ وَسَكنْ كَافِيًا وَاكْسِر اَنَّهَا ... حِمى صَوْبِهِ بِالْخُلْفِ دَرَّ وَأَوْبَلَا", "chapter": 1 }, { "id": 659, "number": 659, "text": "وَخَاطَبَ فِيهَا يُؤْمِنُونَ كَمَا فَشَا ... وَصُحْبَةُ كُفْؤٍ فِي الشَّرِيعَةِ وَصَّلَا", "chapter": 1 }, { "id": 660, "number": 660, "text": "وَكَسْرٌ وَفَتْحٌ ضُمَّ فِي قِبَلاً حَمَى ... ظَهِيرًا وَلِلْكُوفِيِّ فِي الْكَهْفِ وُصِّلَا", "chapter": 1 }, { "id": 661, "number": 661, "text": "وَقُلْ كَلِماَتٌ دُونَ مَا أَلِفٍ ثَوَى ... وَفي يُونُسٍ وَالطَّوْلِ حَامِيهِ ظَلَّلَا", "chapter": 1 }, { "id": 662, "number": 662, "text": "وَشَدَّدَ حَفْصٌ مُنْزَلٌ وَابْنُ عَامِرٍ ... وَحُرِّمَ فَتْحُ الضَّمِّ وَالْكَسْرِ إذْ عَلَا", "chapter": 1 }, { "id": 663, "number": 663, "text": "وَفُصِّلَ إِذْ ثَنَّى يَضِلُّونَ ضَمَّ مَعْ ... يَضِلُّوا الَّذِي فِي يُونُسٍ ثَاِبتًا وَلَا", "chapter": 1 }, { "id": 664, "number": 664, "text": "رِسَالاَتِ فَرْدٌ وَافْتَحُوا دُونَ عِلَّةٍ ... وَضَيْقًا مَعَ الْفُرْقَانِ حَرِّكُ مُثْقِلَا", "chapter": 1 }, { "id": 665, "number": 665, "text": "بِكَسْرٍ سِوَى المَكِّيْ وَرَا حَرَجاً هُنَا ... عَلَى كَسْرِهَا إلْفٌ صَفَا وَتَوَسَّلَا", "chapter": 1 }, { "id": 666, "number": 666, "text": "وَيَصْعَدُ خِفٌّ سَاكِنٌ دُمْ وَمَدُّهُ ... صَحِيحٌ وَخِفُّ الْعَيْنِ دَاوَمَ صَنْدَلَا", "chapter": 1 }, { "id": 667, "number": 667, "text": "ونَحْشُرَ مَعْ ثَانٍ بِيُونُسَ وَهُوَ فِي ... سَبَأَ مَعْ نَقُولُ الْيَا فِي الأَرْبَعِ عُمِّلَا", "chapter": 1 }, { "id": 668, "number": 668, "text": "وَخَاطَبَ شَامٍ يَعْلَمُونَ وَمَنْ تَكُو ... نُ فِيهَا وَتَحْتَ النَّمْلِ ذَكِّرْهُ شُلْشُلَا", "chapter": 1 }, { "id": 669, "number": 669, "text": "مَكَانَاتِ مَدَّ النُّونَ فِي الْكُلِّ شعْبَةٌ ... بِزَعْمِهِمُ الْحَرْفَانِ بِالضَّمِّ رُتِّلَا", "chapter": 1 }, { "id": 670, "number": 670, "text": "وَزَيَّنَ فِي ضَمٍّ وَكَسْرٍ وَرَفْعُ قَتْـ ... لَ أَوْلاَدِهِمْ بِالنَّصْبِ شَامِيُّهُمْ تَلَا", "chapter": 1 }, { "id": 671, "number": 671, "text": "وَيُخْفَضُ عَنْهُ الرَّفْعُ فِي شُرَكَاؤُهُمْ ... وَفِي مُصْحَفِ الشَّامِينَ بِالْيَاءِ مُثِّلاَ", "chapter": 1 }, { "id": 672, "number": 672, "text": "وَمَفْعُولُهُ بَيْنَ المُضَافَيْنِ فَاصِلٌ ... وَلَمْ يُلْفَ غَيْرُ الظُرْفِ فِي الشِّعْرِ فَيْصَلَا", "chapter": 1 }, { "id": 673, "number": 673, "text": "كَلِلَّهِ دَرُّ الْيَوْمَ مَنْ لاَمَهَا فَلاَ ... تَلُمْ مِنْ مُلِيمِي النَّحْوِ إِلاَّ مُجَهِّلَا", "chapter": 1 }, { "id": 674, "number": 674, "text": "وَمَعْ رَسْمِهِ زَجَّ الْقَلُوصَ أَبِي مَزَا ... دَةَ اْلأَخْفَشُ النَّحْوِيُّ أَنْشَدَ مُجْمِلَا", "chapter": 1 }, { "id": 675, "number": 675, "text": "وَإِنْ يَكُن اَنِّثْ كُفْؤَ صِدْقٍ وَمَيْتَةٌ ... دَنَا كَافِيًا وَافْتَحْ حِصَادِ كَذِي حُلَا", "chapter": 1 }, { "id": 676, "number": 676, "text": "نَمَا وَسُكُونُ المَعْزِ حِصْنٌ وَأَنَّثُوا ... يَكُونُ كَمَا فِي دِينِهِمْ مَيْتَةٌ كَلَا", "chapter": 1 }, { "id": 677, "number": 677, "text": "وَتَذَّكَّرُونَ الْكُلُّ خَفَّ عَلَى شَذَا ... وَأَنَّ اكْسِرُوا شَرْعًا وَبِالْخِفِّ كُمِّلَا", "chapter": 1 }, { "id": 678, "number": 678, "text": "وَيَأْتِيَهُمْ شَافٍ مَعَ النَّحْلِ فَارَقُوا ... مَعَ الرُّومِ مَدَّاهُ خَفِيفًا وَعَدَّلَا", "chapter": 1 }, { "id": 679, "number": 679, "text": "وَكَسْرٌ وَفَتْحٌ خَفَّ فِي قِيَماً ذَكَا ... وَيَاآتُهَا وَجْهِيْ مَمَاتِيَ مُقْبِلَا", "chapter": 1 }, { "id": 680, "number": 680, "text": "وَرَبِّيْ صِرَاطِيْ ثُمَّ إِنِّيْ ثَلاَثَةٌ ... وَمَحْيَايَ وَالإِسْكَانُ صَحَّ تَحَمُّلَا", "chapter": 1 }, { "id": 681, "number": 681, "text": "وَتَذَّكَّرُونَ الْغَيْبَ زِدْ قَبْلَ تَائِهِ ... كَرِيماً وَخِفُّ الذَّالِ كَمْ شَرَفاً عَلَا", "chapter": 1 }, { "id": 682, "number": 682, "text": "مَعَ الزُّخْرُفِ اعْكِسْ تُخْرَجُونَ بِفَتْحَةٍ ... وَضَمٍّ وَأُولَى الرُّومِ شَافِيهِ مُثِّلَا", "chapter": 1 }, { "id": 683, "number": 683, "text": "بِخُلْفٍ مَضَى فِي الرُّومِ لاَ يَخْرُجُونَ فِي ... رِضًا وَلِباَسُ الرَّفْعُ فِي حَقِّ نَهْشَلَا", "chapter": 1 }, { "id": 684, "number": 684, "text": "وَخَالِصَةٌ أَصْلٌ وَلاَ يَعْلَمُونَ قُلْ ... لِشُعْبَةَ فِي الثَّانِي وَيُفْتَحُ شَمْلَلَا", "chapter": 1 }, { "id": 685, "number": 685, "text": "وَخَفِّفْ شَفَا حُكْماً وَماَ الْوَاوَ دَعْ كَفَى ... وَحَيْثُ نَعَمْ بِالْكَسْرِ فِي الْعَيْنِ رُتِّلَا", "chapter": 1 }, { "id": 686, "number": 686, "text": "وَأَنْ لَعْنَةُ التَّخْفِيفُ وَالرَّفْعُ نَصُّهُ ... سَماَ مَا خَلاَ الْبَزِّي وَفِي النُّورِ أُوصِلاَ", "chapter": 1 }, { "id": 687, "number": 687, "text": "وَيُغْشِي بِهاَ وَالرَّعْدِ ثَقَّلَ صُحْبَةٌ ... وَوَالشَّمْسُ مَعْ عَطْفِ الثَّلاَثَةِ كَمَّلَا", "chapter": 1 }, { "id": 688, "number": 688, "text": "وَفِي النَّحْلِ مَعْهُ فِي الأَخِيرَيْنِ حَفْصُهُمْ ... وَنُشْراً سُكُونُ الضَّمِّ فِي الْكُلِّ ذَلِّلَا", "chapter": 1 }, { "id": 689, "number": 689, "text": "وَفي النُّونِ فَتْحُ الضمِّ شَافٍ وَعَاصِمٌ ... رَوى نُونَهُ بِالْبَاءِ نُقْطَةٌ اَسْفَلَا", "chapter": 1 }, { "id": 690, "number": 690, "text": "وَرَا مِنْ إِلهٍ غَيْرُهُ خَفْضُ رَفْعِهِ ... بِكُلٍّ رَسَا وَالْخِفُّ أُبْلِغُكُمْ حَلَا", "chapter": 1 }, { "id": 691, "number": 691, "text": "مَعَ اَحْقَافِهاَ وَالْوَاوَ زِدْ بَعْدَ مُفْسِدِ ... يْنَ كُفْؤاً وَبِالإِخْبَارِ إِنَّكُمُو عَلَا", "chapter": 1 }, { "id": 692, "number": 692, "text": "أَلاَ وعَلَى الحِرْمِيُّ إنَّ لَنَا هُنَا ... وَأَوْ أَمِنَ الإسْكَانَ حَرْمِيُّهُ كَلا", "chapter": 1 }, { "id": 693, "number": 693, "text": "عَلَيَّ عَلَى خَصُّوا وَفي سَاحِرٍ بِهَا ... وَيُونُسَ سَحَّارٍ شَفَا وتَسَلْسَلَا", "chapter": 1 }, { "id": 694, "number": 694, "text": "وَفِي الْكُلِّ تَلْقَفْ خِفُّ حَفْصٍ وَضُمَّ فِي ... سَنَقْتُلُ وَاكْسِرْ ضَمَّهُ مُتَثَقِّلَا", "chapter": 1 }, { "id": 695, "number": 695, "text": "وَحَرِّكْ ذَكَا حُسْنٍ وَفِي يَقْتُلُونَ خُذْ ... مَعاً يَعْرِشُونَ الْكَسْرُ ضُمَّ كَذِي صِلَا", "chapter": 1 }, { "id": 696, "number": 696, "text": "وَفي يَعْكُفُونَ الضَّمُّ يُكْسَرُ شَافِياً ... وَأَنْجَى بِحَذْفِ الْيَاءِ وَالنُّونِ كُفِّلَا", "chapter": 1 }, { "id": 697, "number": 697, "text": "وَدَكَّاءَ لاَ تَنْوِينَ وَامْدُدْهُ هَامِزاً ... شَفَا وَعَنِ الْكُوفِيِّ فِي الْكَهْفِ وُصِّلَا", "chapter": 1 }, { "id": 698, "number": 698, "text": "وَجَمْعُ رسَالاَتِي حَمَتْهُ ذُكُورُهُ ... وَفي الرُّشْدِ حَرِّكْ وَافْتَحِ الضَّمَّ شُلْشُلَا", "chapter": 1 }, { "id": 699, "number": 699, "text": "وَفِي الْكَهْفِ حُسْنَاهُ وَضَمُّ حُلِيِّهِمْ ... بَكَسْرٍ شَفَا وَافٍ وَالاِتْبَاعُ ذُو حُلَا", "chapter": 1 }, { "id": 700, "number": 700, "text": "وَخَاطَبَ يَرْحَمْنَا وَيَغْفِرْ لَنَا شَذاً ... وَبَا رَبَّنَا رَفْعٌ لِغَيْرِهِمَا انْجَلَا", "chapter": 1 }, { "id": 701, "number": 701, "text": "وَمِيمَ ابْنَ أُمَّ اكْسِرْ مَعًا كُفْؤَ صُحْبَةٍ ... وَآصَارَهُمْ بِالْجَمْعِ وَالْمَدِّ كُلِّلاَ", "chapter": 1 }, { "id": 702, "number": 702, "text": "خَطِيئَاتُكُمْ وَحِّدَهُ عَنْهُ وَرَفْعُهُ ... كَمَا أَلَّفُوا وَالْغَيْرُ بِالْكَسْرِ عَدَّلَا", "chapter": 1 }, { "id": 703, "number": 703, "text": "وَلكِنْ خَطَايَا حَجَّ فِيهَا وَنُوحِهَا ... وَمَعْذِرَةً رَفْعٌ سِوَى حَفْصِهمْ تَلَا", "chapter": 1 }, { "id": 704, "number": 704, "text": "وَبِيسٍ بِيَاءٍ أَمَّ وَالْهَمْزُ كَهْفُهُ ... وَمِثْلَ رَئِيْسٍ غَيْرُ هذَيْنِ عَوَّلَا", "chapter": 1 }, { "id": 705, "number": 705, "text": "وَبَيْئَسٍ اَسْكِنْ بَيْنَ فَتْحَيْنِ صَادِقاً ... بِخُلْفٍ وَخَفِّفْ يُمْسِكُونَ صَفَا وِلَا", "chapter": 1 }, { "id": 706, "number": 706, "text": "وَيَقْصُرُ ذُرِّيَّاتِ مَعْ فَتْحِ تَائِهِ ... وَفِي الطُّورِ فِي الثَّانِي ظَهِيرٌ تَحَمَّلَا", "chapter": 1 }, { "id": 707, "number": 707, "text": "وَيَاسِينَ دُمْ غُصْناً وَيُكْسَرُ رَفْعُ أَوْ ... وَّلِ الطُّورِ لِلْبَصْرِيْ وَبِالْمَدِّ كَمْ حَلَا", "chapter": 1 }, { "id": 708, "number": 708, "text": "يَقُولُوا مَعاً غَيْبٌ حَمِيدٌ وَحُيْثُ يُلْـ ... ـحِدُونَ بِفَتْحِ الضمِّ وَالْكَسْرِ فُصِّلَا", "chapter": 1 }, { "id": 709, "number": 709, "text": "وَفِي النَّحْلِ وَالاَهُ الْكِسَائِيْ وَجَزْمُهُمْ ... يَذَرْهُمْ شَفَا وَالْيَاءُ غُصْنٌ تَهَدَّلَا", "chapter": 1 }, { "id": 710, "number": 710, "text": "وَحَرِّكْ وَضُمَّ الْكَسْرَ وَامْدُدْهُ هَامِزاً ... وَلاَ نُونَ شِرْكاً عَنْ شَذَا نَفَرٍ مِلَا", "chapter": 1 }, { "id": 711, "number": 711, "text": "وَلاَ يَتْبَعُوكُمْ خَفَّ مَعْ فَتْحِ بَائِهِ ... وَيَتْبَعُهُمْ فِي الظُّلَّةِ احْتَلَّ وَاعْتَلَا", "chapter": 1 }, { "id": 712, "number": 712, "text": "وَقُلْ طَائِفٌ طَيْفٌ رِضىً حَقُّهُ وَيَا ... يَمُدُّونَ فَاضْمُمْ وَاكْسِرِ الضَّمَّ أَعْدَلَا", "chapter": 1 }, { "id": 713, "number": 713, "text": "وَرَبِّيْ مَعِيْ بَعْدِيْ وَإِنِّيْ كِلاَهُمَا ... عَذَابِيَ آيَاتِيْ مُضَافَاتُهَا الْعُلَا", "chapter": 1 }, { "id": 714, "number": 714, "text": "وَفِي مُرْدِفِينَ الدَّالَ يَفْتَحُ نَافِعٌ ... وَعَنْ قُنْبُلٍ يُرْوَى وَلَيْسَ مُعَوَّلَا", "chapter": 1 }, { "id": 715, "number": 715, "text": "وَيُغْشِي سَمَا خِفًّا وَفِي ضَمِّهِ افْتَحُوا ... وَفِي الْكَسْرِ حَقًّا وَالنُّعَاسَ ارْفَعُوا وِلَا", "chapter": 1 }, { "id": 716, "number": 716, "text": "وَتَخْفِيفُهُمْ فِي الأَوَّلَينَ هُنَا وَلـ ... ـكِنِ اللهُ وَارْفَعْ هَاءهُ شَاعَ كُفَّلاَ", "chapter": 1 }, { "id": 717, "number": 717, "text": "وَمُوهِنُ بِالتَّخْفِيفِ ذَاعَ وَفِيهِ لَمْ ... يُنَوَّنْ لِحَفْصٍ كَيْدَ بِالْخَفْضِ عَوَّلَا", "chapter": 1 }, { "id": 718, "number": 718, "text": "وَبَعْدُ وَإِنَّ الْفَتْحُ عَمَّ عُلاً وَفِيـ ... ـهِمَا الْعُدْوَةِ اكْسِرْ حَقًّا الضَّمَّ وَاعْدِلَا", "chapter": 1 }, { "id": 719, "number": 719, "text": "وَمَنْ حَيِيَ اكْسِرْ مُظْهِرًا إِذْ صَفَا هُدًى ... وَإِذْ يَتَوَفَّى أَنِّثُوهُ لَهُ مُلَا", "chapter": 1 }, { "id": 720, "number": 720, "text": "وَبِالْغَيْبِ فِيهَا تَحْسَبَنَّ كَمَا فَشَا ... عَمِيمًا وَقُلْ فِي النُّورِ فَاشِيهِ كَحَّلَا", "chapter": 1 }, { "id": 721, "number": 721, "text": "وَإِنَّهُمُ افْتَحْ كَاِفيًا وَاكْسِرُوا لِشُعْـ ... ـبَةَ السَّلْمَ وَاكْسِرْ فِي الْقِتاَلِ فَطِبْ صِلَا", "chapter": 1 }, { "id": 722, "number": 722, "text": "وَثَانِيْ يَكُنْ غُصْنٌ وَثَالِثُهاَ ثَوَى ... وَضُعْفاً بِفَتْحِ الضَّمِّ فَاشِيهِ نُفِّلا", "chapter": 1 }, { "id": 723, "number": 723, "text": "وِفي الرُّومِ صِفْ عَنْ خُلْفِ فَصْلٍ وَأَنِّثَ اَنْ ... يَكُونَ مَعَ الأَسْرَى الأُسَارَى حُلاً حَلَا", "chapter": 1 }, { "id": 724, "number": 724, "text": "وَلاَيَتِهِمْ بِالْكَسْرِ فُزْ وَبِكَهْفِهِ ... شَفَا وَمَعًا إِنِّيْ بِيَاءَيْنِ أَقْبَلا", "chapter": 1 }, { "id": 725, "number": 725, "text": "وَيُكْسَرُ لاَ أَيْمَانَ عِنْدَ ابْنِ عَامِرٍ ... وَوَحَّدَ حَقٌّ مَسْجِدَ اللهِ الاَوَّلَا", "chapter": 1 }, { "id": 726, "number": 726, "text": "عَشِيرَاتُكُمْ بِالجمْعِ صِدْقٌ وَنَوِّنُوا ... عُزَيْرٌ رِضَى نَصٍّ وَبِالْكَسْرِ وُكِّلَا", "chapter": 1 }, { "id": 727, "number": 727, "text": "يُضَاهُونَ ضَمَّ الْهَاءِ يَكْسِرُ عَاصِمٌ ... وَزِدْ هَمْزَةً مَضْمُومَةً عَنهُ وَاعْقِلَا", "chapter": 1 }, { "id": 728, "number": 728, "text": "يَضِلُّ بِضَمِّ الْيَاءِ مَعْ فَتْحِ ضَادِهِ ... صِحَابٌ وَلَمْ يَخْشَوْا هُنَاكَ مُضَلِّلَا", "chapter": 1 }, { "id": 729, "number": 729, "text": "وَأَنْ تُقْبَلَ التَّذْكِيرُ شَاعَ وِصَالُهُ ... وَرَحْمَةٌ الْمَرْفُوعُ بِالْخَفْضِ فَاقْبَلَا", "chapter": 1 }, { "id": 730, "number": 730, "text": "وَيُعْفَ بِنُونٍ دُونَ ضَمٍّ وَفَاؤُهُ ... يُضَمُّ تُعَذَّبْ تَاهُ بِالنُّونِ وُصِّلَا", "chapter": 1 }, { "id": 731, "number": 731, "text": "وَفِي ذَالِهِ كَسْرٌ وَطَائِفَةٌ بِنَصْـ ... ـبِ مَرْفُوعِهِ عَنْ عَاصِمٍ كُلُّهُ اعْتَلاَ", "chapter": 1 }, { "id": 732, "number": 732, "text": "وَحَقٌّ بِضَمِّ السَّوْءِ مَعْ ثَانِ فَتْحِهَا ... وَتَحْرِيكُ وَرْشٍ قُرْبَةٌ ضَمُّهُ جَلَا", "chapter": 1 }, { "id": 733, "number": 733, "text": "وَمِنْ تَحْتِهَا المَكِّيْ ... يَجُرُّ وَزَادَ مِنْ صَلاَتَكَ وَحِّدْ وَافْتَحِ التَّا شَذًّا عَلَا", "chapter": 1 }, { "id": 734, "number": 734, "text": "وَوَحِّدْ لَهُمْ في هُودَ تُرْجِئُ هَمْزُهُ ... صَفَا نَفَرٍ مَعْ مُرْجَئُونَ وَقَدْ حَلَا", "chapter": 1 }, { "id": 735, "number": 735, "text": "وَعَمَّ بِلاَ وَاوِ الَّذِينَ وَضُمَّ فِي ... مَنَ اَسَّسَ مَعْ كَسْرٍ وَبُنْيَانُهُ وِلَا", "chapter": 1 }, { "id": 736, "number": 736, "text": "وَجُرْفٍ سكونُ الضَّمِّ فِي صَفْوِ كَامِلٍ ... تُقَطَّعُ فَتْحُ الضَّمِّ فِي كَامِلٍ عَلَا", "chapter": 1 }, { "id": 737, "number": 737, "text": "يَزِيغُ عَلَى فَصْلٍ يَرَوْنَ مُخَاطَبٌ ... فَشَا وَمَعِيْ فِيهَا بِيَاءَيْنِ حُمِّلَا", "chapter": 1 }, { "id": 738, "number": 738, "text": "وَإِضْجَاعُ رَا كُلِّ الْفَوَاتِحِ ذِكْرُهُ ... حِمًى غَيْرَ حَفْصٍ طَا وَيَا صُحْبَةٌ وَلَا", "chapter": 1 }, { "id": 739, "number": 739, "text": "وَكَمْ صُحْبَةٍ يَا كَافَ والْخُلْفُ يَاسِرٌ ... وَهَا صِفْ رِضًى حُلْوًا وَتَحْتَ جَنًى حَلَا", "chapter": 1 }, { "id": 740, "number": 740, "text": "شَفَا صادِقًا حَم مُخْتَارُ صُحْبَةٍ ... وَبَصْرٍ وَهُمْ أَدْرَى وَبِالْخُلْفِ مُثِّلَا", "chapter": 1 }, { "id": 741, "number": 741, "text": "وَذو الرَّا لِوَرْشٍ بَيْنَ بَيْنَ وَناَفِعٌ ... لَدى مَرْيَمٍ هَايَا وَحَا جِيدُهُ حَلَا", "chapter": 1 }, { "id": 742, "number": 742, "text": "نُفَصِّلُ يَا حَقٍّ عُلاً سَاحِرٌ ظُبَى ... وَحَيْثُ ضِيَاءً وَافَقَ الْهَمْزُ قُنْبُلَا", "chapter": 1 }, { "id": 743, "number": 743, "text": "وَفِي قُضِيَ الْفَتْحانِ مَعْ أَلِفٍ هُنَا ... وَقُلْ أَجَلُ المَرْفُوعُ بِالنَّصْبِ كُمِّلَا", "chapter": 1 }, { "id": 744, "number": 744, "text": "وَقَصْرُ وَلاَ هَادٍ بِخُلْفٍ زَكَا وَفي الْـ ... ـقِيَامَةِ لاَ الأُوْلَى وَبِالْحَالِ أُوِّلَا", "chapter": 1 }, { "id": 745, "number": 745, "text": "وَخَاطَبَ عَمَّا يُشْرِكُونَ هُناَ شَذاً ... وَفي الرُّومِ وَالْحَرْفَيْنِ في النَّحْلِ أَوَّلَا", "chapter": 1 }, { "id": 746, "number": 746, "text": "يُسَيِّرُكُمْ قُلْ فِيهِ يَنْشُرُكُمْ كَفَى ... مَتَاعَ سِوَى حَفْصٍ بِرَفْعٍ تَحَمَّلاَ", "chapter": 1 }, { "id": 747, "number": 747, "text": "وَإِسْكَانُ قِطْعاً دُونَ رَيْبٍ وُرُودُهُ ... وفِي بَاءِ تَبْلُو التَّاءُ شَاعَ تَنَزُّلَا", "chapter": 1 }, { "id": 748, "number": 748, "text": "وَيَا لاَ يَهَدِّي اكْسِرْ صَفِيًّا وَهَاهُ نَلْ ... وَأَخْفَى بَنُو حَمْدٍ وَخُفِّفَ شُلْشُلَا", "chapter": 1 }, { "id": 749, "number": 749, "text": "وَلكِنْ خَفِيفٌ وَارْفَعِ النَّاسَ عَنْهُمَا ... وَخَاطَبَ فِيهَا يَجْمَعُونَ لَهُ مُلَا", "chapter": 1 }, { "id": 750, "number": 750, "text": "وَيَعْزُبُ كَسْرُ الضَّمِّ مَعْ سَبَأٍ رَسَا ... وَأَصْغَرَ فَارْفَعْهُ وَأَكْبَرَ فَيْصَلَا", "chapter": 1 }, { "id": 751, "number": 751, "text": "مَعَ الْمدِّ قَطْعُ السِّحْرِ حُكْمٌ تَبَوَّءا ... بِيَا وَقْفُ حَفْصٍ لَمْ يَصِحَّ فَيُحْمَلَا", "chapter": 1 }, { "id": 752, "number": 752, "text": "وَتَتَّبِعَانِ النُّونُ خَفَّ مَدًا وَمَـ ... ـاجَ بِالْفَتْحِ وَالإِسْكَانِ قَبْلُ مُثَقَّلَا", "chapter": 1 }, { "id": 753, "number": 753, "text": "وَفِي أَنَّهُ اكْسِرْ شَافِياً وَبِنُونِهِ ... وَنَجْعَلُ صِفْوَ الْخِفُّ نُنْجِ رِضىً عَلَا", "chapter": 1 }, { "id": 754, "number": 754, "text": "وَذَاكَ هُوَ الثَّانِي وَنَفْسِيَ يَاؤُهَا ... وَرَبِّيَ مَعْ أَجْرِيَ وَإِنِّي وَلِي حُلَا", "chapter": 1 }, { "id": 755, "number": 755, "text": "وَإِنِّي لَكُمْ بِالْفَتْحِ حَقُّ رُوَاتِهِ ... وَبَادِيءَ بَعْدَ الدَّالِ بِالْهَمْزِ حُلِّلَا", "chapter": 1 }, { "id": 756, "number": 756, "text": "وَمِنْ كُلِّ نَوِّنْ مَعْ قَدْ اَفْلَحَ عَالِماً ... فَعُمِّيَتِ اضْمُمْهُ وَثَقِّلْ شَذًّا عَلَا", "chapter": 1 }, { "id": 757, "number": 757, "text": "وَفِي ضَمِّ مَجْرَاهَا سِوَاهُمْ وَفَتْحُ يَا ... بُنَيِّ هُنَا نَصٌّ وَفِي الْكُلِّ عُوِّلَا", "chapter": 1 }, { "id": 758, "number": 758, "text": "وَآخِرُ لُقْماَنٍ يُوَالِيهِ أَحْمَدُ ... وَسَكَّنَهُ زَاكٍ وَشَيْخُهُ الاَوَّلَا", "chapter": 1 }, { "id": 759, "number": 759, "text": "وَفِي عَمَلٌ فَتْحٌ وَرَفْعٌ وَنَوِّنُوا ... وَغَيْرَ ارْفَعُوا إِلاَّ الْكِسَائِيَّ ذَا الْمَلَا", "chapter": 1 }, { "id": 760, "number": 760, "text": "وَتَسْئَلْنِ خِفُّ الْكَهْفِ ظِلٌّ حِمًي وَهَا ... هُنَا غُصْنُهُ وَافْتَحْ هُنَا نُونَهُ دَلَا", "chapter": 1 }, { "id": 761, "number": 761, "text": "وَيَوْمَئِذٍ مَعْ سَالَ فَافْتَحْ أَتَى رِضاً ... وَفِي النَّمْلِ حِصْنٌ قَبْلَهُ النُّونُ ثُمِّلاَ", "chapter": 1 }, { "id": 762, "number": 762, "text": "ثَمُودَ مَعَ الْفُرْقَانِ وَالْعَنْكَبُوتِ لَمْ ... يُنَوَّنْ عَلَى فَصْلٍ وَفِي النَّجْمِ فُصِّلَا", "chapter": 1 }, { "id": 763, "number": 763, "text": "نَمَا لِثَمُودٍ نَوِّنُوا وَاخْفِضُوا رِضاً ... وَيَعْقُوبُ نَصْبُ الرَّفْعِ عَنْ فَاضِلٍ كَلَا", "chapter": 1 }, { "id": 764, "number": 764, "text": "هُناَ قَالَ سِلْمٌ كسْرُهُ وَسُكُونُهُ ... وَقَصْرٌ وَفَوْقَ الطُّورِ شَاعَ تَنَزُّلاَش", "chapter": 1 }, { "id": 765, "number": 765, "text": "وَفَاسْرِ أَنِ اسْرِ الْوَصْلُ أَصْلٌ دَناَ وَهَا ... هُنَا حَقٌّ إِلاَّ امْرَاتَكَ ارْفَعْ وَأَبْدِلَا", "chapter": 1 }, { "id": 766, "number": 766, "text": "وَفِي سَعِدُوا فَاضْمُمْ صِحَابًا وَسَلْ بِهِ ... وَخِفُّ وَإِنْ كُلاًّ إِلَى صَفْوِهِ دَلَا", "chapter": 1 }, { "id": 767, "number": 767, "text": "وَفِيها وَفِي يس وَالطَّارِقِ العُلا ... يُشَدِّدُ لَمَّا كَامِلٌ نَصَّ فَاعْتَلا", "chapter": 1 }, { "id": 768, "number": 768, "text": "وَفي زُخْرُفٍ فِي نَصِّ لُسْنٍ بِخُلْفِهِ ... وَيَرْجِعُ فِيه الضَّمُّ وَالْفَتْحُ إِذْ عَلَا", "chapter": 1 }, { "id": 769, "number": 769, "text": "وَخَاطَبَ عَمَّا يَعْمَلُونَ هُنَا وَآ ... خِرَ النَّمْلِ عِلْماً عَمَّ وَارْتَادَ مَنْزِلَا", "chapter": 1 }, { "id": 770, "number": 770, "text": "وَيَاآتُهاَ عَنِّيْ وَإِنِّيْ ثَمَانِيا ... وَضَيْفِيْ وَلكِنِّيْ وَنُصْحِيَ فَاقْبَلَا", "chapter": 1 }, { "id": 771, "number": 771, "text": "شِقَاقِيْ وَتَوْفِيقِيْ وَرَهْطِيَ عُدَّهاَ ... وَمَعْ فَطَرَنْ أَجْرِيْ مَعاً تُحْصِ مُكْمِلَا", "chapter": 1 }, { "id": 772, "number": 772, "text": "وَيَا أَبَتِ افْتَحْ حَيْثُ جَا لاِبْنِ عَامِرٍ ... وَوُحِّدَ لِلْمَكِّيِّ آيَاتٌ الْوِلَا", "chapter": 1 }, { "id": 773, "number": 773, "text": "غَيَابَاتِ فِي الْحَرْفَيْنِ بِالْجَمْعِ نَافِعٌ ... وَتَأْمَنُنَا لِلْكُلِّ يُخْفَى مُفَصَّلَا", "chapter": 1 }, { "id": 774, "number": 774, "text": "وَأَدْغَمَ مَعْ إِشْمَامِهِ البَعْضُ عَنْهُمُ ... وَنَرْتَعْ وَنَلْعَبْ يَاءُ حِصْنٍ تَطَوَّلَا", "chapter": 1 }, { "id": 775, "number": 775, "text": "وَيَرْتَعْ سُكُونُ الْكَسْرِ فِي الْعَيْنِ ذُو حِماً ... وَبُشْرَايَ حَذْفُ الْيَاءِ ثَبْتٌ وَمُيِّلَا", "chapter": 1 }, { "id": 776, "number": 776, "text": "شِفَاءٌ وَقَلِّلْ جِهْبِذَا وَكِلاَهُمَا ... عَنِ ابْنِ الْعَلاَ وَالْفَتْحُ عَنْهُ تَفَضَّلاَ", "chapter": 1 }, { "id": 777, "number": 777, "text": "وَهَيْتَ بِكَسْرٍ أَصْلُ كُفْؤٍ وَهَمْزُهُ ... لِسَانٌ وَضَمُّ التَّا لِوَى خُلْفُهُ دَلَا", "chapter": 1 }, { "id": 778, "number": 778, "text": "وَفِي كَافَ فَتْحُ الَّلامِ فِي مُخْلِصاً ثَوَى ... وَفِي الْمُخْلِصِينَ الْكُلُّ حِصْنٌ تَجَمَّلَا", "chapter": 1 }, { "id": 779, "number": 779, "text": "معاً وَصْلُ حَاشَا حَجَّ دَأْباً لِحَفْصِهِمْ ... فَحَرِّكْ وَخَاطِبْ يَعْصِرُونَ شَمَرْدَلَا", "chapter": 1 }, { "id": 780, "number": 780, "text": "وَنَكْتَلْ بِيَا شَافٍ وَحَيْثُ يَشَاءُ نُو ... نُ دَارٍ وَحِفْظاً حَافِظاً شَاعَ عُقَّلَا", "chapter": 1 }, { "id": 781, "number": 781, "text": "وَفِتْيَتِهِ فِتْيَانِهِ عَنْ شَذاً وَرُدْ ... بِالاِخْبَارِ فِي قَالُوا أَئِنَّكَ دَغْفَلَا", "chapter": 1 }, { "id": 782, "number": 782, "text": "وَيَيْأَسْ مَعًا وَاسْتَيْأَسَ اسْتَيْأَسُوا وَتَيْـ ... ـأَسُوا اقْلِبْ عَنِ الْبَزِّيْ بِخُلْفٍ وَأَبْدِلَا", "chapter": 1 }, { "id": 783, "number": 783, "text": "وَيُوحَى إِلَيْهِمْ كَسْرُ حَاءِ جَمِيعِهَا ... وَنُونٌ عُلاً يُوحَى إِلَيْهِ شَذاً عَلَا", "chapter": 1 }, { "id": 784, "number": 784, "text": "وَثَانِيَ نُنْجِ احْذِفْ وَشَدِّدْ وَحَرِّكَنْ ... كَذَا نَلْ وَخَفِّفْ كُذِّبُوا ثَابِتاً تَلَا", "chapter": 1 }, { "id": 785, "number": 785, "text": "وَأَنِّيْ وَإِنَّىْ الْخَمْسُ رَبِّيْ بِأَرْبَعٍ ... أَرَانِيْ مَعاً نَفْسِيْ لَيُحْزِنُنِي حُلَا", "chapter": 1 }, { "id": 786, "number": 786, "text": "وَفِي إِخْوَتِيْ حُزْنِيْ سَبِيلِيْ بِيْ وَلِيْ ... لَعَلِّيَ آبَاءِيْ أَبِيْ فَاخْشَ مَوْحَلَا", "chapter": 1 }, { "id": 787, "number": 787, "text": "وَزَرْعٌ نَخِيلٌ غَيْرُ صِنْوَانٍ اَوَّلاَ ... لَدَى خَفْضِهَا رَفْعٌ عَلَى حَقُّهُ طُلَا", "chapter": 1 }, { "id": 788, "number": 788, "text": "وَذَكَّرَ تُسْقَى عَاصِمٌ وَابْنُ عَامِرٍ ... وَقُلْ بَعْدَهُ بِالْيَا يُفَضِّلُ شُلْشُلَا", "chapter": 1 }, { "id": 789, "number": 789, "text": "وَمَا كُرِّرَ اسْتِفْهَامُهُ نَحْوُ آئِذَا ... أَئِنَّا فَذُو اسْتِفْهَامٍ الْكُلُّ أَوَّلَا", "chapter": 1 }, { "id": 790, "number": 790, "text": "سِوَى نَافِعٍ فِي النَّمْلِ وَالشَّامِ مُخْبِرٌ ... سِوَى النَّازِعَاتِ مَعْ إِذَا وَقَعَتْ وِلَا", "chapter": 1 }, { "id": 791, "number": 791, "text": "وَدُونَ عِنَادٍ عَمَّ فِي الْعَنْكَبُوتِ مُخْـ ... ـبِرًا وَهْوَ في الثَّانِي أَتَى رَاشِدًا وَلاَ", "chapter": 1 }, { "id": 792, "number": 792, "text": "سِوَى الْعَنْكَبُوتِ وَهْوَ فِي الْنَّمْلِ كُنْ رِضَا ... وَزَادَاهُ نُونًا إِنَّنَا عَنْهُمَا اعْتَلَا", "chapter": 1 }, { "id": 793, "number": 793, "text": "وَعَمَّ رِضاً فِي النَّازِعَاتِ وَهُمْ عَلَى ... أُصُولِهِمْ وَامْدُدْ لِوَى حَافِظٍ بَلَا", "chapter": 1 }, { "id": 794, "number": 794, "text": "وَهَادٍ وَوَالٍ قِفْ وَوَاقٍ بِيَائِهِ ... وَبَاقٍ دَنَا هَلْ يَسْتَوِي صُحْبَةٌ تَلَا", "chapter": 1 }, { "id": 795, "number": 795, "text": "وَبَعْدُ صِحَابٌ يُوْقِدُونَ وَضَمُّهُمْ ... وَصُدُّوا ثَوَى مَعْ صُدَّ فِي الطَّوْلِ وَانْجَلَا", "chapter": 1 }, { "id": 796, "number": 796, "text": "وَيُثْبِتُ فِي تَخْفِيفِهِ حَقُّ نَاصِرٍ ... وَفِي الْكَافِرُ الْكُفَّارُ بِالْجَمْعِ ذُلِّلَا", "chapter": 1 }, { "id": 797, "number": 797, "text": "وَفِي الْخَفْضِ فِي اللهِ الَّذِي الرَّفْعُ عَمَّ خَا ... لِقُ امْدُدْهُ وَاكْسِرْ وَارْفَعِ الْقَافَ شُلْشُلَا", "chapter": 1 }, { "id": 798, "number": 798, "text": "وَفِي النُّورِ وَاخْفِضْ كُلَّ فِيهَا وَالأرْضَ هَا ... هُنَا مُصْرِخِيَّ اكْسِرْ لِحَمْزَةَ مُجْمِلَا", "chapter": 1 }, { "id": 799, "number": 799, "text": "كَهَا وَصْلٍ أَوْ لِلسَّاكِنَينِ وَقُطْرُبٌ ... حَكَاهَا مَعَ الْفَرَّاءِ مَعْ وَلَدِ الْعَلَا", "chapter": 1 }, { "id": 800, "number": 800, "text": "وَضُمَّ كِفَا حِصْنٍ يَضِلُّوا يَضِلَّ عَنْ ... وَأَفْئِدَةً بِالْيَا بِخُلْفٍ لَهُ وَلَا", "chapter": 1 }, { "id": 801, "number": 801, "text": "وَفِي لِتَزُولَ الْفَتْحُ وَارْفَعْهُ رَاشِداً ... وَمَا كَانَ لِيْ إِنِّيْ عِبَادِيَ خُذْ مُلا", "chapter": 1 }, { "id": 802, "number": 802, "text": "وَرُبَّ خَفِيفٌ إِذْ نَمَا سُكِّرَتْ دَنَا ... تَنَزَّلُ ضَمُّ التَّا لِشُعْبَةَ مُثِّلَا", "chapter": 1 }, { "id": 803, "number": 803, "text": "وَبِالنُّونِ فِيهاَ وَاكْسِرِ الزَّايَ وَانُصِبِ الْ ... مَلائِكَةَ المَرْفُوعَ عَنْ شَائِدٍ عُلَا", "chapter": 1 }, { "id": 804, "number": 804, "text": "وَثُقِّلَ لِلْمَكِّيِّ نُونُ تُبَشِّرُو ... نَ وَاكْسِرْهُ حِرْمِيَّا وَمَا الْحَذُفُ أَوَّلَا", "chapter": 1 }, { "id": 805, "number": 805, "text": "وَيَقْنَطُ مَعْهُ يَقْنَطُونَ وَتَقْنَطُوا ... وَهُنَّ بِكَسْرِ النُّونِ رَافَقْنَ حُمَّلاَ", "chapter": 1 }, { "id": 806, "number": 806, "text": "وَمُنْجُوهُمُ خِفٌّ وَفِي الْعَنْكَبُوتِ نُنْـ ... ـجِيَنَّ شَفَا مُنْجُوكَ صُحْبَتُهُ دَلَا", "chapter": 1 }, { "id": 807, "number": 807, "text": "قَدَرْنَا بِهَا وَالنَّمْلِ صِفْ وَعِبَادِ مَعْ ... بَناتِيْ وَأَنيْ ثُمَّ إِنِّيْ فَاعْقِلَا", "chapter": 1 }, { "id": 808, "number": 808, "text": "وَيُنْبِتُ نُونٌ صَحَّ يَدْعُونَ عَاصِمٌ ... وَفِي شُرَكَايَ الْخُلْفُ فِي الْهَمْزِ هَلْهَلَا", "chapter": 1 }, { "id": 809, "number": 809, "text": "وَمِنْ قَبْلِ فِيهِمْ يَكْسِرُ النُّونَ نَافِع ... مَعًا يَتَوَفَّاهُمْ لِحَمْزَةَ وُصِّلَا", "chapter": 1 }, { "id": 810, "number": 810, "text": "سَمَا كامِلاً يَهْدِيْ بِضَمٍّ وَفَتْحَةٍ ... وَخَاطِبْ تَرَوْا شَرْعاً وَاْلآخِرُ فِي كِلَا", "chapter": 1 }, { "id": 811, "number": 811, "text": "وَرَا مُفْرِطُونَ اكْسِرْ أَضاَ يَتَفَيَّؤاُ الْـ ... ـمُؤَنَّثُ لِلْبَصْرِيِّ قَبْلُ تُقُبِّلَا", "chapter": 1 }, { "id": 812, "number": 812, "text": "وَحَقُّ صِحَابٍ ضَمَّ نَسْقِيكُمُو مَعََا ... لِشُعْبَةَ خَاطِبْ يَجْحَدُونَ مُعَلَّلَا", "chapter": 1 }, { "id": 813, "number": 813, "text": "وَظَعْنِكُمُ إِسْكَانُهُ ذَائِعٌ وَنَجْـ ... ـزِيَنَّ الَّذِينَ النُّونُ دَاعِيهِ نُوِّلَا", "chapter": 1 }, { "id": 814, "number": 814, "text": "مَلَكْتُ وَعَنْهُ نَصَّ الاخْفَشُ يَاءَهُ ... وَعَنْهُ رَوَى النَّقَّاشُ نُوناً مُوَهَّلَا", "chapter": 1 }, { "id": 815, "number": 815, "text": "سِوَى الشَّامِ ضُمُّوا وَاكْسِرُوا فَتَنُوا لَهُمْ ... وَيُكْسَرُ فِي ضَيْقٍ مَعَ النَّمْلِ دُخْلُلَا", "chapter": 1 }, { "id": 816, "number": 816, "text": "وَيَتَّخِذُوا غَيْبٌ حَلاَ لِيَسُوءَ نُو ... نُ رَاوٍ وَضَمُّ الْهَمْزِ وَالْمَدِّ عُدِّلَا", "chapter": 1 }, { "id": 817, "number": 817, "text": "سَمَا وَيُلَقَّاهُ يُضَمُّ مُشَدَّداً ... كَفَى يَبْلُغَنَّ امْدُدْهُ وَاكْسِرْ شَمَرْدَلَا", "chapter": 1 }, { "id": 818, "number": 818, "text": "وَعَنْ كُلِّهِمْ شَدِّدْ وَفَا أُفِّ كُلِّهاَ ... بِفَتْحٍ دَناَ كُفْؤًا وَنَوِّنْ عَلَى اعْتِلَا", "chapter": 1 }, { "id": 819, "number": 819, "text": "وَبِالْفَتْحِ وَالتَّحْرِيكِ خِطْأً مُصَوَّب ... وَحَرَّكَهُ الْمَكِّيْ وَمَدَّ وَجَمَّلَا", "chapter": 1 }, { "id": 820, "number": 820, "text": "وَخَاطَبَ فِي يُسْرِفْ شُهُودٌ وَضَمُّنَا ... بِحَرْفَيْهِ بِالْقِسْطَاسِ كَسْرُ شَذٍ عَلاَ", "chapter": 1 }, { "id": 821, "number": 821, "text": "وَسَيِّئَةً فِي هَمْزِهِ اضْمُمْ وَهَائِهِ ... وَذَكِّرْ وَلاَ تَنْوِينَ ذِكْراً مُكَمَّلَا", "chapter": 1 }, { "id": 822, "number": 822, "text": "وَخَفِّفْ مَعَ الْفُرْقَانِ وَاضُمُمْ لِيَذْكُرُوا ... شِفَاءً وَفِي الْفُرْقَانِ يَذْكُرُ فُصِّلَا", "chapter": 1 }, { "id": 823, "number": 823, "text": "وَفِي مَرْيَمٍ بِالْعَكْسِ حَقٌّ شِفَاؤُهُ ... يَقُولُونَ عَنْ دَارٍ وَفِي الثَّانِ نُزِّلَا", "chapter": 1 }, { "id": 824, "number": 824, "text": "سَمَا كِفْلُهُ أَنِّثْ يُسَبِّحُ عَنْ حِميً ... شَفَا وَاكْسِرُوا إِسْكَانَ رَجْلِكَ عُمَّلَا", "chapter": 1 }, { "id": 825, "number": 825, "text": "وَيَخْسِفَ حق نُونُهُ وَيُعِيدَكُمْ ... فَيُغْرِقَكُمْ وَاثْنَانِ يُرْسِلَ يُرْسِلَا", "chapter": 1 }, { "id": 826, "number": 826, "text": "خِلاَفَكَ فَافْتَحْ مَعْ سُكُونٍ وَقَصْرِهِ ... سَمَا صِفْ نَآى أَخِّرْ مَعاً هَمْزَهُ مَلَا", "chapter": 1 }, { "id": 827, "number": 827, "text": "تُفَجِّرَ فِي اْلأُولَى كَتَقْتُلَ ثَابِتٌ ... وَعَمَّ نَدىً كِسْفاً بِتَحْرِيكِهِ وَلَا", "chapter": 1 }, { "id": 828, "number": 828, "text": "وَفي سَبَأٍ حَفْصٌ مَعَ الشُّعَرَاءِ قُلْ ... وَفِي الرُّومِ سَكِّنْ لَيْسَ بِالْخُلْفِ مُشْكِلَا", "chapter": 1 }, { "id": 829, "number": 829, "text": "وَقُلْ قَالَ اْلاُولَى كَيْفَ دَارَ وَضُمَّ تَا ... عَلِمْتَ رِضىً وَالْيَاءُ فِي رَبِّيَ انْجَلَا", "chapter": 1 }, { "id": 830, "number": 830, "text": "وَسَكْتَةُ حَفْصٍ دُونَ قَطْعٍ لَطِيفَةٌ ... عَلَى أَلِفِ التَّنْوِينِ فِي عِوَجاً بَلَا", "chapter": 1 }, { "id": 831, "number": 831, "text": "وَفِي نُونِ مَنْ رَاق وَمَرْقَدِناَ وَلاَ ... مِ بَلْ رَانَ وَالْبَاقُونَ لاَ سَكْتَ مُوصَلَا", "chapter": 1 }, { "id": 832, "number": 832, "text": "وَمِنْ لَدْنِهِ في الضَّمِّ أَسْكِنْ مُشِمَّهُ ... وَمِنْ بَعْدِهِ كَسْرَانِ عَنْ شُعْبَةَ اعْتَلَا", "chapter": 1 }, { "id": 833, "number": 833, "text": "وَضُمَّ وَسَكِّنْ ثُمَّ ضُمَّ لِغَيْرِهِ ... وَكُلُّهُمُ فِي الْهَا عَلَى أَصْلِهِ تَلَا", "chapter": 1 }, { "id": 834, "number": 834, "text": "وَقُلْ مِرْفَقاً فَتْحٌ مَعَ الْكَسْرِ عَمَّهُ ... وَتَزْوَرُّ لِلشَّامِيْ كَتَحْمَرُّ وُصِّلَا", "chapter": 1 }, { "id": 835, "number": 835, "text": "وَتَزَّاوَرُ التَّخْفِيفُ فِي الزَّايِ ثَاِبتٌ ... وَحِرْمِيُّهُمْ مُلِّئْتَ فِي اللاَّمِ ثَقَّلاَ", "chapter": 1 }, { "id": 836, "number": 836, "text": "بَوَرْقِكُمُ الإِسْكَانُ فِي صَفْوِ حُلْوِهِ ... وَفِيهِ عَنِ الْبَاقِينَ كَسْرٌ تَأَصَّلا", "chapter": 1 }, { "id": 837, "number": 837, "text": "وَحَذْفُكَ لِلتَّنْوِينِ مِنْ مِائَةٍ شَفَا ... وَتُشْرِكْ خِطَابٌ وَهْوَ بِالْجَزْمِ كُمِّلَا", "chapter": 1 }, { "id": 838, "number": 838, "text": "وَفِي ثُمُرٍ ضَمَّيْهِ يَفْتَحُ عَاصِمٌ ... بِحَرْفَيْهِ وَاْلإِسْكَانُ فِي الْمِيمِ حُصِّلاِ", "chapter": 1 }, { "id": 839, "number": 839, "text": "وَدَعْ مِيمَ خَيْراً مِنْهُمَا حُكْمُ ثَابِتٍ ... وَفِي الْوَصْلِ لكِنَّا فَمُدَّ لَهُ مُلَا", "chapter": 1 }, { "id": 840, "number": 840, "text": "وَذَكِّرْ تَكُنْ شَافٍ وَفِي الْحَقِّ جَرُّهُ ... عَلَى رَفْعِهِ حَبْرٌ سَعِيدٌ تَأَوَّلَا", "chapter": 1 }, { "id": 841, "number": 841, "text": "وَعُقُباً سُكُونُ الضَّمِّ نَصُّ فَتىً وَيَا ... نُسَيِّرُ وَالَى فَتْحَهَا نَفَرٌ مَلَا", "chapter": 1 }, { "id": 842, "number": 842, "text": "وَفِي النُّونِ أَنِّثْ وَالْجِبَالَ بِرَفْعِهِمْ ... وَيَوْمُ يقُولُ النُّونُ حَمْزَةُ فَضَّلَا", "chapter": 1 }, { "id": 843, "number": 843, "text": "لِمَهْلَكِهِمْ ضَمُّوا وَمَهْلَكَ أَهْلِهِ ... سِوى عَاصِمٍ وَالْكَسْرُ فِي الْلاَّمِ عُوِّلَا", "chapter": 1 }, { "id": 844, "number": 844, "text": "وَهَا كَسْرِ أَنْسَانِيهِ ضُمَّ لِحَفْصِهِمْ ... وَمَعْهُ عَلَيْهِ اللهَ فِي الْفَتْحِ وَصَّلَا", "chapter": 1 }, { "id": 845, "number": 845, "text": "لِتُغْرِقَ فَتْحُ الضَّمِّ وَالْكَسْرِ غَيْبَةً ... وَقُلْ أَهْلَهَا بِالرَّفْعِ رَاوِيهِ فَصَّلَا", "chapter": 1 }, { "id": 846, "number": 846, "text": "وَمُدَّ وَخَفِّفْ يَاءَ زَاكِيَةً سَمَا ... وَنُونَ لَدُنِّي خَفَّ صَاحِبُهُ إِلَى", "chapter": 1 }, { "id": 847, "number": 847, "text": "وَسَكِّنْ وَأَشْمِمْ ضَمَّةَ الدَّالِ صَادِقاً ... تَخِذْتَ فَخَفِّفْ وَاكْسِرِ الْخَاءَ دُمْ حُلَا", "chapter": 1 }, { "id": 848, "number": 848, "text": "وَمِنْ بَعْدُ بِالتَّخْفِيفِ يُبْدِلُ هَاهُنَا ... وَفَوْقَ وَتَحْتَ الْمُلْكِ كَافِيهِ ظَلَّلَا", "chapter": 1 }, { "id": 849, "number": 849, "text": "فَأَتْبَعَ خَفِّفْ فِي الثَّلاَثَةِ ذَاكِراً ... وَحَامِيَةٍ بِالْمَدِّ صُحْبَتُهُ كَلَا", "chapter": 1 }, { "id": 850, "number": 850, "text": "وَفِي الْهَمْزِ يَاءٌ عَنْهُمْ وَصِحَابُهُمْ ... جَزَاءُ فَنَوِّنْ وَانْصِبِ الرَّفْعَ وَأَقْبَلَا", "chapter": 1 }, { "id": 851, "number": 851, "text": "عَلَى حَقٍّ السُّدَّيْنِ سُدًّا صِحَابُ حَقْ ... قٍ الضَّمُّ مَفْتُوحٌ وَيَاسِينَ شِدْ عُلاَ", "chapter": 1 }, { "id": 852, "number": 852, "text": "وَيَأْجُوجَ مَأْجُوجَ اهْمِزِ الْكُلَّ نَاصِراً ... وَفِي يَفْقَهُونَ الضَّمُّ وَالْكَسْرُ شُكِّلَا", "chapter": 1 }, { "id": 853, "number": 853, "text": "وَحَرِّكْ بِهاَ وَالمُؤْمِنينَ وَمُدَّهُ ... خَرَاجاً شَفَا وَاعْكِسْ فَخَرْجُ لَهُ مُلَا", "chapter": 1 }, { "id": 854, "number": 854, "text": "وَمَكَّنَنِي أَظْهِرْ دَلِيلاً وَسَكَّنُوا ... مَعَ الضَّمِّ فِي الصُّدْفَيْنِ عَنْ شُعْبَةَ الْمَلَا", "chapter": 1 }, { "id": 855, "number": 855, "text": "كَمَا حَقُّهُ ضَمَّاهُ وَاهْمِزْ مُسَكِّناً ... لَدَى رَدْماً ائْتُونِي وَقَبْلُ اكْسِرِ الْوِلَا", "chapter": 1 }, { "id": 856, "number": 856, "text": "لِشُعْبَةَ وَالثَّانِي فَشَا صِفْ بِخُلْفِهِ ... وَلاَ كَسْرَ وَابْدَأْ فِيهِمَا الْيَاءَ مُبْدِلا", "chapter": 1 }, { "id": 857, "number": 857, "text": "وَزِدْ قَبْلُ هَمْزَ الْوَصْلِ وَالْغَيْرُ فِيهِمَا ... بِقَطْعِهِمَا وَالْمَدِّ بَدْءاً وَمَوْصِلَا", "chapter": 1 }, { "id": 858, "number": 858, "text": "وَطَاءَ فَمَا اسْطَاعُوا لِحَمْزَةَ شَدِّدُوا ... وَأَنْ تَنْفَدَ التَّذْكِيرُ شَافٍ تَأَوَّلَا", "chapter": 1 }, { "id": 859, "number": 859, "text": "ثَلاَثٌ مَعيْ دُونِيْ وَرَبِّيْ بِأَرْبَعٍ ... وَمَا قَبْلَ إِنْ شَاءَ الْمُضَافَاتُ تُجْتَلَا", "chapter": 1 }, { "id": 860, "number": 860, "text": "وَحَرْفَا يَرِثْ بِالْجَزْمِ حُلْوُ رِضىً وَقُلْ ... خَلَقْتُ خَلَقْنَا شَاعَ وَجْهاً مُجَمَّلَا", "chapter": 1 }, { "id": 861, "number": 861, "text": "وَضَمُّ بُكِيًّا كَسْرُهُ عَنْهُمَا وَقُلْ ... عُتيًّا صُلِيًّا مَعْ جُثِيًّا شَذًا عَلَا", "chapter": 1 }, { "id": 862, "number": 862, "text": "وَهَمْزُ أَهَبْ بِالْيَا جَرَى حُلْوُ بَحْرِهِ ... بِخُلْفٍ وَنِسْيًا فَتْحُهُ فَائِزٌ عُلَا", "chapter": 1 }, { "id": 863, "number": 863, "text": "وَمَنْ تَحْتَهَا اكْسِرْ وَاخْفِضِ الدَّهْرَ عَنْ شَذاً ... وَخَفَّ تَسَاقَطْ فَاصِلاً فَتُحُمِّلَا", "chapter": 1 }, { "id": 864, "number": 864, "text": "وَبِالضَّمِّ وَالتَّخْفِيفِ وَالْكَسْرِ حَفْصُهُمْ ... وَفِي رَفْعِ قَوْلُ الْحَقِّ نَصْبُ نَدٍ كَلَا", "chapter": 1 }, { "id": 865, "number": 865, "text": "وَكَسْرُ وَأَنَّ اللهَ ذَاكٍ وأَخْبَروا ... بِخُلْفٍ إِذَا مَا مُتُّ مُوفِينَ وُصَّلَا", "chapter": 1 }, { "id": 866, "number": 866, "text": "وَنُنَجِّي خَفِيفاً رُضْ مَقَاماً بِضَمِّهِ ... دَنَا رِءْيًا اَبْدِلْ مُدْغِماً بَاسِطًا مُلاَ", "chapter": 1 }, { "id": 867, "number": 867, "text": "وَوُلْدَا بِهاَ وَالزُّخْرُفِ اضْمُمْ وَسَكِّنَنْ ... شِفاَءً وَفِي نُوحٍ شَفاَ حَقُّهُ وَلَا", "chapter": 1 }, { "id": 868, "number": 868, "text": "وَفِيهاَ وَفِي الشُّورى يَكَادُ أَتَى رِضاً ... وَطَا يَتَفَطَّرْنَ اكْسِرُوا غَيْرَ أَثْقَلَا", "chapter": 1 }, { "id": 869, "number": 869, "text": "وَفي التَّاءِ نُونٌ سَاكِنٌ حَجَّ فِي صَفا ... كَمَالٍ وَفِي الشُّورى حَلاَ صَفْوُهُ وِلَا", "chapter": 1 }, { "id": 870, "number": 870, "text": "وَرَاءِيَ وَاجْعَلْ لِي وَإِنِّيْ كِلاَهُماَ ... وَرَبِّيْ وَآتَانِيْ مُضَافَاتُهَا الْعُلا", "chapter": 1 }, { "id": 871, "number": 871, "text": "لِحَمْزَةَ فَاضْمُمْ كَسْرَهاَ أَهْلِهِ امْكُثُوا ... مَعاً وَافْتَحُوا إِنِّي أَنَا دَائِماً حُلَا", "chapter": 1 }, { "id": 872, "number": 872, "text": "وَنَوِّنْ بِها وَالنَّازِعَاتِ طُوًى ذَكَا ... وَفِي اخْتَرْتُكَ اخْتَرْناَكَ فَازَ وَثَقَّلَا", "chapter": 1 }, { "id": 873, "number": 873, "text": "وَأَنَّا وَشَامٍ قَطْعُ أَشْدُدْ وَضُمَّ فِي ابْـ ... ـتِدَا غَيْرِهِ واضْمُمْ وَأَشْرِكْهُ كَلْكَلَا", "chapter": 1 }, { "id": 874, "number": 874, "text": "معَ الزُّخْرُفِ اقْصُرْ بَعْدَ فَتْحٍ وَسَاكِنٍ ... مِهَاداً ثَوَى واضْمُمْ سِوىً فِي نَدٍ كَلَا", "chapter": 1 }, { "id": 875, "number": 875, "text": "وَيَكْسِرُ بَاقِيهِمْ وَفِيهِ وَفِي سُدىً ... مُمَالُ وُقُوفٍ فِي الأَصُولِ تَأَصَّلَا", "chapter": 1 }, { "id": 876, "number": 876, "text": "فَيَسْحَتَكمْ ضَمٌّ وَكَسْرٌ صِحَابُهُمْ ... وَتَخْفِيفُ قَالُوا إِنَّ عَالِمُهُ دَلَا", "chapter": 1 }, { "id": 877, "number": 877, "text": "وَهذَيْنِ فِي هذَانِ حَجَّ وَثِقْلُهُ ... دَناَ فَاجْمَعُوا صِلْ وَافْتَحِ الْمِيمَ حُوَّلَا", "chapter": 1 }, { "id": 878, "number": 878, "text": "وَقُلْ سَاحِرٍ سِحْرٍ شَفَا وَتَلَقَّفُ ارْ ... فَعِ الْجَزْمَ مَعْ أُنْثى يُخَيَّلُ مُقْبِلَا", "chapter": 1 }, { "id": 879, "number": 879, "text": "وَأَنْجَيْتُكُمْ وَاعَدْتُكُمْ مَا رَزَقْتُكُمْ ... شَفَا لاَ تَخَفْ بِالْقَصْرِ وَالْجَزْمِ فُصِّلَا", "chapter": 1 }, { "id": 880, "number": 880, "text": "وَحاَ فَيَحِلَّ الضَّمُّ فِي كَسْرِهِ رِضاً ... وَفِي لاَمِ يَحْلِلْ عَنْهُ وَافَى مُحَلَّلَا", "chapter": 1 }, { "id": 881, "number": 881, "text": "وَفي مُلكِناَ ضَمٌّ شَفَا وَافْتَحُوا أُولِي ... نُهَىً وَحَمَلْناَ ضُمَّ وَاكْسِرْ مُثَقِّلاَ", "chapter": 1 }, { "id": 882, "number": 882, "text": "كَمَا عِنْدَ حِرْمِيٍّ وَخَاطَبَ يَبْصُرُوا ... شَذًا وَبِكَسْرِ اللاَّمِ تُخْلِفَهُ حَلَا", "chapter": 1 }, { "id": 883, "number": 883, "text": "دَرَاكِ وَمَعْ يَاءٍ بِنَنْفُخُ ضَمُّهُ ... وَفي ضَمِّهِ افْتَحْ عَنْ سِوَى وَلَدِ الْعَلاِ", "chapter": 1 }, { "id": 884, "number": 884, "text": "وَبِالْقَصْرِ لِلْمَكِّيِّ وَاجْزِمْ فَلاَ يَخَفْ ... وَأَنَّكَ لاَ فِي كَسْرِهِ صَفْوَةُ الْعُلَا", "chapter": 1 }, { "id": 885, "number": 885, "text": "وَبِالْضَّمِّ تُرْضَى صِفْ رِضاً يَأْتِهِمْ مُؤَنْ ... نَثٌ عَنْ أُولِي حِفْظٍ لَعَلِّيْ أَخِيْ حُلَا", "chapter": 1 }, { "id": 886, "number": 886, "text": "وَذِكْرِيْ مَعاً إِنِّيْ مَعاً لِيْ مَعاً حَشَرْ ... تَنِيْ عَيْنِ نَفْسِيْ إِنَّنِيْ رَأْسِيَ انْجَلَا", "chapter": 1 }, { "id": 887, "number": 887, "text": "وَقُلْ قَالَ عَنْ شُهْدٍ وَآخِرُهَا عَلاَ ... وَقُلْ أَوَلَمْ لاَ وَاوَ دَارِيهِ وَصَّلَا", "chapter": 1 }, { "id": 888, "number": 888, "text": "وَتُسْمِعُ فَتْحُ الضَّمِّ وَالْكَسْرِ غَيْبَةً ... سِوَى الْيَحْصَبِيْ وَالصُّمَّ بِالرَّفْعِ وُكِّلا", "chapter": 1 }, { "id": 889, "number": 889, "text": "وَقَالَ بِهِ فِي النَّمْلِ وَالرُّومِ دَارِمٌ ... وَمِثْقَالُ مَعْ لُقْمَانَ بِالرَّفْعِ أُكْمِلَا", "chapter": 1 }, { "id": 890, "number": 890, "text": "جُذَاذاً بِكَسْرِ الضَّمِّ رَاوٍ وَنُونُهْ ... لِيُحْصِنَكُمْ صَافَى وَأُنِّثَ عَنْ كِلَا", "chapter": 1 }, { "id": 891, "number": 891, "text": "وَسَكَّنَ بَيْنَ الْكَسْرِ وَالْقَصْرِ صُحْبَةٌ ... وَحِرْمٌ وَنُنْجِي اِحْذِفْ وَثَقِّلْ كَذِيْ صِلَا", "chapter": 1 }, { "id": 892, "number": 892, "text": "وَلِلْكُتُبِ اجْمَعْ عَنْ شَذاً وَمُضَافُهَا ... مَعِيْ مَسَّنِيْ إِنِّيْ عِبَادِيَ مُجْتَلَا", "chapter": 1 }, { "id": 893, "number": 893, "text": "سُكَارَى مَعاَ سَكْرَى شَفاَ وَمُحَرَّكٌ ... لِيَقْطَعْ بِكَسْرِ اللاَّمِ كَمْ جِيدُهُ حَلَا", "chapter": 1 }, { "id": 894, "number": 894, "text": "لِيُوفُوا ابْنُ ذَكْوَانٍ لِيَطَّوَّفُوا لَهُ ... لِيَقْضُوا سِوَى بَزِّيِّهِمْ نَفَرٌ جَلَا", "chapter": 1 }, { "id": 895, "number": 895, "text": "وَمَعْ فَاطِرِ انْصِبْ لُؤْلُؤاً نَظْمُ أُلْفَةٍ ... وَرَفْعَ سَوَاءً غَيْرُ حَفْصٍ تَنَخَّلاَ", "chapter": 1 }, { "id": 896, "number": 896, "text": "وَغَيْرُ صِحَابٍ فِي الشَّرِيَعةِ ثُمَّ وَلْـ ... ـيُوَفُّوا فَحَرِّكْهُ لِشُعْبَةَ أَثْقَلَا", "chapter": 1 }, { "id": 897, "number": 897, "text": "فَتَخْطَفُهُ عَنْ نَافِعٍ مِثْلُهُ وَقُلْ ... معاً مَنْسَكاً بالكَسْرِ فِي السِّينِ شُلْشُلَا", "chapter": 1 }, { "id": 898, "number": 898, "text": "وَيُدْفَعُ حَقٌّ بَيْنَ فَتْحَيْهِ سَاكِنٌ ... يُدَافِعُ وَالْمَضْمُومُ فِي أَذِنَ اَعْتَلَا", "chapter": 1 }, { "id": 899, "number": 899, "text": "نَعَمْ حَفِظُوا وَالْفَتْحُ فِي تَا يُقَاتِلُو ... نَ عَمَّ عُلاَهُ هُدِّمَتْ خَفَّ إِذْ دُلَا", "chapter": 1 }, { "id": 900, "number": 900, "text": "وَبَصْرِيٌّ أَهْلَكْنَا بِتَاءٍ وَضَمِّهَا ... يَعُدُّونَ فِيهِ الْغَيْبُ شَايَعَ دُخْلُلَا", "chapter": 1 }, { "id": 901, "number": 901, "text": "وَفِي سَبَإٍ حَرْفَانِ مَعْهَا مُعَاجِزِيْـ ... ـنَ حَقٌّ بِلاَ مَدٍّ وَفِي الْجِيمِ ثُقِّلَا", "chapter": 1 }, { "id": 902, "number": 902, "text": "وَالاَوَّلُ مَعْ لُقْماَنَ يَدْعُونَ غَلَّبُوا ... سِوَى شُعْبَةٍ وَالْيَاءُ بَيْتِيَ جَمَّلَا", "chapter": 1 }, { "id": 903, "number": 903, "text": "أَمَانَاتِهِمْ وَحِّدْ وَفِي سَالَ دَارِياً ... صَلاَتِهِمُ شَافٍ وَعَظْماً كَذِيْ صِلَا", "chapter": 1 }, { "id": 904, "number": 904, "text": "مَعَ الْعَظْمِ وَاضْمُمْ وَاكْسِرِ الضَّمَّ حَقُّهُ ... بِتَنْبُتُ وَالمَفْتُوحُ سِيناَءَ ذُلِّلَا", "chapter": 1 }, { "id": 905, "number": 905, "text": "وَضَمٌّ وَفَتْحٌ مَنْزِلاً غَيْرَ شُعْبَةٍ ... وَنَوَّنَ تَتْراً حَقُّهُ وَاكْسِرِ الْوِلَا", "chapter": 1 }, { "id": 906, "number": 906, "text": "وَأَنَّ ثَوَى وَالنُّونَ خَفِّفْ كَفَى وَتَهْـ ... ـجُرُونَ بِضَمٍّ وَاكْسِرِ الضَّمَّ أَجْمَلَا", "chapter": 1 }, { "id": 907, "number": 907, "text": "وَفِي لاَمِ لِلهِ الأَخِيرَيْنِ حَذْفُهاَ ... وَفِي الْهَاءِ رَفْعُ الْجَرِّ عَنْ وَلَدِ الْعَلَا", "chapter": 1 }, { "id": 908, "number": 908, "text": "وَعَالِمُ خَفْضُ الرَّفْعِ عَنْ نَفَرٍ وَفَتْـ ... ـحُ شِقْوَتُنَا وَامْدُدْ وَحَرِّكُهُ شُلْشُلَا", "chapter": 1 }, { "id": 909, "number": 909, "text": "وَكَسْرُكَ سُخْرِيًّا بِهاَ وَبِصَادِهاَ ... عَلَى ضَمِّهِ أَعْطَى شِفَاءً وَأَكْمَلَا", "chapter": 1 }, { "id": 910, "number": 910, "text": "وَفِي أَنَّهُمْ كَسْرٌ شَرِيفٌ وَتُرْجَعُـ ... ـونَ في الضَّمِّ فَتْحٌ وَاكْسِرِ الْجيمَ وَاكْمُلاَ", "chapter": 1 }, { "id": 911, "number": 911, "text": "وَفي قَالَ كَمْ قُلْ دُونَ شَكٍّ وَبَعْدَهُ ... شَفَا وَبِهَا يَاءٌ لَعَلِّيَ عُلِّلَا", "chapter": 1 }, { "id": 912, "number": 912, "text": "وَحَقٌّ وَفَرَّضْناَ ثَقِيلاً وَرَأْفَةٌ ... يُحَرِّكُهُ الْمَكِّيْ وَأَرْبَعُ أَوَّلَا", "chapter": 1 }, { "id": 913, "number": 913, "text": "صِحَابٌ وَغَيْرُ الْحَفْصِ خَامِسَةُ الأَخِيْـ ... ـرُ أَنْ غَضِبَ التَّخْفِيفُ وَالْكَسْرُ أُدْخِلَا", "chapter": 1 }, { "id": 914, "number": 914, "text": "وَيَرْفَعُ بَعْدَ الْجَرِّ يَشْهَدُ شَائِعٌ ... وَغَيْرُ أُولِيْ بِالنَّصْبِ صَاحِبُهُ كَلَا", "chapter": 1 }, { "id": 915, "number": 915, "text": "وَدُرِّيٌّ اكْسِرْ ضَمَّهُ حُجَّةً رِضَى ... وَفِي مَدِّهِ وَالْهَمْزِ صُحْبَتُهُ حَلَا", "chapter": 1 }, { "id": 916, "number": 916, "text": "يُسَبِّحُ فَتْحُ الْبَا كَذَا صِفْ وَيوقَدُ الْـ ... ـمُؤَنَّثُ صِفْ شَرْعاً وَحَقٌّ تَفَعَّلَا", "chapter": 1 }, { "id": 917, "number": 917, "text": "وَمَا نَوَّنَ البَزِّيْ سَحاَبٌ وَرَفْعُهُمْ ... لَدَى ظُلُمَاتٍ جَرَّ دَارٍ وَأَوْصَلَا", "chapter": 1 }, { "id": 918, "number": 918, "text": "كَمَا اسْتُخْلِفَ اضْمُمْهُ مَعَ الْكَسْرِ صَادِقاً ... وَفِي يُبْدِلَنَّ الْخِفُّ صَاحِبُهُ دَلَا", "chapter": 1 }, { "id": 919, "number": 919, "text": "وَثَانِيْ ثَلاَثَ ارْفَعْ سِوَى صُحْبَةٍ ... وَقِفْ وَلاَ وَقْفَ قَبْلَ النَّصْبِ إِنْ قُلْتَ أُبْدِلَا", "chapter": 1 }, { "id": 920, "number": 920, "text": "وَيَأْكُلُ مِنْهَا النُّونُ شَاعَ وَجَزْمُنَا ... وَيَجْعَلْ بِرَفْعٍ دَلَّ صَافِيهِ كُمَّلَا", "chapter": 1 }, { "id": 921, "number": 921, "text": "وَنَحْشُرُ يَا دَارٍ عَلاَ فَيَقُولُ نُـ ... ـونُ شَامٍ وَخَاطِبْ تَسْتَطِيعُونَ عُمَّلَا", "chapter": 1 }, { "id": 922, "number": 922, "text": "وَنُزِّلَ زِدْهُ النُّونَ وَارْفَعْ وَخِفَّ وَالْـ ... ... ـمَلاَئِكَةُ المَرْفُوعُ يُنْصَبُ دُخْلُلَا", "chapter": 1 }, { "id": 923, "number": 923, "text": "تَشَقَّقُ خِفُّ الشِّينِ مَعْ قَافَ غَالِبٌ ... وَيَأْمُرُ شَافٍ وَاجْمَعُوا سُرُجاً وِلَا", "chapter": 1 }, { "id": 924, "number": 924, "text": "وَلَمْ يَقْتِرُوا اضْمُمْ عَمَّ وَالْكَسْرَ ضُمَّ ثِقْ ... يُضَاعَفْ وَيَخْلُدْ رَفْعُ جَزْمٍ كَذِي صِلَا", "chapter": 1 }, { "id": 925, "number": 925, "text": "وَوَحَّدَ ذُرِّيَّاتِنَا حِفْظُ صُحْبَةٍ ... وَيَلْقَوْنَ فَاضْمُمْهُ وَحَرِّكْ مُثَقِّلاَ", "chapter": 1 }, { "id": 926, "number": 926, "text": "سِوى صُحْبَةٍ وَالْيَاءُ قَوْمِي وَلَيْتَنِي ... وَكَمْ لَوْ وَلَيْتٍ تُورِثُ الْقَلْبَ أَنْصُلَا", "chapter": 1 }, { "id": 927, "number": 927, "text": "وَفِي حَاذِرُونَ الْمدُّ مَا ثُلَّ فَارِهِيْـ ... ـنَ ذَاعَ وَخَلْقُ اضْمُمْ وَحَرِّكْ بِهِ الْعُلَا", "chapter": 1 }, { "id": 928, "number": 928, "text": "كَمَا فِي نَدٍ وَالأيْكَةِ اللاَّمُ سَاكِنٌ ... مَعَ الْهَمْزِ وَاخْفِضْهُ وَفِي صَادَ غَيْطَلَا", "chapter": 1 }, { "id": 929, "number": 929, "text": "وَفِي نَزَّلَ التَّخْفِيفُ وَالرُّوحُ وَالأَمِيْـ ... ـنُ رَفْعُهُماَ عُلْوٌّ سَمَا وَتَبَجَّلَا", "chapter": 1 }, { "id": 930, "number": 930, "text": "وَأَنِّثْ يَكُنْ لِلْيَحْصَبِيْ وَارْفَعَ آيَةً ... وَفَا فَتَوَكَّلْ وَاوُ ظَمْآنِهِ حَلَا", "chapter": 1 }, { "id": 931, "number": 931, "text": "وَيَا خَمْسِ أَجْرِيْ مَعْ عِبَادِيْ وَلِيْ مَعِيْ ... مَعاً مَعْ أَبِيْ إِنِّيْ مَعاً رَبِّيَ انْجَلا", "chapter": 1 }, { "id": 932, "number": 932, "text": "شِهَابٍ بِنُونٍ ثِقْ وَقُلْ يَأْتِيَنَّنِي ... دَنَا مَكُثَ افْتَحْ ضَمَّةَ الْكَافِ نَوْفَلَا", "chapter": 1 }, { "id": 933, "number": 933, "text": "مَعاً سَبَأَ افْتَحْ دُونَ نُونٍ حِمًى هُدًى ... وَسَكِّنْهُ وَانْوِ الْوَقْفَ زُهْراً وَمَنْدَلَا", "chapter": 1 }, { "id": 934, "number": 934, "text": "أَلاَ يَسْجُدُوا رَاوٍ وَقِفْ مُبْتَلىً أَلاَ ... وَياَ وَاسْجُدُوا وَأبْدَأْهُ بِالضَّمِّ مُوصِلَا", "chapter": 1 }, { "id": 935, "number": 935, "text": "أَرَادَ أَلاَ يَا هؤُلاَءِ اسْجُدُوا وَقِفْ ... لَهُ قَبْلَهُ وَالْغَيْرُ أَدْرَجَ مُبْدِلَا", "chapter": 1 }, { "id": 936, "number": 936, "text": "وَقَدْ قِيلَ مَفْعُولاً وَأَنْ أَدْغَمُوا بِلاَ ... وَلَيْسَ بِمَقْطُوعٍ فَقِفْ يَسْجُدُوا وَلَا", "chapter": 1 }, { "id": 937, "number": 937, "text": "وَيُخْفُونَ خَاطِبْ يُعْلِنُونَ عَلَى رِضاً ... تُمِدُّونَنِي الإِدْغامُ فَازَ فَثَقَّلَا", "chapter": 1 }, { "id": 938, "number": 938, "text": "مَعَ السُّوقِ سَاقَيهاَ وَسُوقِ اهْمِزُوا زَكَا ... وَوَجْهٌ بِهَمْزٍ بَعْدَهُ الْوَاوُ وُكِّلَا", "chapter": 1 }, { "id": 939, "number": 939, "text": "نَقُولَنَّ فَاضْمُمْ رَابِعاً وَنُبَيِّتَنْـ ... ـنَهُ وَمَعاً فِي النُّونِ خَاطِبْ شَمَرْدَلَا", "chapter": 1 }, { "id": 940, "number": 940, "text": "وَمَعْ فَتْحِ أَنَّ النَّاسَ مَا بَعْدَ مَكْرِهِمْ ... لِكُوفٍ وَأَمَّا يُشْرِكُونَ نَدٍ حَلاَ", "chapter": 1 }, { "id": 941, "number": 941, "text": "وَشَدِّدْ وَصِلْ وَامْدُدْ بَلِ أدَّارَكَ الَّذِي ... ذَكاَ قَبْلَهُ يَذَّكَّرُونَ لَهُ حُلَا", "chapter": 1 }, { "id": 942, "number": 942, "text": "بِهَادِيْ مَعًا تَهْدِيْ فَشَا الْعُمْيِ نَاصِباً ... وَبِالْيَا لِكُلٍّ قِفْ وَفِي الرُّومِ شَمْلَلَا", "chapter": 1 }, { "id": 943, "number": 943, "text": "وَآتُوهُ فَاقْصُرْ وَافْتَحِ الضَّمَّ عِلْمُهُ ... فَشاَ تَفْعَلُونَ الْغَيْبُ حَقٌّ لَهُ وَلَا", "chapter": 1 }, { "id": 944, "number": 944, "text": "وَمَالِيْ وَأَوْزِعْنِيْ وَإِنِّيْ كِلاَهُماَ ... لِيَبْلُوَنِي الْيَاءَاتُ فِي قَوْلِ مَنْ بَلَا", "chapter": 1 }, { "id": 945, "number": 945, "text": "وَفِي نُرِيَ الْفَتْحَانِ مَعْ أَلِفٍ وَيَا ... ئِهِ وَثَلاَثٌ رَفْعُهَا بَعْدُ شُكِّلَا", "chapter": 1 }, { "id": 946, "number": 946, "text": "وَحُزْناً بِضَمٍّ مَعْ سُكُونٍ شَفَا وَيَصْـ ... ـدُرَ اضْمُمْ وَكَسْرُ الضَّمِّ ظَامِيهِ أَنْهَلَا", "chapter": 1 }, { "id": 947, "number": 947, "text": "وَجِذْوَةٍ اضْمُمْ فُزْتَ وَالْفَتْحَ نَلْ وَصُحْـ ... ـبَةٌ كَهْفُ ضَمِّ الرَّهْبِ وَاسْكِنْهُ ذُبَّلَا", "chapter": 1 }, { "id": 948, "number": 948, "text": "يُصَدِّقُنِي ارْفَعْ جَزْمَهُ فِي نُصُوصِهِ ... وَقُلْ قَالَ مُوسَى وَاحْذِفِ الْوَاوَ دُخْلُلَا", "chapter": 1 }, { "id": 949, "number": 949, "text": "نَمَا نَفَرٌ بِالضَّمِّ وَالْفَتْحِ يَرْجِعُو ... نَ سِحْرَانِ ثِقْ فِي سَاحِرَانِ فَتُقْبَلَا", "chapter": 1 }, { "id": 950, "number": 950, "text": "وَيُجْبَى خَلِيطٌ يَعْقِلُونَ حَفِظْتُهُ ... وَفِي خُسِفَ الْفَتْحَتَيْنِ حَفْصٌ تَنَخَّلَا", "chapter": 1 }, { "id": 951, "number": 951, "text": "وَعِنْدِيْ وَذُو الثُّنْياَ وَإِنِّيَ أَرْبَعٌ ... لَعَلِّيْ مَعاً رَبِّيْ ثَلاَثٌ مَعِيْ اعْتَلَا", "chapter": 1 }, { "id": 952, "number": 952, "text": "يَرَوْا صُحْبَةٌ خَاطِبْ وَحَرِّكْ وَمُدَّ فِي النْـ ... ـنَشَاءَةِ حَقاًّ وَهْوَ حَيْثُ تَنَزَّلَا", "chapter": 1 }, { "id": 953, "number": 953, "text": "مَوَدَّةً المَرْفُوعُ حَقُّ رُوَاتِهِ ... وَنَوِّنْهُ وَانْصِبْ بَيْنَكُمْ عَمَّ صَنْدَلَا", "chapter": 1 }, { "id": 954, "number": 954, "text": "وَيَدْعُونَ نَجْمٌ حَافِظٌ وَمُوَحِّدٌ ... هُنَا آيَةٌ مِنْ رَبِّهِ صُحْبَةٌ دَلَا", "chapter": 1 }, { "id": 955, "number": 955, "text": "وَفِي وَنَقُولُ الْيَاءُ حِصْنٌ وَيُرْجَعُو ... نَ صَفْوٌ وَحَرْفُ الرُّومِ صَافِيهِ حُلِّلاَ", "chapter": 1 }, { "id": 956, "number": 956, "text": "وَذَاتُ ثَلاَثٍ سُكِّنَتْ بَا نُبُوِّئَنْـ ... نَ مَعْ خِفِّهِ وَالْهَمْزُ بِالْيَاءِ شَمْلَلَا", "chapter": 1 }, { "id": 957, "number": 957, "text": "وَإِسْكَانُ وَلْ فَاكْسِرْ كَمَا حَجَّ جَا نَدىً ... وَرَبِّيْ عِبَادِيْ أَرْضِيَ الْيَا بِهَا انْجَلَا", "chapter": 1 }, { "id": 958, "number": 958, "text": "وَعَاقِبَةُ الثَّانِيْ سَمَا وَبِنُونِهِ ... نُذِيقُ زَكَا لِلْعَالَمِينَ اكْسِرُوا عُلَا", "chapter": 1 }, { "id": 959, "number": 959, "text": "لِيَرْبُوا خِطَابٌ ضُمَّ وَالْوَاوُ سَاكِنٌ ... أَتَى وَاجْمَعُوا آثَارِ كَمْ شَرَفاً عَلَا", "chapter": 1 }, { "id": 960, "number": 960, "text": "وَيَنْفَعُ كُوفِيٌّ وَفِي الطَّولِ حِصْنُهُ ... وَرَحْمَةً ارْفَعْ فَائِزاً وَمُحَصِّلَا", "chapter": 1 }, { "id": 961, "number": 961, "text": "وَيَتَّخِذَ المَرْفُوعُ غَيْرُ صِحَابِهِمْ ... تُصَعِّرْ بِمدٍّ خَفَّ إِذْ شَرْعُهُ حَلَا", "chapter": 1 }, { "id": 962, "number": 962, "text": "وَفِي نِعْمَةً حَرِّكْ وَذُكِّرَ هَاؤُهَا ... وَضُمَّ وَلاَ تَنْوِينَ عَنْ حُسْنٍ اعْتَلَا", "chapter": 1 }, { "id": 963, "number": 963, "text": "سِوَى ابْنِ الْعَلاَ وَالْبَحْرُ أُخْفِي سُكُونُهُ ... فَشاَ خَلْقَهُ التَّحْرِيكُ حِصْنٌ تَطَوَّلَا", "chapter": 1 }, { "id": 964, "number": 964, "text": "لِمَا صَبَرُوا فَاكْسِرْ وَخَفِّفْ شَذاً وَقُلْ ... بِماَ يَعْمَلُونَ اثْناَنِ عَنْ وَلَدِ الْعَلَا", "chapter": 1 }, { "id": 965, "number": 965, "text": "وَبِالْهَمْزِ كُلُّ اللاَّءِ وَالْياَءِ بَعْدَهُ ... ذَكَا وَبِيَاءٍ سَاكِنٍ حَجَّ هُمَّلَا", "chapter": 1 }, { "id": 966, "number": 966, "text": "وَكَالْيَاءِ مَكْسُوراً لِوَرْشٍ وَعَنْهُمَا ... وَقِفْ مُسْكِناً وَالْهَمْزُ زَاكِيهِ بُجِّلَا", "chapter": 1 }, { "id": 967, "number": 967, "text": "وَتَظَّاهَرُونَ اضْمُمْهُ وَاكْسِرْ لِعاَصِمٍ ... وَفِي الْهَاءِ خَفِّفْ وَامْدُدِ الظَّاءَ ذُبَّلَا", "chapter": 1 }, { "id": 968, "number": 968, "text": "وَخَفَّفَهُ ثَبْتٌ وَفِي قَدْ سَمِعْ كَمَا ... هُنَا وَهُناَكَ الظَّاءُ خُفِّفَ نَوْفَلَا", "chapter": 1 }, { "id": 969, "number": 969, "text": "وَحَقُّ صِحَابٍ قَصْرُ وَصْلِ الظَّنُونَ وَالرَّ ... سُولَ السَّبِيَلا وَهْوَ فِي الْوَقْفِ فِي حُلَا", "chapter": 1 }, { "id": 970, "number": 970, "text": "مَقَامَ لِحَفْصٍ ضُمَّ وَالثَّانِ عَمَّ فِي الدْ ... دُخَانِ وَآتَوْهَا عَلَى الْمَدِّ ذُو حُلاَ", "chapter": 1 }, { "id": 971, "number": 971, "text": "وَفِي الْكُلِّ ضَمُّ الْكَسْرِ فِي إِسْوَةٌ نَدىً ... وَقَصْرُ كِفَا حَقٍّ يُضَاعَفْ مُثَقَّلَا", "chapter": 1 }, { "id": 972, "number": 972, "text": "وَبِالْيَا وَفَتْحِ الْعَيْنِ رَفْعُ الْعَذَابَ حِصْـ ... ـنُ حُسْنٍ وَتَعْمَلْ نُؤْتِ بِالْيَاءِ شَمْلَلَا", "chapter": 1 }, { "id": 973, "number": 973, "text": "وَقِرْنَ افْتَحْ اذْ نَصُّوا يَكُونَ لَهُ ثَوى ... يَحِلُّ سِوَى الْبَصْرِيْ وَخَاتِمَ وُكِّلَا", "chapter": 1 }, { "id": 974, "number": 974, "text": "بِفَتْحٍ نَمَا سَادَاتِنَا اجْمَعْ بِكَسْرَةٍ ... كَفَى وَكَثِيراً نُقْطَةٌ تَحْتُ نُفِّلَا", "chapter": 1 }, { "id": 975, "number": 975, "text": "وَعَالِمِ قُلْ عَلاَّمِ شَاعَ وَرَفْعُ خَفْـ ... ... ـضِهِ عَمَّ مِنْ رِجْزٍ أَلِيْمٍ مَعاً وِلَا", "chapter": 1 }, { "id": 976, "number": 976, "text": "عَلَى رَفْعِ خَفْضِ الْمِيمِ دَلَّ عَلِيمُهُ ... وَنَخْسِفْ نَشَأْ نُسْقِطْ بِهاَ الْيَاءُ شَمْلَلَا", "chapter": 1 }, { "id": 977, "number": 977, "text": "وَفِي الرِّيحَ رَفْعٌ صَحَّ مِنْسَأَتَهْ سُكُو ... نُ هَمْزَتِهِ مَاضٍ وَأَبْدِلْهُ إِذْ حَلَا", "chapter": 1 }, { "id": 978, "number": 978, "text": "مَسَاكِنِهِمْ سَكِّنْهُ وَاقْصُرْ عَلَى شَذاً ... وَفِي الْكَافِ فَافْتَحْ عَالِمًا فَتُبَجَّلَا", "chapter": 1 }, { "id": 979, "number": 979, "text": "نُجَازِي بِيَاءٍ وَافْتَحِ الزَّايَ وَالْكَفُو ... رَ رَفْعٌ سَمَا كَمْ صَابَ أُكْلٍ أَضِفْ حُلَا", "chapter": 1 }, { "id": 980, "number": 980, "text": "وَحَقُّ لِوَا بَاعِدْ بِقَصْرٍ مُشَدَّدَا ... وَصَدَّقَ لِلْكُوفِيِّ جَاءَ مُثَقَّلَا", "chapter": 1 }, { "id": 981, "number": 981, "text": "وَفُزِّعَ فَتْحُ الضَّمِّ وَالْكَسْرِ كَامِلٌ ... وَمَنْ أَذِنَ اضْمُمْ حُلْوَ شَرْعٍ تَسَلْسَلَا", "chapter": 1 }, { "id": 982, "number": 982, "text": "وَفِي الْغُرْفَةِ التَّوْحِيدُ فَازَ وَيُهْمَزْ التْـ ... ـتَنَاوُشُ حُلْوًا صُحْبَةً وَتَوَصُّلَا", "chapter": 1 }, { "id": 983, "number": 983, "text": "وَأَجْرِىْ عِبَادِيْ رَبِّيَ الْيَا مُضَافُهاَ ... وَقُلْ رَفْعُ غَيْرُ اللهِ بِالْخَفْضِ شُكِّلَا", "chapter": 1 }, { "id": 984, "number": 984, "text": "وَنَجْزِي بِياَءٍ ضُمَّ مَعْ فَتْحِ زَايِهِ ... وَكُلَّ بِهِ ارْفَعْ وَهْوَ عَنْ وَلَدِ الْعَلَا", "chapter": 1 }, { "id": 985, "number": 985, "text": "وَفِي السَّيِّئِ المَخْفُوضِ هَمْزاً سُكُونُهُ ... فَشاَ بَيِّناتٍ قَصْرُ حَقٍّ فَتًى عَلَا", "chapter": 1 }, { "id": 986, "number": 986, "text": "وَتَنْزِيلُ نَصْبُ الرَّفْعِ كَهْفُ صِحاَبِهِ ... وَخَفِّفْ فَعَزَّزْناَ لِشُعْبَةَ مُحْمِلَا", "chapter": 1 }, { "id": 987, "number": 987, "text": "وَمَا عَمِلَتْهُ يَحْذِفُ الْهاَءَ صُحْبَةٌ ... وَوَالْقَمَرَ ارْفَعْهُ سَماَ وَلَقَدْ حَلَا", "chapter": 1 }, { "id": 988, "number": 988, "text": "وَخَا يَخْصِمُونَ افْتَحْ سَمَا لُذْ وَأَخْفِ حُلْـ ... ـوَ بَرٍّ وَسَكِّنْهُ وَخَفِّفْ فَتُكْمِلَا", "chapter": 1 }, { "id": 989, "number": 989, "text": "وَسَاكِنَ شُغْلٍ ضُمَّ ذِكْراً وَكَسْرُ فِي ... ظِلاَلٍ بِضَمٍّ وَاقْصُرِ اللاَّمَ شُلْشُلَا", "chapter": 1 }, { "id": 990, "number": 990, "text": "وَقُلْ جُبُلاً مَعْ كَسْرِ ضَمَّيْهِ ثِقْلُهُ ... أَخُو نُصْرَةٍ وَاضْمُمْ وَسَكِّنْ كَذِيْ حَلَا", "chapter": 1 }, { "id": 991, "number": 991, "text": "وَتَنْكُسْهُ فَاضْمُمْهُ وَحَرِّكْ لِعَاصِمٍ ... وَحَمْزَةَ وَاكْسِرْ عَنْهُمَا الضَّمَّ أَثْقَلَا", "chapter": 1 }, { "id": 992, "number": 992, "text": "لِيُنْذِرَ دُمْ غُصْناً وَالاَحْقَافُ هُمْ بِهَا ... بِخُلْفٍ هَدَى مَالِيْ وَإِنِّيْ مَعاً حُلَا", "chapter": 1 }, { "id": 993, "number": 993, "text": "وَصَفًّا وَزَجْراً ذِكْراً ادْغَمَ حَمْزَةٌ ... وَذَرْواً بِلاَ رَوْمٍ بِها التَّا فَثَقَّلَا", "chapter": 1 }, { "id": 994, "number": 994, "text": "وَخَلاَّدُهُمْ بِالْخُلْفِ فَالْمُلْقِياَتِ فَالْـ ... ـمُغِيرَاتِ فِي ذِكْراً وَصُبْحاً فَحَصِّلَا", "chapter": 1 }, { "id": 995, "number": 995, "text": "بِزِينَةِ نَوِّنْ فِي نَدٍ وَالْكَوَاكِبِ انْـ ... صِبُوا صَفْوَةً يَسَّمَّعُونَ شَذاً عَلَا", "chapter": 1 }, { "id": 996, "number": 996, "text": "بِثِقْلَيْهِ وَاضْمُمْ تَا عَجِبْتَ شَذاً وَسَا ... كِنٌ مَعاً اَوْ آبَاؤُنَا كَيْفَ بَلَّلَا", "chapter": 1 }, { "id": 997, "number": 997, "text": "وَفِي يُنْزَفُونَ الزَّايَ فَاكْسِرْ شَذاً وَقُلْ ... في الاُخْرى ثَوَى وَاضْمُمْ يَزِفُّونَ فَاكْمُلَا", "chapter": 1 }, { "id": 998, "number": 998, "text": "وَمَاذَا تُرِي بِالضَّمِّ وَالْكَسْرِ شَائِعٌ ... وَإِلْيَاسَ حَذْفُ الْهَمْزِ بِالْخُلْفِ مُثِّلَا", "chapter": 1 }, { "id": 999, "number": 999, "text": "وَغَيْرُ صِحَابٍ رَفْعُهُ اللهَ رَبَّكُمْ ... وَرَبَّ وَإِلْيَاسِينَ بِالْكَسْرِ وُصِّلَا", "chapter": 1 }, { "id": 1000, "number": 1000, "text": "مَعَ الْقَصْرِ مَعْ إِسْكَانِ كَسْرٍ دَنَا غِنىً ... وَإِنِّيْ وَذُو الثُّنْيَا وَأَنِّيَ اجْمِلَا", "chapter": 1 }, { "id": 1001, "number": 1001, "text": "وَضَمُّ فَوَاقٍ شَاعَ خَالِصَةٍ أَضِفْ ... لَهُ الرَّحْبُ وَحِّدْ عَبْدَنَا قَبْلُ دُخْلُلَا", "chapter": 1 }, { "id": 1002, "number": 1002, "text": "وَفي يُوعَدُونَ دمُ حُلاً وَبِقَافَ دُمْ ... وَثَقَّلْ غَسَّاقاً مَعاً شَائِدٌ عُلَا", "chapter": 1 }, { "id": 1003, "number": 1003, "text": "وَآخَرُ لِلْبَصْرِيْ بَضَمٍّ وَقَصْرِهِ ... وَوَصْلُ اتَّخَذْناَهُمْ حَلاَ شَرْعُهُ وِلَا", "chapter": 1 }, { "id": 1004, "number": 1004, "text": "وَفَالْحَقُّ فِى نَصْرٍ وَخُذْ يَاءَ لِيْ مَعاً ... وَإِنِّيْ وَبَعْدِيْ مَسَّنِيْ لَعْنَتِيْ إِلَى", "chapter": 1 }, { "id": 1005, "number": 1005, "text": "أَمَنْ خَفَّ حِرْمِيٌّ فَشَا مَدَّ سَالِماً ... مَعَ الْكَسْرِ حَقٌّ عَبْدَهُ اِجْمَعْ شَمَرْدَلَا", "chapter": 1 }, { "id": 1006, "number": 1006, "text": "وَقُلْ كَاشِفاَتٌ مُمْسِكَاتٌ مُنَوِّناً ... وَرَحْمَتِهِ مَعْ ضُرِّهِ النَّصْبُ حُمِّلَا", "chapter": 1 }, { "id": 1007, "number": 1007, "text": "وَضُمَّ قَضَى وَاكْسِرْ وَحَرِّكْ وَبَعْدُ رَفْـ ... ـعُ شَافٍ مَفَازَاتٍ اجْمَعُوا شَاعَ صَنْدَلَا", "chapter": 1 }, { "id": 1008, "number": 1008, "text": "وَزِدْ تَأْمُرونِيْ النُّونَ كَهْفًا وَعَمَّ خِفْـ ... ـفُهُ فُتِّحَتْ خَفِّفْ وَفِي النَّبإ الْعُلَا", "chapter": 1 }, { "id": 1009, "number": 1009, "text": "لِكُوفٍ وَخُذْ يَا تَأْمُرُونِي أَرَادَنِيْ ... وَإِنِّيْ مَعاً مَعْ يَا عِبَادِيْ فَحَصِّلَا", "chapter": 1 }, { "id": 1010, "number": 1010, "text": "وَيَدْعُونَ خَاطِبْ إِذْ لَوَى هَاءُ مِنْهُمُ ... بِكَافٍ كَفَى أَوْ أَنْ زِدِ الْهَمْزَ ثُمَّلَا", "chapter": 1 }, { "id": 1011, "number": 1011, "text": "وَسَكِّنْ لَهُمْ وَاضْمُمْ بِيَظْهَرَ وَاكْسِرَنْ ... وَرَفْعَ الْفَسَادَ انْصِبْ إِلَى عَاقِلٍ حَلَا", "chapter": 1 }, { "id": 1012, "number": 1012, "text": "فَأَطَّلِعَ ارْفَعْ غَيْرَ حَفْصٍ وَقَلْبِ نَوْ ... وِنُوا مِنْ حَمِيدٍ أَدْخِلُوا نَفَرٌ صِلَا", "chapter": 1 }, { "id": 1013, "number": 1013, "text": "عَلَى الْوَصْلِ وَاضْمُمْ كَسْرَهُ يَتَذَكَّرُو ... نَ كَهْفٌ سَماَ وَاحْفَظْ مُضاَفَاتِهاَ الْعُلَا", "chapter": 1 }, { "id": 1014, "number": 1014, "text": "ذَرُونِيَ وَادْعُونِيْ وَإِنِّيْ ثَلاثَةٌ ... لَعَلِيِّ وَفِيْ مَالِيْ وَأَمْرِيَ مَعْ إِلى", "chapter": 1 }, { "id": 1015, "number": 1015, "text": "وَإِسْكَانُ نَحْسَاتٍ بِهِ كَسْرُهُ ذَكاَ ... وَقَوْلُ مُمِيلِ السِّينِ لِلَّيْثِ أُخْمِلَا", "chapter": 1 }, { "id": 1016, "number": 1016, "text": "وَنَحْشُرُ يَاءٌ ضُمَّ مَعْ فَتْحِ ضَمِّهِ ... وَأَعْدَاءُ خُذْ وَالْجَمْعُ عَمَّ عَقَنْقَلَا", "chapter": 1 }, { "id": 1017, "number": 1017, "text": "لَدَى ثَمَرَاتٍ ثُمَّ يَا شُرَكَائِىَ الْـ ... ـمُضَافُ وَيَا رَبِّيْ بِهِ الْخُلْفُ بُجِّلَا", "chapter": 1 }, { "id": 1018, "number": 1018, "text": "وَيُوحَى بِفَتْحِ الْحَاءِ دَانَ وَيَفْعَلُو ... نَ غَيْرُ صِحَابٍ يَعْلَمَ ارْفَعْ كَماَ اعْتَلَا", "chapter": 1 }, { "id": 1019, "number": 1019, "text": "بِمَا كَسَبَتْ لاَ فَاءَ عَمَّ كَبِيرَ في ... كَبَائِرَ فِيها ثُمَّ فِي النَّجْمِ شَمْلَلَا", "chapter": 1 }, { "id": 1020, "number": 1020, "text": "وَيُرْسِلَ فَارْفَعْ مَعْ فَيُوحِي مُسَكِّناً ... أَتَانَا وَأَنْ كُنْتُمْ بِكَسْرٍ شَذَا الْعُلَا", "chapter": 1 }, { "id": 1021, "number": 1021, "text": "وَيَنْشَأُ فِي ضَمٍّ وَثِقْلٍ صِحاَبُهُ ... عِبَادُ بِرَفْعِ الدَّالِ فِي عِنْدَ غَلْغَلَا", "chapter": 1 }, { "id": 1022, "number": 1022, "text": "وَسَكِّنْ وَزِدْ هَمْزاً كَوَاوٍ أَؤُشْهِدوا ... أَمِيناً وَفِيهِ الْمَدُّ بِالْخُلْفِ بَلَّلَا", "chapter": 1 }, { "id": 1023, "number": 1023, "text": "وَقُلْ قَالَ عَنْ كُفْؤٍ وَسَقْفاً بِضَمِّهِ ... وَتَحْرِيكِهِ بِالضَّمِّ ذَكَّرَ أَنْبَلَا", "chapter": 1 }, { "id": 1024, "number": 1024, "text": "وَحُكْمُ صِحَابٍ قَصْرُ هَمْزَةِ جَاءَنَا ... وَأَسْوِرَةٌ سَكِّنْ وَبِالْقَصْرِ عُدِّلَا", "chapter": 1 }, { "id": 1025, "number": 1025, "text": "وَفِي سَلَفاً ضَمَّا شَرِيفٍ وَصَادُهُ ... يَصُدُّونَ كَسْرُ الضَّمِّ فِى حَقِّ نَهْشَلَا", "chapter": 1 }, { "id": 1026, "number": 1026, "text": "ءَآلِهةٌ كُوفٍ يُحَقِّقُ ثَانِياً ... وَقُلْ أَلِفاً لِلْكُلِّ ثَالِثاً اُبْدِلَا", "chapter": 1 }, { "id": 1027, "number": 1027, "text": "وَفِي تَشْتَهِيهِ تَشْتَهِيْ حَقُّ صُحْبَةٍ ... وَفِي تُرْجَعُونَ الْغَيْبُ شَايَعَ دُخْلُلَا", "chapter": 1 }, { "id": 1028, "number": 1028, "text": "وَفِي قِيلَهُ اكْسِرْ وَاكْسِرِ الضَّمَّ بَعْدُ فِي ... نَصِيرٍ وَخَاطِبْ يَعْلَمُونَ كَمَا انْجَلَا", "chapter": 1 }, { "id": 1029, "number": 1029, "text": "بِتَحْتِيْ عِبَادِيْ الْيَا وَيَغْلِيْ دَناَ عُلاً ... وَرَبُّ السَّموَاتِ اخْفِضُوا الرَّفْعَ ثُمَّلاَ", "chapter": 1 }, { "id": 1030, "number": 1030, "text": "وَضَمَّ اعْتِلُوهُ اكْسِرْ غِنىً إِنَّكَ افْتَحُوا ... رَبِيعاً وَقُلْ إِنِّيْ وَلِيْ الْيَاءُ حُمِّلَا", "chapter": 1 }, { "id": 1031, "number": 1031, "text": "مَعاً رَفْعُ آيَاتٍ عَلَى كَسْرِهِ شَفَا ... وَإِنَّ وَفِي أَضْمِرْ بِتَوْكِيدٍ اَوَّلَا", "chapter": 1 }, { "id": 1032, "number": 1032, "text": "لِنَجْزِيَ يَا نَصٍّ سَمَا وَغِشَاوَةً ... بِهِ الْفَتْحُ وَاْلإِسْكَانُ وَالْقَصْرُ شُمِّلَا", "chapter": 1 }, { "id": 1033, "number": 1033, "text": "وَوَالسَّاعَةَ ارْفَعْ غَيْرَ حَمْزَةَ حُسْناً الْـ ... ـمُحَسِّنُ إِحْسَاناً لِكُوفٍ تَحَوَّلَا", "chapter": 1 }, { "id": 1034, "number": 1034, "text": "وَغَيْرُ صِحَابٍ أَحْسَنَ ارْفَعْ وَقَبْلَهُ ... وَبَعْدُ بِياءٍ ضُمَّ فِعْلاَنِ وُصِّلَا", "chapter": 1 }, { "id": 1035, "number": 1035, "text": "وَقَلْ عَنْ هِشاَمٍ أَدْغَمُوا تَعِدَانِنِيْ ... نُوَفيَهُمْ بِالْيَا لَهُ حَقُّ نَهْشَلَا", "chapter": 1 }, { "id": 1036, "number": 1036, "text": "وَقُلْ لاَ تَرَى بِالْغَيْبِ وَاضْمُمْ وَبَعْدَهُ ... مَسَاكِنَهُمْ بِالرَّفْعِ فَاشِيهِ نُوِّلَا", "chapter": 1 }, { "id": 1037, "number": 1037, "text": "وَيَاءُ وَلكِنِّيْ وَيَا تَعِدَانِنِيْ ... وَإِنِّيْ وَأَوْزِعْنِيْ بِهاَ خُلْفُ مَنْ بَلَا", "chapter": 1 }, { "id": 1038, "number": 1038, "text": "وَبِالضَّمِّ وَاقْصُرْ وَاكْسِرِ التَّاءَ قَاتَلُوا ... عَلَى حُجَّةٍ وَالْقَصْرُ فِي آسِنٍ دَلَا", "chapter": 1 }, { "id": 1039, "number": 1039, "text": "وَفِي آنِفاً خُلْفٌ هَدَى وَبِضَمِّهِمْ ... وَكَسْرٍ وَتَحْرِيْكٍ وَأُمْلِيَ حُصِّلَا", "chapter": 1 }, { "id": 1040, "number": 1040, "text": "وَأَسْرَارَهُمْ فَاكْسِرْ صِحَاباً وَنَبْلُوَنْـ ... ـنَكُمْ نَعْلَمَ الْيَا صِفْ وَنَبْلُوَ وَاقْبَلَا", "chapter": 1 }, { "id": 1041, "number": 1041, "text": "وَفِي يُؤْمِنُوا حَقٌّ وَبَعْدُ ثَلاثَةٌ ... وَفي ياَءِ يُؤْتيِهِ غَدِيرٌ تَسَلْسَلَا", "chapter": 1 }, { "id": 1042, "number": 1042, "text": "وَبِالضَّمِّ ضُرًّا شَاعَ وَالْكَسْرُ عَنْهُماَ ... بِلاَمِ كلاَمَ اللهِ وَالْقَصْرُ وُكِّلَا", "chapter": 1 }, { "id": 1043, "number": 1043, "text": "بِمَا يَعْمَلُونَ حَجَّ حَرَّكَ شَطْأَهُ ... دُعَا مَاجِدٍ وَاقْصُرْ فَآزَرَهُ مُلاَ", "chapter": 1 }, { "id": 1044, "number": 1044, "text": "وَفِي يَعْمَلُونَ دُمْ يَقُولُ بِياَءٍ إذْ ... صَفَا وَاكْسِرُوا أَدْبَارَ إذ فَازَ دُخْلُلَا", "chapter": 1 }, { "id": 1045, "number": 1045, "text": "وَبِالْيَا يُنَادِيْ قِفْ دَلِيلاَ بِخُلْفِهِ ... وَقُلْ مِثْلُ مَا بِالرَّفْعِ شَمَّمَ صَنْدَلَا", "chapter": 1 }, { "id": 1046, "number": 1046, "text": "وَفي الصَّعْقَةِ اقْصُرْ مُسْكِنَ الْعَيْنِ رَاوِياً ... وَقَوْمَ بِخَفْضِ الْمِيمِ شَرَّفَ حُمَّلَا", "chapter": 1 }, { "id": 1047, "number": 1047, "text": "وَبَصْرٍ وَأَتْبَعنَا بِوَاتَّبَعَتْ وَمَا ... أَلَتْنَا اكْسِرُوا دِنْياً وَإِنَّ افْتَحُوا الْجَلَا", "chapter": 1 }, { "id": 1048, "number": 1048, "text": "رِضاً يَصْعَقُونَ اضُمُمْهُ كَمْ نَصَّ وَالْمُسَيْـ ... ـطِرُونَ لِساَنٌ عَابَ بِالْخُلْفِ زُمَّلَا", "chapter": 1 }, { "id": 1049, "number": 1049, "text": "وَصَادٌ كَزَايٍ قَامَ بِالْخُلْفِ ضَبْعُهُ ... وَكَذَّبَ يَرْوِيهِ هِشَامٌ مُثَقَّلَا", "chapter": 1 }, { "id": 1050, "number": 1050, "text": "تُمَارُونَهُ تَمْرُونَهُ وَافْتَحُوا شَذاً ... مَناءَةَ لِلْمَكِّيْ زِدِ الْهَمْزَ وَأَحْفِلَا", "chapter": 1 }, { "id": 1051, "number": 1051, "text": "ويَهْمِزُ ضِيزَى خُشَّعاً خَاشِعاً شَفَا ... حَمِيداً وَخَاطِبْ يَعْلَمُونَ فطِبْ كَلَا", "chapter": 1 }, { "id": 1052, "number": 1052, "text": "وَوَالْحَبُّ ذُو الرَّيْحاَنُ رَفْعُ ثَلاَثِهَا ... بِنَصْبٍ كَفَى وَالنُّونُ بِالْخَفْضِ شُكِّلَا", "chapter": 1 }, { "id": 1053, "number": 1053, "text": "وَيَخْرُجُ فَاضْمُمْ وَافْتَحِ الضَّمَّ إِذْ حَمَى ... وَفِى الْمُنْشَآتُ الشِّينُ بِالْكَسْرِ فَاحْمِلَا", "chapter": 1 }, { "id": 1054, "number": 1054, "text": "صَحِيحاً بِخُلْفٍ نَفْرُغُ الْياءُ شَائِعٌ ... شُوَاظٌ بِكَسْرِ الضَّمِّ مَكِّيُّهُمْ جَلَا", "chapter": 1 }, { "id": 1055, "number": 1055, "text": "وَرَفْعَ نُحَاسٌ جَرَّ حَقٌّ وَكَسْرَ مِيْـ ... ـمِ يَطْمِثْ فِي الاُولَى ضُمَّ تُهْدَى وَتُقْبَلَا", "chapter": 1 }, { "id": 1056, "number": 1056, "text": "وَقَالَ بِهِ اللَّيْثِ فِي الثَّانِ وَحْدَهُ ... شُيُوخٌ وَنَصُّ اللَّيْثِ بِالضَّمِّ الاوَّلَا", "chapter": 1 }, { "id": 1057, "number": 1057, "text": "وَقَوْلُ الْكِسَائِيْ ضُمَّ أَيُّهُمَا تَشَا ... وَجِيهٌ وَبَعْضُ الْمُقْرِئِينَ بِهِ تَلا", "chapter": 1 }, { "id": 1058, "number": 1058, "text": "وَآخِرُهَا يَا ذِي الْجَلاَلِ ابْنُ عَامِرٍ ... بِوَاوٍ وَرَسْمُ الشَّامِ فِيهِ تَمَثَّلاَ", "chapter": 1 }, { "id": 1059, "number": 1059, "text": "وَحُورٌ وَعِينٌ خَفْضُ رَفْعِهِمَا شَفَا ... وَعُرْباً سُكُونُ الضَّمِّ صُحِّحَ فَاعْتَلَى", "chapter": 1 }, { "id": 1060, "number": 1060, "text": "وَخِفُّ قَدَرْناَ دَارَ وَانْضَمَّ شُرْبَ فِي ... نَدَى الصَّفْوِ وَاسْتِفْهَامُ إِنَّا صَفَا وِلَا", "chapter": 1 }, { "id": 1061, "number": 1061, "text": "بِمَوْقِعِ بِالإِسْكاَنِ وَالْقَصْرِ شَائِعٌ ... وَقَدْ أَخَذَ اضْمُمْ وَاكْسِرِ الْخَاءَ حُوَّلَا", "chapter": 1 }, { "id": 1062, "number": 1062, "text": "ومِيثَاقُكُمْ عَنْهُ وَكُلٌّ كَفَى وَأَنْـ ... ـظِرُوناَ بِقَطْعٍ وَاكْسِرِ الضَّمَّ فَيْصَلَا", "chapter": 1 }, { "id": 1063, "number": 1063, "text": "وَيؤْخَذُ غَيْرُ الشَّامِ مَا نَزَلَ الْخَفِيْـ ... ـفُ إِذْ عَزَّ وَالصَّادَانِ مِنْ بَعْدُ دُمْ صِلَا", "chapter": 1 }, { "id": 1064, "number": 1064, "text": "وَآتَاكُمُ فَاقْصُرْ حَفِيظاً وَقُلْ هُوَ الْـ ... ـغَنِيُّ هُوَ احْذِفْ عَمَّ وَصْلاً مُوَصَّلَا", "chapter": 1 }, { "id": 1065, "number": 1065, "text": "وَفي يَتَنَاجَوْنَ اقْصُرِ النُّونَ سَاكِناً ... وَقَدِّمْهُ وَاضْمُمْ جِيمَهُ فَتُكَمِّلَا", "chapter": 1 }, { "id": 1066, "number": 1066, "text": "وَكَسْرُ انْشِزُوا فَاضْمُمْ مَعاً صَفْوَ خُلْفِهِ ... عُلاً عَمَّ وَامْدُدْ فِي المَجَالِسِ نَوْفَلَا", "chapter": 1 }, { "id": 1067, "number": 1067, "text": "وَفي رُسُلِي الْيَا يُخْرِبُونَ الثَّقِيلَ حُزْ ... وَمَعْ دُوْلَةً أَنِّثْ يَكُونَ بِخُلْفِ لَاَ", "chapter": 1 }, { "id": 1068, "number": 1068, "text": "وَكَسْرَ جِدَارٍ ضُمَّ وَالْفَتْحَ وَاقْصُرُوا ... ذَوِيْ أُسْوَةٍ إِنِّيْ بَياءٍ تَوَصَّلَا", "chapter": 1 }, { "id": 1069, "number": 1069, "text": "وَيُفْصَلُ فَتْحُ الضَّمِّ نَصٌّ وَصَادُهُ ... بِكَسْرٍ ثَوَى وَالثِّقْلُ شَافِيْهِ كُمِّلَا", "chapter": 1 }, { "id": 1070, "number": 1070, "text": "وَفى تُمْسِكُوا ثِقْلٌ حَلاَ وَمُتِمُّ لاَ ... تُنَوِّنْهُ وَاخْفِضْ نُورَهُ عَنْ شَذاً دَلَا", "chapter": 1 }, { "id": 1071, "number": 1071, "text": "وَلِله زِد لاَماً وَأَنْصَارَ نَوِّناً ... سَماَ وَتُنَجِّيكُمْ عَنِ الشَّامِ ثُقِّلَا", "chapter": 1 }, { "id": 1072, "number": 1072, "text": "وَبَعْدِيْ وَأَنْصَارِيْ بِيَاءِ إِضاَفَةٍ ... وَخُشْبٌ سُكُونُ الضَّمِّ زَادَ رِضاً حَلاَ", "chapter": 1 }, { "id": 1073, "number": 1073, "text": "وَخَفَّ لَوَوْا إِلْفاً بِمَا يَعْمَلُونَ صِفْ ... أَكُونَ بِوَاوٍ وَانْصِبُوا الْجَزْمَ حُفَّلَا", "chapter": 1 }, { "id": 1074, "number": 1074, "text": "وَبَالِغُ لاَ تَنْوِينَ مَعْ خَفْضِ أَمْرِهِ ... لِحَفْصٍ وَبِالتَّخَّفِيفِ عَرَّفَ رُفِّلَا", "chapter": 1 }, { "id": 1075, "number": 1075, "text": "وَضَمَّ نَصُوحاً شُعْبَةٌ مِنْ تَفَوُّتٍ ... عَلَى الْقَصْرِ وَالتَّشْدِيدِ شَقَّ تَهَلُّلَا", "chapter": 1 }, { "id": 1076, "number": 1076, "text": "وَآمِنْتُمُو فِي الْهَمْزَتَيْنِ أُصُولُهُ ... وَفي الْوَصْلِ الاُولَى قُنْبُلٌ وَاواً اَبْدَلَا", "chapter": 1 }, { "id": 1077, "number": 1077, "text": "فَسُحْقاً سُكُوناً ضُمَّ مَعْ غَيْبِ يَعْلَمُو ... نَ مَنْ رُضْ مَعِيْ بِالْيَا وَأَهْلَكَنِيْ انْجَلَا", "chapter": 1 }, { "id": 1078, "number": 1078, "text": "وَضَمُّهُمُ فِي يَزْلِقُونَكَ خَالِدٌ ... وَمَنْ قَبْلَهُ فَاكْسِرْ وَحَرِّكْ رِوًى حَلَا", "chapter": 1 }, { "id": 1079, "number": 1079, "text": "وَيَخْفَى شِفَاءً مَالِيَهْ مَاهِيَهْ فَصِلْ ... وَسُلْطَانِيَهْ مِنْ دُونِ هَاءٍ فَتُوصلَا", "chapter": 1 }, { "id": 1080, "number": 1080, "text": "وَيَذَّكَّرُونَ يُؤْمِنُونَ مَقاَلُهُ ... بِخُلْفٍ لَهُ دَاعٍ وَيَعْرُجُ رُتِّلَا", "chapter": 1 }, { "id": 1081, "number": 1081, "text": "وَسَالَ بِهَمْزٍ غُصْنُ دَانٍ وَغَيْرُهُمْ ... مِنَ الْهَمْزِ أَوْ مِنْ وَاوٍ اَوْ يَاءٍ اَبْدَلَا", "chapter": 1 }, { "id": 1082, "number": 1082, "text": "وَنَزَّاعَةً فَارْفعْ سِوى حَفْصِهِمْ وَقُلْ ... شَهَادَاتِهِمْ بِالْجَمْعِ حَفْصٌ تَقَبَّلَا", "chapter": 1 }, { "id": 1083, "number": 1083, "text": "إِلى نُصُبٍ فَاضُمُمْ وَحَرِّكْ بِهِ عُلا ... كِرَامٍ وَقُلْ وُدًّا بِهِ الضَّمُّ أُعْمِلَا", "chapter": 1 }, { "id": 1084, "number": 1084, "text": "دُعَائِيْ وَإِنِّيْ ثُمَّ بَيْتِيْ مُضَافُها ... مَعَ الْوَاوِ فَافْتَحْ إِنَّ كَمْ شَرَفاً علَا", "chapter": 1 }, { "id": 1085, "number": 1085, "text": "وَعَنْ كُلِّهِمْ أَنَّ المَسَاجِدَ فَتْحُهُ ... وَفِي أَنّهُ لَمَّا بِكَسْرٍ صُوَى العُلَا", "chapter": 1 }, { "id": 1086, "number": 1086, "text": "وَنَسْلُكْهُ يَا كُوفٍ وَفِي قَالَ إِنَّمَا ... هُنَا قُلْ فَشاَ نَصًّا وَطَابَ تَقَبُّلَا", "chapter": 1 }, { "id": 1087, "number": 1087, "text": "وَقُلْ لِبَداً فِي كَسْرِهِ الضَّمُّ لَازِمٌ ... بِخُلْفٍ وَيَا رَبِّيْ مُضَافٌ تَجَمَّلاَ", "chapter": 1 }, { "id": 1088, "number": 1088, "text": "وَوَطْئاً وِطَاءً فَاكْسِرُوهُ كَمَا حَكَوْا ... وَرَبُّ بِخَفْضِ الرَّفْعِ صُحْبَتُهُ كَلَا", "chapter": 1 }, { "id": 1089, "number": 1089, "text": "وَثَا ثُلُثِهْ فَانْصِبْ وَفَا نِصْفِهِ ظُبىً ... وَثُلْثَيْ سُكُونُ الضَّمِّ لَاحَ وَجَمَّلَا", "chapter": 1 }, { "id": 1090, "number": 1090, "text": "وَوَالرِّجْزَ ضَمَّ الْكَسْرَ حَفْصٌ إِذَا قُلِ إذْ ... وَأَدْبَرَ فَاهْمِزْهُ وَسَكِّنْ عَنِ اجْتِلَا", "chapter": 1 }, { "id": 1091, "number": 1091, "text": "فَبَادِرْ وَفَا مُسْتَنْفِرَهْ عَمَّ فَتْحُهُ ... وَمَا يَذْكُرُونَ الْغَيْبَ خُصَّ وَخُلِّلَا", "chapter": 1 }, { "id": 1092, "number": 1092, "text": "وَرَا بَرِقَ افْتَحْ آمِناً يَذَرُونَ مَعْ ... يُحِبُّونَ حَقٌّ كَفَّ يُمْنَى عُلاً عَلَا", "chapter": 1 }, { "id": 1093, "number": 1093, "text": "سَلاَسِلَ نَوِّنْ إِذْ رَوَوَا صَرْفَهُ لََنَا ... وَبَالْقَصْرِ قِفْ مِنْ عَنْ هُدىً خُلْفُهُمْ فَلَا", "chapter": 1 }, { "id": 1094, "number": 1094, "text": "زَكاَ وَقَوَارِيراً فَنَوِّنْهُ إِذْ دَنَا ... رِضَا صَرْفِهِ وَاقْصُرْهُ فِي الْوَقْفِ فَيْصَلَا", "chapter": 1 }, { "id": 1095, "number": 1095, "text": "وَفِي الثَّانِ نَوِّنْ إِذْ رَوَوْا صَرْفَهُ وَقُلْ ... يَمُدُّ هِشَامٌ وَاقِفاً مَعْهُمُ وِلَا", "chapter": 1 }, { "id": 1096, "number": 1096, "text": "وَعَالِيهِمُ اسْكِنْ وَاكْسِرِ الضَّمَّ إِذْ فَشَا ... وَخُضْرٌ بِرَفْعِ الْخَفْضِ عَمَّ حُلاً عُلَا", "chapter": 1 }, { "id": 1097, "number": 1097, "text": "وَإِسْتَبْرَقٌ حِرْمِيُّ نَصْرٍ وَخَاطَبُوا ... تَشَاءُونَ حِصْنٌ وُقِّتَتْ وَاوُهُ حَلَا", "chapter": 1 }, { "id": 1098, "number": 1098, "text": "وَبِالْهَمْزِ بَاقِيهِمْ قَدَرْنَا ثَقِيلاً اِذْ ... رَساَ وَجِماَلاَتٌ فَوَحِّدْ شَذاً عَلَا", "chapter": 1 }, { "id": 1099, "number": 1099, "text": "وَقُلْ لاَبِثِينَ الْقَصْرُ فَاشٍ وقُلْ وَلاَ ... كِذَاباً بِتَخْفِيفِ الْكِسَائِيِّ أَقْبَلَا", "chapter": 1 }, { "id": 1100, "number": 1100, "text": "وَفي رَفْعِ بَا رَبُّ السَّموَاتِ خَفْضُهُ ... ذَلُولٌ وَفِي الرَّحْمنِ نَامِيهِ كَمَّلَا", "chapter": 1 }, { "id": 1101, "number": 1101, "text": "وَنَاخِرَةً بِالْمَدِّ صُحْبَتُهُمْ وَفي ... تَزَكَّى تَصَدَّى الثَّانِ حِرْمِيٌّ اَثْقَلاَ", "chapter": 1 }, { "id": 1102, "number": 1102, "text": "فَتَنْفَعُهُ فِي رَفْعِهِ نَصْبُ عَاصِمٍ ... وَإِنَّا صَبَبْناَ فَتْحُهُ ثَبْتُهُ تَلَا", "chapter": 1 }, { "id": 1103, "number": 1103, "text": "وَخَفَّفَ حَقٌّ سُجِّرَتْ ثِقْلُ نُشِّرْتْ ... شَرِيعَةُ حَقٍّ سُعِّرَتْ عَنْ أُولِي مَلَا", "chapter": 1 }, { "id": 1104, "number": 1104, "text": "وَظَا بِضَنِينٍ حَقُّ رَاوٍ وَخَفَّ فِي ... فَعَدَّلَكَ الْكُوفِي وَحَقُّكَ يَوْمُ لَا", "chapter": 1 }, { "id": 1105, "number": 1105, "text": "وَفِي فَاكهِينَ اقْصُرْ عُلاً وَخِتاَمُهُ ... بِفَتْحٍ وَقَدِّمْ مَدَّهُ رَاشِداً وَلَا", "chapter": 1 }, { "id": 1106, "number": 1106, "text": "يُصَلَّى ثَقِيلاً ضُمَّ عَمَّ رِضاً دَنَا ... وَبَا تَرْكَبَنَّ اضْمُمْ حَياً عَمَّ نُهَّلَا", "chapter": 1 }, { "id": 1107, "number": 1107, "text": "وَمَحْفُوظٌ اخْفِضْ رَفْعَهُ خُصَّ وَهْوَ فِي الْـ ... ـمَجِيدِ شَفَا وَالْخِفُّ قَدَّرَ رُتِّلَا", "chapter": 1 }, { "id": 1108, "number": 1108, "text": "وَبَلْ يُوْثِرُونَ حُزْ وَتَصْلَى يُضَمُّ حُزْ ... صَفَا تُسْمَعُ التَّذْكِيرُ حَقٌّ وَذُو جِلَا", "chapter": 1 }, { "id": 1109, "number": 1109, "text": "وَضَمَّ أُولُوا حَقٍّ وَلاَغِيَةٌ لَهُمْ ... مُصَيْطِرٍ اشْمِمْ ضَاعَ وَالْخُلْفُ قُلِّلَا", "chapter": 1 }, { "id": 1110, "number": 1110, "text": "وَبِالسِّينِ لُذْ وَالْوَتْرِ بِالْكَسْرِ شَائِعٌ ... فَقَدَّرَ يَرْوِي اليَحْصَبْيُّ مُثَقَّلَا", "chapter": 1 }, { "id": 1111, "number": 1111, "text": "وَأَرْبَعُ غَيْبٍ بَعْدَ بَلْ لاَ حُصُولُهَا ... يَحُضُّونَ فَتْحُ الضَّمِّ بِالْمَدِّ ثُمِّلَا", "chapter": 1 }, { "id": 1112, "number": 1112, "text": "يُعَذِّبُ فَافْتَحْهُ وَيُوثِقُ رَاوِياً ... وَيَاءَانِ فِي رَبِّيْ وَفَكَّ ارْفَعَنْ وِلَا", "chapter": 1 }, { "id": 1113, "number": 1113, "text": "وَبَعْدُ اخْفِضَنْ وَاكْسِرْ وَمُدَّ مُنَوِّناً ... مَعَ الرَّفْعِ إِطْعَامٌ نَدىً عَمَّ فَانْهَلَا", "chapter": 1 }, { "id": 1114, "number": 1114, "text": "وَمُؤْصَدَةٌ فَاهْمِزْ مَعاً عَنْ فَتىً حِمىً ... وَلاَ عَمَّ فِي وَالشَّمْسِ بِالْفاَءِ وَانْجَلَا", "chapter": 1 }, { "id": 1115, "number": 1115, "text": "وَعَنْ قُنْبُلٍ قَصْراً رَوَى ابْنُ مُجاَهِدِ ... رَآهُ وَلَمْ يَأْخُذْ بِهِ مُتَعَمِّلَا", "chapter": 1 }, { "id": 1116, "number": 1116, "text": "وَمَطلَعِ كَسْرُ اللاَّمِ رَحْبٌ وَحَرْفَي الْـ ... ـبَرِيَّةِ فَاهْمِزْ آهِلاً مُتَأَهِّلاَ", "chapter": 1 }, { "id": 1117, "number": 1117, "text": "وَتَا تَرَوُنَّ اضْمُمْ في الاُولَى كَمَا رَسَا ... وَجَمَّعَ بِالتَّشْدِيدِ شَافِيهِ كَمَّلَا", "chapter": 1 }, { "id": 1118, "number": 1118, "text": "وَصُحْبَةٌ الضَّمَّيْنِ فِي عَمَدٍ وَعَوْا ... لإِيلاَفِ بِالْيَا غَيْرُ شَامِيِّهِمْ تَلَا", "chapter": 1 }, { "id": 1119, "number": 1119, "text": "وَإِيلاَفِ كُلٌّ وَهْوَ في الْخَطِّ سَاقِطٌ ... وَلِى دِينِ قُلْ فِي الْكَافِرِينَ تَحَصَّلَا", "chapter": 1 }, { "id": 1120, "number": 1120, "text": "وَهَا أَبِي لَهْبٍ بِالاِسْكَانِ دَوَّنُوا ... وَحَمَّالَةُ المَرْفُوعُ بِالنَّصْبِ نُزِّلَا", "chapter": 1 }, { "id": 1121, "number": 1121, "text": "رِوَى الْقَلْبِ ذِكْرُ اللهِ فَاسْتَسْقِ مُقْبِلاَ ... وَلاَ تَعْدُ رَوْضَ الذَّاكِرِينَ فَتُمحِلَا", "chapter": 1 }, { "id": 1122, "number": 1122, "text": "وَآثِرْ عَنِ الآثَارِ مَثْرَاةَ عَذْبِهِ ... وَمَا مِثْلُهُ لِلْعَبدِ حِصْناً وَمَوْئِلَا", "chapter": 1 }, { "id": 1123, "number": 1123, "text": "وَلاَ عَمَلٌ أَنْجَى لَهُ مِنْ عَذَابِهِ ... غَدَاةَ الْجَزَا مِنْ ذِكْرِهِ مُتَقَبَّلَا", "chapter": 1 }, { "id": 1124, "number": 1124, "text": "ومَنْ شَغَلَ الْقُرْآنُ عَنْهُ لِسَانَهُ ... يَنَلْ خَيْرَ أَجْرِ الذَّاكِرِينَ مُكَمَّلَا", "chapter": 1 }, { "id": 1125, "number": 1125, "text": "وَمَا أَفْضَلُ الأَعْمَالِ إِلاَّ افْتِتَاحُهُ ... مَعَ الْخَتْمِ حِلاًّ وَارْتِحاَلاً مُوَصَّلَا", "chapter": 1 }, { "id": 1126, "number": 1126, "text": "وَفِيهِ عَنِ الْمَكِّيْنَ تَكْبِيرُهُمْ مَعَ الْـ ... ـخَوَاتِمِ قُرْبَ الْخَتْمِ يُرْوَى مُسَلْسَلَا", "chapter": 1 }, { "id": 1127, "number": 1127, "text": "إِذا كَبَّرُوا في آخِرِ النَّاسِ أَرْدَفُوا ... مَعَ الْحَمْدِ حَتَّى الْمُفْلِحُونَ تَوَسُّلَا", "chapter": 1 }, { "id": 1128, "number": 1128, "text": "وَقَالَ بِهِ الْبَزِّيُّ مِنْ آخِرِ الضُّحَى ... وَبَعْضٌ لَهُ مِنْ آخِرِ اللَّيْلِ وَصَّلَا", "chapter": 1 }, { "id": 1129, "number": 1129, "text": "فَإِنْ شِئْتَ فَاقْطَعْ دُونَهُ أَوْ عَلَيْهِ أَوْ ... صِلِ الْكُلَّ دُونَ الْقَطْعِ مَعْهُ مُبَسْمِلَا", "chapter": 1 }, { "id": 1130, "number": 1130, "text": "وَمَا قَبْلَهُ مِنْ سَاكِنٍ أَوْ مُنَوَّنٍ ... فَلِلسَّاكِنَيْنِ اكْسِرْهُ فِي الْوَصْلِ مُرْسَلَا", "chapter": 1 }, { "id": 1131, "number": 1131, "text": "وَأَدْرِجْ عَلَى إِعْرَابِهِ مَا سِوَاهُمَا ... وَلاَ تَصِلَنْ هَاءَ الضَّمِيرِ لِتُوصَلاَ", "chapter": 1 }, { "id": 1132, "number": 1132, "text": "وَقُلْ لَفْظُهُ اللهُ أَكْبَرْ وَقَبْلَهُ ... لَأَحْمَدَ زَادَ ابْنُ الْحُبَابِ فَهَلَّلَا", "chapter": 1 }, { "id": 1133, "number": 1133, "text": "وَقِيلَ بِهذَا عَنْ أَبِي الْفَتْحِ فَارِسٍ ... وَعَنْ قُنْبُلٍ بَعْضٌ بِتَكْبِيرِهِ تَلَا", "chapter": 1 }, { "id": 1134, "number": 1134, "text": "وَهَاكَ مَوَازِينَ الْحُرُوفِ وَمَا حَكَى ... جَهَابِذَةُ النُّقَّادِ فِيهاَ مُحَصَّلَا", "chapter": 1 }, { "id": 1135, "number": 1135, "text": "وَلاَ رِيَبةٌ فِي عَيْنِهِنَّ وَلاَ رِبَا ... وَعِنْدَ صَلِيلِ الزَّيْفِ يَصْدُقُ الاِبْتِلَا", "chapter": 1 }, { "id": 1136, "number": 1136, "text": "وَلاَ بُدَّ فِي تَعْيِينِهِنَّ مِنَ الأُولَى ... عُنُوا بِالْمَعانِيْ عَامِلِيْنَ وَقُوَّلا", "chapter": 1 }, { "id": 1137, "number": 1137, "text": "فَابْدَأْ مِنْهاَ بِالْمَخَارِجِ مُرْدِفاً ... لَهُنَّ بِمَشْهُورِ الصِّفَاتِ مُفَصِّلَا", "chapter": 1 }, { "id": 1138, "number": 1138, "text": "ثَلاَثٌ بِأَقْصَى الْحَلْقِ وَاثْنانِ وَسْطَهُ ... وَحَرْفَانِ مِنْهاَ أَوَّلَ الْحَلْقِ جُمِّلَا", "chapter": 1 }, { "id": 1139, "number": 1139, "text": "وَحَرْفٌ لَهُ أَقْصَى اللِّسَانِ وَفَوْقَهْ ... مِنَ الْحَنَكِ احْفَظْهُ وَحَرْفٌ بِأَسْفَلَا", "chapter": 1 }, { "id": 1140, "number": 1140, "text": "وَوَسْطُهُمَا مِنْهُ ثَلاَثٌ وَحَافَةُ الْـ ... ـلِسَانِ فَأَقْصَاهَا لِحَرْفٍ تَطَوَّلَا", "chapter": 1 }, { "id": 1141, "number": 1141, "text": "إِلى مَا يَلِي الأَضْرَاسَ وَهْوَ لَدَيْهِمَا ... يَعِزُّ وَبِالْيُمْنَى يَكُونُ مُقَلَّلاً", "chapter": 1 }, { "id": 1142, "number": 1142, "text": "وَحَرْفٌ بِأَدْنَاهَا إِلى مُنْتَهاَهُ قَدْ ... يَلِي الْحَنَكَ الأَعْلى وَدُونَهُ ذُو وِلَا", "chapter": 1 }, { "id": 1143, "number": 1143, "text": "وَحَرْفٌ يُدَانِيهِ إِلَى الظَّهْرِ مَدْخَلٌ ... وَكَمْ حَاذِقٍ مَعْ سِيبَويْهِ بِهِ اجْتَلَا", "chapter": 1 }, { "id": 1144, "number": 1144, "text": "وَمِنْ طَرَفٍ هُنَّ الثَّلاثُ لِقُطْرُبٍ ... وَيَحْيَى مَعَ الْجَرْمِيِّ مَعْناَهُ قُوِّلَا", "chapter": 1 }, { "id": 1145, "number": 1145, "text": "وَمِنْهُ وَمِنْ عُلْيَا الثَّنَايَا ثَلاَثَةٌ ... وَمِنْهُ وَمِنْ أَطْرَافِهاَ مِثْلُهاَ انْجَلَى", "chapter": 1 }, { "id": 1146, "number": 1146, "text": "وَمِنْهُ وَمِنْ بَيْنِ الثنَايَا ثَلاَثَةٌ ... وَحَرْفٌ مِنْ اطْرَافِ الثَّنَاياَ هِيَ الْعُلاَ", "chapter": 1 }, { "id": 1147, "number": 1147, "text": "وَمِنْ بَاطِنِ السُّفْلَى مِنَ الشَّفَتَيْنِ قُلْ ... وَلِلشَّفَتَيْنِ اجْعَلْ ثَلاَثاً لِتَعْدِلَا", "chapter": 1 }, { "id": 1148, "number": 1148, "text": "وَفِي أَوَّلٍ مِنْ كِلْمِ بَيْتَيْنِ جَمْعُهَا ... سِوَى أَرْبَعٍ فِيهِنَّ كِلْمَةٌ اَوَّلَا", "chapter": 1 }, { "id": 1149, "number": 1149, "text": "أَهَاعَ حَشَا غَاوٍ خَلاَ قَارِئٍ كَمَا ... جَرَى شَرْطُ يُسْرَى ضَارِعٍ لاحَ نَوْفَلَا", "chapter": 1 }, { "id": 1150, "number": 1150, "text": "رَعَى طُهْرَ دِينٍ تَمَّهُ ظِلُّ ذِي ثَناَ ... صَفَا سَجْلُ زُهْدٍ فِي وُجُوهِ بَنِي مَلَا", "chapter": 1 }, { "id": 1151, "number": 1151, "text": "وَغُنَّةُ تَنْوِينٍ وَنُونٍ وَمِيمٍ اِنْ ... سَكَنَّ وَلاَ إِظْهَارَ فِي الأَنْفِ يُجْتَلَى", "chapter": 1 }, { "id": 1152, "number": 1152, "text": "وَجَهْرٌ وَرَخْوٌ وَانْفِتَاحٌ صِفَاتُهَا ... وَمُسْتَفِلٌ فَاجْمَعْ بِالاَضْدَادِ أَشْمُلَا", "chapter": 1 }, { "id": 1153, "number": 1153, "text": "فَمَهْمُوسُهاَ عَشْرٌ (حَثَتْ كِسْفَ شَخْصِهِ) ... (أَجَدَّتْ كَقُطْبٍ) لِلشَّدِيدَةِ مُثِّلَا", "chapter": 1 }, { "id": 1154, "number": 1154, "text": "وَمَا بَيْنَ رَخْوٍ وَالشَّدِيدَةِ (عَمْرُنَلْ) ... وَ (وَايٌ) حُرُوفُ الْمَدِّ وَالرَّخْوِ كَمَّلَا", "chapter": 1 }, { "id": 1155, "number": 1155, "text": "وَ (قِظْ خُصَّ ضَغْطٍ) سَبْعُ عُلُوٍ وَمُطْبَقٌ ... هُوَ الضَّادُ وَالظَّا أُعْجِما وَإِنُ اهْمِلَا", "chapter": 1 }, { "id": 1156, "number": 1156, "text": "وَصَادٌ وَسِينٌ مُهْمَلانِ وَزَايُهَا ... صَفِيرٌ وَشِينٌ بِالتَّفَشِّي تَعَمَّلَا", "chapter": 1 }, { "id": 1157, "number": 1157, "text": "وَمُنْحَرِفٌ لاَمٌ وَرَاءٌ وَكُرِّرَتْ ... كَمَا الْمُسْتَطِيلُ الضَّادُ لَيْسَ بِأَغْفَلَا", "chapter": 1 }, { "id": 1158, "number": 1158, "text": "كَمَا اْلأَلِفُ الْهَاوِي وَ (آوِي) لِعِلَّةٍ ... وَفِي (قُطْبِ جَدٍّ) خَمْسُ قَلْقَلَةٍ عُلَا", "chapter": 1 }, { "id": 1159, "number": 1159, "text": "وَأَعْرَفُهُنَّ الْقَافُ كُلُّ يَعُدُّهَا ... فَهذَا مَعَ التَّوْفِيقِ كَافٍ مُحَصِّلَا", "chapter": 1 }, { "id": 1160, "number": 1160, "text": "وَقَدْ وَفقَ اللهُ الكَرِيمُ بِمَنِّهِ ... لإِكْمَالِهَا حَسْنَاءَ مَيْمُونَةَ الْجِلَا", "chapter": 1 }, { "id": 1161, "number": 1161, "text": "وَأَبْيَاتُهَا أَلْفٌ تَزِيدُ ثَلاَثَةً ... وَمَعْ مِائَةٍ سَبْعِينَ زُهْراً وَكُمَّلَا", "chapter": 1 }, { "id": 1162, "number": 1162, "text": "وَقَدْ كُسِيَتْ مِنْهَا الْمَعَانِي عِنَايَةً ... كَمَا عَرِيَتْ عَنْ كُلِّ عَوْرَاءَ مِفْصَلاَ", "chapter": 1 }, { "id": 1163, "number": 1163, "text": "وَتَمَّتْ بِحَمْدِ اللهِ فِي الْخَلْقِ سَهْلَةً ... مُنَزَّهَةً عَنْ مَنْطِقِ الْهُجْرِ مِقْوَلَا", "chapter": 1 }, { "id": 1164, "number": 1164, "text": "وَلكِنَّهَا تَبْغِيْ مِنَ النَّاسِ كُفْؤَهَا ... أَخَا ثِقَةٍ يَعْفُوْ وَيُغْضِيْ تَجَمُّلَا", "chapter": 1 }, { "id": 1165, "number": 1165, "text": "وَلَيْسَ لَهَا إِلاَّ ذُنُوبُ وَلِيِّهَا ... فَيَا طَيِّبَ الأَنْفَاسِ أَحْسِنْ تَأَوُّلَا", "chapter": 1 }, { "id": 1166, "number": 1166, "text": "وَقُلْ رَحِمَ الرَّحمنُّ حَيًّا وَمَيِّتًا ... فَتًى كَانَ لِلإِنْصَافِ وَالْحِلْمِ مَعْقِلَا", "chapter": 1 }, { "id": 1167, "number": 1167, "text": "عَسَى اللهُ يُدْنِيْ سَعْيَهُ بِجَوَازِهِ ... وَإِنْ كَانَ زَيْفاً غَيْرَ خَافٍ مُزَلَّلَا", "chapter": 1 }, { "id": 1168, "number": 1168, "text": "فَيا خَيْرَ غَفَّارٍ وَيَا خَيْرَ رَاحِمٍ ... وَياَ خَيْرَ مَأْمُولٍ جَدًا وَتَفَضُّلَا", "chapter": 1 }, { "id": 1169, "number": 1169, "text": "أَقِلْ عَثْرَتِي وَانْفَعْ بِهاَ وَبِقَصْدِهَا ... حَنَانَيْكَ يَا اللهُ يَا رَافِعَ الْعُلَا", "chapter": 1 }, { "id": 1170, "number": 1170, "text": "وَآخِرُ دَعْوَانَا بِتَوْفِيقِ رَبِّنَا ... أَنِ الْحَمْدُ لِلهِ الَّذِي وَحْدَهُ عَلَا", "chapter": 1 }, { "id": 1171, "number": 1171, "text": "وَبَعْدُ صَلاَةُ اللهِ ثُمَّ سَلاَمُهُ ... عَلَى سَيِّدِ الْخَلْقِ الرِّضَا مُتَنَخِّلَا", "chapter": 1 }, { "id": 1172, "number": 1172, "text": "مُحَمَّدٍ الْمُخْتَارِ لِلْمَجْدِ كَعْبَةً ... صَلاَةً تُبَارِيْ الرِّيحَ مِسْكاً وَمَنْدَلَا", "chapter": 1 }, { "id": 1173, "number": 1173, "text": "وَتُبْدِي عَلَى أَصْحَابِهِ نَفَحَاتِهَا ... بِغَيْرِ تَنَاهٍ زَرْنَبًا وَقَرَنْفُلَا", "chapter": 1 }];

export const CanonicalData = {
    getMeta: () => metaInfo,

    getBaytByNumber: (num: number) => {
        return baytList.find(b => b.number === num) || null;
    },

    getRange: (from: number, to: number) => {
        return baytList.filter(b => b.number >= from && b.number <= to);
    },

    search: (query: string) => {
        if (!query.trim()) return [];
        const q = query.trim();
        return baytList.filter(b => b.text.includes(q));
    },

    getAll: () => baytList
};
